   488‚Üí    <div>
   489‚Üí      {filteredProducts.map(product => (
   490‚Üí        <Product key={product.id} product={product} />
   491‚Üí      ))}
   492‚Üí    </div>
   493‚Üí  );
   494‚Üí}
   495‚Üí
   496‚Üí// Memoize callbacks passed to children
   497‚Üífunction Parent() {
   498‚Üí  const [count, setCount] = useState(0);
   499‚Üí
   500‚Üí  const handleClick = useCallback(() => {
   501‚Üí    setCount(c => c + 1);
   502‚Üí  }, []);
   503‚Üí
   504‚Üí  return <MemoizedChild onClick={handleClick} />;
