import SwiftUI

struct AboutView: View {
    @Environment(\.designTheme) private var theme

    var body: some View {
        ScrollView {
            VStack(spacing: .spacing6) {
                // About Me Card
                VStack(alignment: .leading, spacing: .spacing3) {
                    Text("About Me")
                        .font(.title3)
                        .fontWeight(.bold)
                        .foregroundStyle(theme.color(.foreground))

                    Text("Volleyball Middle Blocker at Carroll High School. Passionate about volleyball and dedicated to academic excellence. Ready to compete at the collegiate level.")
                        .font(.body)
                        .foregroundStyle(theme.color(.foreground))
                        .lineSpacing(4)
                }
                .glassCard(cornerRadius: .radiusLarge, padding: .spacing5)
                .padding(.horizontal)

                // Personal Information Card
                VStack(alignment: .leading, spacing: .spacing5) {
                    Text("Personal Information")
                        .font(.title3)
                        .fontWeight(.bold)
                        .foregroundStyle(theme.color(.foreground))

                    InfoRow(icon: "person.fill", iconColor: .blue, label: "Full Name", value: "Kinsley Young")
                    InfoRow(icon: "mappin.circle.fill", iconColor: .blue, label: "Address", value: "Southlake, TX 75001")
                    InfoRow(icon: "phone.fill", iconColor: .blue, label: "Phone", value: "(214) 555-0123")
                    InfoRow(icon: "envelope.fill", iconColor: .blue, label: "Email", value: "kinsley.young@email.com")
                    InfoRow(icon: "calendar", iconColor: .blue, label: "Birthday", value: "March 15, 2008")
                }
                .glassCard(cornerRadius: .radiusLarge, padding: .spacing5)
                .padding(.horizontal)

                // Social Media Card
                VStack(alignment: .leading, spacing: .spacing5) {
                    Text("Social Media")
                        .font(.title3)
                        .fontWeight(.bold)
                        .foregroundStyle(theme.color(.foreground))

                    SocialRow(icon: "camera.fill", iconColor: .pink, handle: "@kinsley.young", platform: "Instagram")
                    SocialRow(icon: "play.rectangle.fill", iconColor: .black, handle: "@kinsleyyoung", platform: "TikTok")
                    SocialRow(icon: "play.fill", iconColor: .red, handle: "@KinsleyYoungVB", platform: "YouTube")
                }
                .glassCard(cornerRadius: .radiusLarge, padding: .spacing5)
                .padding(.horizontal)

                // Guardian Information Card
                VStack(alignment: .leading, spacing: .spacing5) {
                    Text("Guardian Information")
                        .font(.title3)
                        .fontWeight(.bold)
                        .foregroundStyle(theme.color(.foreground))

                    GuardianCard(
                        number: 1,
                        name: "Sarah Young",
                        phone: "(214) 555-0124",
                        email: "sarah.young@email.com",
                        relation: "Mother"
                    )

                    GuardianCard(
                        number: 2,
                        name: "Michael Young",
                        phone: "(214) 555-0125",
                        email: "michael.young@email.com",
                        relation: "Father"
                    )
                }
                .glassCard(cornerRadius: .radiusLarge, padding: .spacing5)
                .padding(.horizontal)
            }
            .padding(.vertical, .spacing5)
        }
        .withDesignTheme()
        .brandBackground(.feed)
        .navigationTitle("About")
        .navigationBarTitleDisplayMode(.inline)
    }
}

// Info Row Component
struct InfoRow: View {
    @Environment(\.designTheme) private var theme
    let icon: String
    let iconColor: Color
    let label: String
    let value: String

    var body: some View {
        HStack(spacing: .spacing4) {
            Image(systemName: icon)
                .foregroundStyle(theme.color(.foreground))
                .frame(width: .spacing10, height: .spacing10)
                .background(iconColor.opacity(0.3))
                .clipShape(Circle())

            VStack(alignment: .leading, spacing: .spacing1) {
                Text(label)
                    .font(.caption)
                    .foregroundStyle(theme.color(.mutedforeground))
                Text(value)
                    .font(.body)
                    .foregroundStyle(theme.color(.foreground))
            }

            Spacer()
        }
    }
}

// Social Row Component
struct SocialRow: View {
    @Environment(\.designTheme) private var theme
    let icon: String
    let iconColor: Color
    let handle: String
    var platform: String = ""

    var body: some View {
        HStack(spacing: .spacing4) {
            Image(systemName: icon)
                .foregroundStyle(theme.color(.foreground))
                .frame(width: .spacing10, height: .spacing10)
                .background(iconColor)
                .clipShape(Circle())

            VStack(alignment: .leading, spacing: .spacing1) {
                if !platform.isEmpty {
                    Text(platform)
                        .font(.caption)
                        .foregroundStyle(theme.color(.mutedforeground))
                }
                Text(handle)
                    .font(.body)
                    .foregroundStyle(theme.color(.foreground))
            }

            Spacer()
        }
    }
}

// Guardian Card Component
struct GuardianCard: View {
    @Environment(\.designTheme) private var theme
    let number: Int
    let name: String
    let phone: String
    let email: String
    let relation: String

    var body: some View {
        VStack(alignment: .leading, spacing: .spacing3) {
            HStack {
                Image(systemName: "person.2.fill")
                    .foregroundStyle(theme.color(.primary))
                Text("Guardian \(number)")
                    .font(.headline)
                    .foregroundStyle(theme.color(.primary))
            }

            VStack(alignment: .leading, spacing: .spacing2) {
                Text(name)
                    .font(.body)
                    .fontWeight(.medium)
                    .foregroundStyle(theme.color(.foreground))
                Text(phone)
                    .font(.subheadline)
                    .foregroundStyle(theme.color(.mutedforeground))
                Text(email)
                    .font(.subheadline)
                    .foregroundStyle(theme.color(.mutedforeground))
                Text(relation)
                    .font(.subheadline)
                    .foregroundStyle(theme.color(.mutedforeground))
            }
        }
        .frame(maxWidth: .infinity, alignment: .leading)
        .glassCard(cornerRadius: .radiusMedium, padding: .spacing4)
    }
}

