import SwiftUI

struct AthleticsView: View {
    @Environment(\.designTheme) private var theme

    var body: some View {
        ScrollView {
                VStack(spacing: .spacing4) {
                    // Physical Stats Section
                    SectionCard(title: "Physical Stats", icon: "ruler", color: .blue) {
                        VStack(spacing: .spacing4) {
                            HStack(spacing: .spacing4) {
                                StatItem(label: "Height", value: "6'0\"")
                                StatItem(label: "Weight", value: "155 lbs")
                            }

                            HStack(spacing: .spacing4) {
                                StatItem(label: "Vertical Jump", value: "32\"")
                                StatItem(label: "Reach Height", value: "9'6\"")
                            }

                            StatItem(label: "Dominant Hand", value: "Right")
                        }
                    }

                    // Position & Sport Section
                    SectionCard(title: "Position & Sport", icon: "target", color: .blue) {
                        VStack(alignment: .leading, spacing: .spacing3) {
                            AthleticsInfoRow(label: "Sport", value: "Volleyball")
                            AthleticsInfoRow(label: "Primary Position", value: "Middle Blocker")
                            AthleticsInfoRow(label: "Secondary Position", value: "Outside Hitter")
                            AthleticsInfoRow(label: "Jersey Number", value: "#10")
                        }
                    }

                    // Season Stats Section
                    SectionCard(title: "Season Stats", icon: "chart.bar", color: .blue) {
                        VStack(spacing: .spacing4) {
                            StatRow(label: "Kills", value: "287")
                            StatRow(label: "Blocks", value: "142")
                            StatRow(label: "Aces", value: "36")
                            StatRow(label: "Digs", value: "198")
                            StatRow(label: "Games Played", value: "47")
                            StatRow(label: "Sets Played", value: "156")
                        }
                    }

                    // Team Information Section
                    SectionCard(title: "Team Information", icon: "person.3", color: .blue) {
                        VStack(alignment: .leading, spacing: .spacing4) {
                            Text("High School Team")
                                .font(.headline)
                                .foregroundStyle(theme.color(.foreground))

                            AthleticsInfoRow(label: "School", value: "Carroll High School")
                            AthleticsInfoRow(label: "Coach", value: "Coach Jennifer Martinez")
                            AthleticsInfoRow(label: "Team Record", value: "32-5")
                            AthleticsInfoRow(label: "Division", value: "6A")

                            Text("Club Team")
                                .font(.headline)
                                .foregroundStyle(theme.color(.foreground))
                                .padding(.top, .spacing2)

                            AthleticsInfoRow(label: "Club Name", value: "Texas Advantage Volleyball")
                            AthleticsInfoRow(label: "Club Coach", value: "Coach David Thompson")
                            AthleticsInfoRow(label: "Club Division", value: "Open Division")
                        }
                    }

                    // Athletic Honors Section
                    SectionCard(title: "Athletic Honors", icon: "trophy", color: .blue) {
                        VStack(alignment: .leading, spacing: .spacing2) {
                            HonorItem(text: "2024 First Team All-District")
                            HonorItem(text: "2024 District MVP")
                            HonorItem(text: "2023 Second Team All-State")
                            HonorItem(text: "2023 Regional Tournament MVP")
                            HonorItem(text: "2023 Team Captain")
                            HonorItem(text: "3x Academic All-District")
                            HonorItem(text: "2022 Newcomer of the Year")
                        }
                    }

                    // Press & Media Section
                    SectionCard(title: "Press & Media", icon: "link", color: .blue) {
                        VStack(spacing: .spacing3) {
                            MediaLink(
                                title: "Dallas Morning News",
                                subtitle: "Carroll's Young leads team to district championship"
                            )
                            MediaLink(
                                title: "MaxPreps Feature",
                                subtitle: "Player of the Week - Kinsley Young"
                            )
                            MediaLink(
                                title: "Southlake Times",
                                subtitle: "Local volleyball star commits to excellence"
                            )
                        }
                    }
                }
                .padding(.horizontal)
                .padding(.top, .spacing5)
                .padding(.bottom, 100)
        }
        .withDesignTheme()
        .brandBackground(.feed)
        .navigationTitle("Athletics")
        .navigationBarTitleDisplayMode(.inline)
    }
}

// MARK: - Components

struct SectionCard<Content: View>: View {
    @Environment(\.designTheme) private var theme
    let title: String
    let icon: String
    let color: Color
    let content: Content

    init(title: String, icon: String, color: Color, @ViewBuilder content: () -> Content) {
        self.title = title
        self.icon = icon
        self.color = color
        self.content = content()
    }

    var body: some View {
        VStack(alignment: .leading, spacing: .spacing4) {
            HStack(spacing: .spacing2) {
                Image(systemName: icon)
                    .foregroundStyle(theme.color(.primary))
                    .font(.title3)
                Text(title)
                    .font(.title3)
                    .fontWeight(.bold)
                    .foregroundStyle(theme.color(.foreground))
            }

            content
        }
        .frame(maxWidth: .infinity, alignment: .leading)
        .glassCard(cornerRadius: .radiusLarge, padding: .spacing4)
    }
}

struct StatItem: View {
    @Environment(\.designTheme) private var theme
    let label: String
    let value: String

    var body: some View {
        VStack(alignment: .leading, spacing: .spacing1) {
            Text(label)
                .font(.caption)
                .foregroundStyle(theme.color(.mutedforeground))
            Text(value)
                .font(.title2)
                .fontWeight(.bold)
                .foregroundStyle(theme.color(.foreground))
        }
        .frame(maxWidth: .infinity, alignment: .leading)
        .glassCard(cornerRadius: .radiusSmall, padding: .spacing3)
    }
}

struct AthleticsInfoRow: View {
    @Environment(\.designTheme) private var theme
    let label: String
    let value: String

    var body: some View {
        VStack(alignment: .leading, spacing: .spacing1) {
            Text(label)
                .font(.caption)
                .foregroundStyle(theme.color(.mutedforeground))
            Text(value)
                .font(.body)
                .foregroundStyle(theme.color(.foreground))
        }
    }
}

struct StatRow: View {
    @Environment(\.designTheme) private var theme
    let label: String
    let value: String

    var body: some View {
        VStack(alignment: .leading, spacing: .spacing1) {
            Text(label)
                .font(.caption)
                .foregroundStyle(theme.color(.mutedforeground))
            Text(value)
                .font(.title3)
                .fontWeight(.bold)
                .foregroundStyle(theme.color(.foreground))
        }
        .frame(maxWidth: .infinity, alignment: .leading)
    }
}

struct HonorItem: View {
    @Environment(\.designTheme) private var theme
    let text: String

    var body: some View {
        HStack(spacing: .spacing2) {
            Text("•")
                .foregroundStyle(theme.color(.foreground))
            Text(text)
                .font(.body)
                .foregroundStyle(theme.color(.foreground))
        }
    }
}

struct MediaLink: View {
    @Environment(\.designTheme) private var theme
    let title: String
    let subtitle: String

    var body: some View {
        VStack(alignment: .leading, spacing: .spacing1) {
            Text(title)
                .font(.headline)
                .foregroundStyle(theme.color(.primary))
            Text(subtitle)
                .font(.caption)
                .foregroundStyle(theme.color(.mutedforeground))
        }
        .frame(maxWidth: .infinity, alignment: .leading)
    }
}

