    }
}

import SwiftUI

struct FilmView: View {
    @Environment(\.dismiss) var dismiss
    @Environment(\.designTheme) private var theme
    @State private var showingUploadVideo = false
    @State private var showingStartStream = false

    var body: some View {
        ScrollView {
                VStack(spacing: .spacing6) {
                    // Highlight Reels Section
                    VStack(alignment: .leading, spacing: .spacing4) {
                        HStack {
                            Text("Highlight Reels")
                                .font(.title3)
                                .fontWeight(.bold)
                                .foregroundStyle(theme.color(.foreground))

                            Spacer()

                            Text("3 videos")
                                .font(.subheadline)
                                .foregroundStyle(theme.color(.mutedforeground))
                        }
                        .padding(.horizontal)

                        VStack(spacing: .spacing3) {
                            VideoCard(
                                title: "Season Highlights 2024",
                                duration: "3:42",
                                uploadTime: "1 week ago",
                                thumbnailIcon: "video.fill",
                                thumbnailImage: "highlight video",
                                videoURL: URL(string: "https://commondatastorage.googleapis.com/gtv-videos-bucket/sample/BigBuckBunny.mp4")
                            )

                            VideoCard(
                                title: "Championship Game",
                                duration: "5:15",
                                uploadTime: "2 weeks ago",
                                thumbnailIcon: "video.fill",
                                videoURL: URL(string: "https://commondatastorage.googleapis.com/gtv-videos-bucket/sample/ElephantsDream.mp4")
                            )

                            VideoCard(
                                title: "Skills Showcase",
                                duration: "2:28",
                                uploadTime: "3 weeks ago",
                                thumbnailIcon: "video.fill",
                                videoURL: URL(string: "https://commondatastorage.googleapis.com/gtv-videos-bucket/sample/ForBiggerBlazes.mp4")
                            )
                        }
                        .padding(.horizontal)

                        Button(action: {
                            showingUploadVideo = true
                        }) {
                            HStack {
                                Spacer()
                                Text("Upload New Video")
                                    .font(.body)
                                    .fontWeight(.medium)
                                    .foregroundStyle(theme.color(.foreground))
                                Spacer()
