// Email Row Component
struct EmailRow: View {
    let sender: String
    let subject: String
    let preview: String
    let timestamp: String
    let unread: Bool

    var body: some View {
        NavigationLink(destination: EmailDetailView(sender: sender, subject: subject)) {
            HStack(alignment: .top, spacing: 12) {
                // Icon
                Image(systemName: "envelope.fill")
                    .foregroundColor(unread ? .blue : .gray)
                    .frame(width: 50, height: 50)

                VStack(alignment: .leading, spacing: 6) {
                    HStack {
                        Text(sender)
                            .font(.headline)
                            .foregroundColor(.white)
                            .fontWeight(unread ? .bold : .semibold)

                        Spacer()

                        Text(timestamp)
                            .font(.caption)
                            .foregroundColor(.gray)
                    }

                    Text(subject)
                        .font(.subheadline)
                        .foregroundColor(unread ? .white : .gray)
                        .fontWeight(unread ? .semibold : .medium)
                        .lineLimit(1)

                    Text(preview)
                        .font(.caption)
                        .foregroundColor(.gray)
                        .lineLimit(2)
                }

                if unread {
                    Circle()
                        .fill(Color.blue)
                        .frame(width: 10, height: 10)
                }
            }
            .padding(.horizontal)
            .padding(.vertical, 12)
            .background(
                RoundedRectangle(cornerRadius: 0)
                    .fill(Color.white.opacity(0.05))
            )
        }
        .buttonStyle(PlainButtonStyle())
    }
}

