//
//  UserTypeSelectionView.swift
//  AthleadAI
//
//  User type selection screen during sign-up
//

import SwiftUI

enum UserType: String, CaseIterable {
    case athlete = "Athlete"
    case parent = "Parent/Guardian"
    case coach = "College Coach"
    case clubCoach = "Club / High School Coach"
}

struct UserTypeSelectionView: View {
    @Environment(\.dismiss) private var dismiss
    @State private var selectedUserType: UserType?
    @State private var navigateToAuth = false

    var body: some View {
        ZStack {
            // Background
            Color(.systemBackground)
                .ignoresSafeArea()

            VStack(spacing: 0) {
                // Header
                VStack(spacing: 16) {
                    Text("User Type Sign Up")
                        .font(.caption)
                        .foregroundColor(.gray)
                        .frame(maxWidth: .infinity, alignment: .leading)
                        .padding(.horizontal, 24)
                        .padding(.top, 20)

                    Image(systemName: "target")
                        .font(.system(size: 60))
                        .foregroundStyle(
                            LinearGradient(
                                colors: [.blue, .purple],
                                startPoint: .topLeading,
                                endPoint: .bottomTrailing
                            )
                        )

                    Text("ATHLEAD")
                        .font(.system(size: 32, weight: .bold))
                        .foregroundColor(.blue)
                }
                .padding(.bottom, 40)

                // User type options
                VStack(spacing: 16) {
                    ForEach(UserType.allCases, id: \.self) { userType in
                        Button {
                            selectedUserType = userType
                            navigateToAuth = true
                        } label: {
                            Text(userType.rawValue)
                                .font(.body)
                                .foregroundColor(.primary)
                                .frame(maxWidth: .infinity)
                                .padding()
                                .background(Color(.systemGray6))
                                .cornerRadius(8)
                        }
                    }
                }
                .padding(.horizontal, 24)

                Spacer()

                // Already have an account
                HStack(spacing: 4) {
                    Text("Already have an account?")
                        .font(.subheadline)
                        .foregroundColor(.secondary)

                    Button {
                        dismiss()
                    } label: {
                        Text("Sign In")
                            .font(.subheadline)
                            .fontWeight(.semibold)
                            .foregroundColor(.blue)
                    }
                }
                .padding(.bottom, 40)
            }
        }
        .navigationDestination(isPresented: $navigateToAuth) {
            if let userType = selectedUserType {
                SignUpAuthView(userType: userType)
            }
        }
    }
}

#Preview {
    NavigationStack {
        UserTypeSelectionView()
    }
}

//
//  SignUpAuthView.swift
//  AthleadAI
//
//  Sign-up view using Clerk authentication
//

import SwiftUI
import Clerk

struct SignUpAuthView: View {
    @Environment(\.clerk) private var clerk
    @Environment(\.dismiss) private var dismiss

    let userType: UserType

    @State private var isLoading = false
    @State private var errorMessage: String?
    @State private var showEmailSignUp = false
    @State private var navigateToOnboarding = false
    @State private var selectedAuthMethod: AuthMethod?

    enum AuthMethod {
        case google
        case microsoft
        case email
    }

    var body: some View {
        ZStack {
            // Background
            Color(.systemBackground)
                .ignoresSafeArea()

            VStack(spacing: 0) {
                // Header
                VStack(spacing: 16) {
                    Text("User Type Sign Up")
                        .font(.caption)
                        .foregroundColor(.gray)
                        .frame(maxWidth: .infinity, alignment: .leading)
                        .padding(.horizontal, 24)
                        .padding(.top, 20)

                    Image(systemName: "target")
                        .font(.system(size: 60))
                        .foregroundStyle(
                            LinearGradient(
                                colors: [.blue, .purple],
                                startPoint: .topLeading,
                                endPoint: .bottomTrailing
                            )
                        )

                    Text("ATHLEAD")
                        .font(.system(size: 32, weight: .bold))
                        .foregroundColor(.blue)
                }
                .padding(.bottom, 40)

                // Error message
                if let errorMessage = errorMessage {
                    HStack {
                        Image(systemName: "exclamationmark.triangle.fill")
                        Text(errorMessage)
                            .font(.footnote)
                    }
                    .foregroundColor(.red)
                    .padding()
                    .background(Color.red.opacity(0.1))
                    .cornerRadius(10)
                    .padding(.horizontal, 24)
                    .padding(.bottom, 16)
                    .transition(.scale.combined(with: .opacity))
                }

                // Sign-up options
                VStack(spacing: 16) {
                    // Google Sign-Up
                    Button {
                        selectedAuthMethod = .google
                        navigateToOnboarding = true
                    } label: {
                        HStack {
                            Image(systemName: "g.circle.fill")
                                .font(.title3)
                            Text("Sign-Up with Google")
                                .font(.body)
                                .fontWeight(.semibold)
                        }
                        .frame(maxWidth: .infinity)
                        .padding()
                        .background(Color(red: 0.87, green: 0.32, blue: 0.24))
                        .foregroundColor(.white)
                        .cornerRadius(8)
                    }
                    .disabled(isLoading)

                    // Microsoft Sign-Up
                    Button {
                        selectedAuthMethod = .microsoft
                        navigateToOnboarding = true
                    } label: {
                        HStack {
                            Image(systemName: "square.grid.2x2.fill")
                                .font(.title3)
                            Text("Sign-Up with Microsoft")
                                .font(.body)
                                .fontWeight(.semibold)
                        }
                        .frame(maxWidth: .infinity)
                        .padding()
                        .background(Color(red: 0.0, green: 0.46, blue: 0.74))
                        .foregroundColor(.white)
                        .cornerRadius(8)
                    }
                    .disabled(isLoading)

                    // Email Sign-Up
                    Button {
                        selectedAuthMethod = .email
                        navigateToOnboarding = true
                    } label: {
                        HStack {
                            Image(systemName: "envelope.fill")
                                .font(.title3)
                            Text("Sign-Up with Email")
                                .font(.body)
                                .fontWeight(.semibold)
                        }
                        .frame(maxWidth: .infinity)
                        .padding()
                        .background(Color(red: 0.18, green: 0.67, blue: 0.90))
                        .foregroundColor(.white)
                        .cornerRadius(8)
                    }
                    .disabled(isLoading)
                }
                .padding(.horizontal, 24)

                Spacer()

                // Already have an account
                HStack(spacing: 4) {
                    Text("Already have an account?")
                        .font(.subheadline)
                        .foregroundColor(.secondary)

                    Button {
                        dismiss()
                    } label: {
                        Text("Sign-In")
                            .font(.subheadline)
                            .fontWeight(.semibold)
                            .foregroundColor(.blue)
                    }
                }
                .padding(.bottom, 40)
            }

            // Loading overlay
            if isLoading {
                Color.black.opacity(0.3)
                    .ignoresSafeArea()

                ProgressView()
                    .progressViewStyle(CircularProgressViewStyle(tint: .white))
                    .scaleEffect(1.5)
            }
        }
        .navigationDestination(isPresented: $navigateToOnboarding) {
            // Route to appropriate onboarding based on user type
            if userType == .athlete {
                AthleteProfileSetupView(authMethod: selectedAuthMethod ?? .email)
            } else if userType == .parent {
                ParentProfileSetupView(authMethod: selectedAuthMethod ?? .email)
            } else if userType == .clubCoach {
                CoachProfileSetupView(authMethod: selectedAuthMethod ?? .email)
            } else {
                // TODO: Create onboarding flows for other user types
                Text("Onboarding for \(userType.rawValue) coming soon!")
                    .font(.title2)
                    .padding()
            }
        }
    }
}

// MARK: - Email Sign-Up View
struct EmailSignUpView: View {
    @Environment(\.clerk) private var clerk
    @Environment(\.dismiss) private var dismiss

    var body: some View {
        NavigationView {
            AuthView()
                .navigationTitle("Sign Up with Email")
                .navigationBarTitleDisplayMode(.inline)
                .toolbar {
                    ToolbarItem(placement: .cancellationAction) {
                        Button("Cancel") {
                            dismiss()
                        }
                    }
                }
        }
    }
}

