                            )

                            // Last Name
                            FormField(
                                label: "Last Name",
                                icon: "person",
                                placeholder: "Last Name",
                                text: $lastName
                            )
                        }

                        // Phone Number (optional)
                        FormField(
                            label: "Phone Number (Optional)",
                            icon: "phone",
                            placeholder: "(555) 123-4567",
                            text: $phoneNumber,
                            keyboardType: .phonePad,
