        return sports.filter { $0.localizedCaseInsensitiveContains(searchText) }
    }

    var body: some View {
        NavigationStack {
            List {
                ForEach(filteredSports, id: \.self) { sport in
                    Button {
                        selectedSport = sport
                        dismiss()
                    } label: {
                        HStack {
                            Text(sport)
