import SwiftUI

struct ProfileView: View {
    @State private var currentUser = User.currentUser
    @State private var showingGoLive = false
    @State private var showingPrivacySettings = false

    var body: some View {
        NavigationView {
            ZStack {
                // Background - Athlete photo
                GeometryReader { geometry in
                    Image(currentUser.profileImageName)
                        .resizable()
                        .aspectRatio(contentMode: .fill)
                        .frame(width: geometry.size.width, height: geometry.size.height)
                        .clipped()
                        .ignoresSafeArea()
                        .overlay(
                            LinearGradient(
                                gradient: Gradient(colors: [
                                    Color.black.opacity(0.3),
                                    Color.black.opacity(0.7)
                                ]),
                                startPoint: .top,
                                endPoint: .bottom
                            )
                            .ignoresSafeArea()
                        )
                }

                ScrollView {
                    VStack(spacing: 0) {
                        Spacer()
                            .frame(height: 500)

                        // Slim Profile Info Card
                        VStack(spacing: 4) {
                            Text(currentUser.fullName)
                                .font(.title2)
                                .fontWeight(.bold)
                                .foregroundColor(.white)

                            Text("\(currentUser.sport)  \(currentUser.position)  \(currentUser.classYear)")
                                .font(.subheadline)
                                .foregroundColor(.white.opacity(0.8))
                        }
                        .frame(maxWidth: .infinity)
                        .padding(.vertical, 16)
                        .background(
                            RoundedRectangle(cornerRadius: 16)
                                .fill(Color.black.opacity(0.4))
                                .overlay(
                                    RoundedRectangle(cornerRadius: 16)
                                        .stroke(Color.white.opacity(0.3), lineWidth: 1)
                                )
                        )
                        .padding(.horizontal)
                        .padding(.top, 16)

                        // Menu Items
                        VStack(spacing: 12) {
                            ProfileMenuItem(icon: "person.fill", title: "About", color: .blue)
                            ProfileMenuItem(icon: "video.fill", title: "Film", color: .red)
                            ProfileMenuItem(icon: "pencil", title: "Posts", color: .green)
                            ProfileMenuItem(icon: "figure.run", title: "Athletics", color: .orange)
                            ProfileMenuItem(icon: "book.fill", title: "Academics", color: .purple)
                        }
                        .padding(.horizontal)
                        .padding(.top, 16)
                        .padding(.bottom, 100)
                    }
                }
            }
            .navigationBarHidden(true)
            .sheet(isPresented: $showingGoLive) {
                GoLiveView()
            }
            .overlay(
                HStack(spacing: 16) {
                    Spacer()
                    Button(action: {
                        showingGoLive = true
                    }) {
                        Image(systemName: "video.fill")
                            .foregroundColor(.white)
                            .font(.title3)
                            .padding(10)
                            .background(Color.red)
                            .clipShape(Circle())
                    }
                    Button(action: {
                        showingPrivacySettings = true
                    }) {
                        Image(systemName: "gearshape.fill")
                            .foregroundColor(.white)
                            .font(.title3)
                    }
                }
                .padding(.horizontal, 20)
                .padding(.top, 50)
                , alignment: .top
            )
            .sheet(isPresented: $showingPrivacySettings) {
                PrivacySettingsView(privacySettings: $currentUser.privacySettings)
            }
        }
    }
}

// Social Button Component
struct SocialButton: View {
    let icon: String
    let color: Color

    var body: some View {
        Button(action: {}) {
            Image(systemName: icon)
                .foregroundColor(.white)
                .frame(width: 44, height: 44)
                .background(color)
                .clipShape(Circle())
        }
    }
}

// Profile Menu Item Component
struct ProfileMenuItem: View {
    let icon: String
    let title: String
    let color: Color

    var body: some View {
        NavigationLink(destination: destinationView()) {
            HStack(spacing: 16) {
                Image(systemName: icon)
                    .foregroundColor(.white)
                    .frame(width: 36, height: 36)
                    .background(color)
                    .clipShape(Circle())

                Text(title)
                    .font(.body)
                    .fontWeight(.medium)
                    .foregroundColor(.white)

                Spacer()

                Image(systemName: "chevron.right")
                    .foregroundColor(.white.opacity(0.5))
            }
            .padding(.horizontal, 20)
            .padding(.vertical, 16)
            .background(
                RoundedRectangle(cornerRadius: 16)
                    .fill(Color.black.opacity(0.4))
                    .overlay(
                        RoundedRectangle(cornerRadius: 16)
                            .stroke(Color.white.opacity(0.3), lineWidth: 1)
                    )
            )
        }
    }

    @ViewBuilder
    private func destinationView() -> some View {
        switch title {
        case "About":
            AboutView()
        case "Film":
            FilmView()
        case "Posts":
            PostsView()
        case "Athletics":
            AthleticsView()
        case "Academics":
            AcademicsView()
        default:
            Text("View Not Found")
        }
    }
}

