// MARK: - Sport Picker Sheet
struct SportPickerSheet: View {
    @Environment(\.dismiss) private var dismiss
    @Binding var selectedSport: String
    let sports: [String]

    @State private var searchText = ""

    var filteredSports: [String] {
        if searchText.isEmpty {
            return sports
        }
        return sports.filter { $0.localizedCaseInsensitiveContains(searchText) }
    }

    var body: some View {
        NavigationStack {
            List {
                ForEach(filteredSports, id: \.self) { sport in
                    Button {
                        selectedSport = sport
                        dismiss()
                    } label: {
                        HStack {
                            Text(sport)
                                .foregroundColor(.primary)

                            Spacer()

                            if selectedSport == sport {
                                Image(systemName: "checkmark")
                                    .foregroundColor(.blue)
                            }
                        }
                    }
                }
            }
            .searchable(text: $searchText, prompt: "Search sports")
            .navigationTitle("Select Sport")
            .navigationBarTitleDisplayMode(.inline)
            .toolbar {
                ToolbarItem(placement: .cancellationAction) {
                    Button("Cancel") {
                        dismiss()
                    }
                }
            }
        }
    }
}

