                        }
                        .font(.subheadline)
                        .fontWeight(.semibold)
                        .foregroundColor(.white)
                        .padding(.horizontal, 20)
                        .padding(.vertical, 10)
                        .background(Color.white.opacity(0.2))
                        .background(.ultraThinMaterial)
                        .cornerRadius(20)
                    }

                    Button(action: onConnect) {
                        HStack(spacing: 6) {
                            Image(systemName: "link")
                            Text("Connect")
                        }
                        .font(.subheadline)
