    let icon: String
    let title: String
    let year: Int
}

struct RecruitingPriorityItem: Identifiable {
    let id = UUID()
    let graduatingClass: String
    let positions: [PositionNeedItem]
}

struct PositionNeedItem: Identifiable {
    let id = UUID()
    let position: String
    let isPriority: Bool
}

struct PlayerItem: Identifiable {
    let id = UUID()
    let jerseyNumber: Int
    let name: String
    let position: String
    let height: String
    let hometown: String
    let photoURL: String
    let starRating: Int
    let classYear: String
}

struct CoachStaffItem: Identifiable {
    let id = UUID()
    let name: String
    let title: String
    let photoURL: String
    let yearsExperience: Int
}

struct FacilityItem: Identifiable {
    let id = UUID()
    let name: String
    let icon: String
    let description: String
    let imageURL: String
    let features: [String]
}

struct RosterSection {
    let classYear: String
    let players: [PlayerItem]
}

