//
//  APIClient.swift
//  AthleadAI
//
//  API client with Clerk authentication
//

import Foundation
import Clerk

class APIClient {
    static let shared = APIClient()

    private let livestreamBaseURL: URL
    private let usersBaseURL: URL

    private init() {
        guard let livestreamURL = URL(string: Config.livestreamAPIURL),
              let usersURL = URL(string: Config.usersAPIURL) else {
            fatalError("Invalid API URLs in configuration")
        }
        self.livestreamBaseURL = livestreamURL
        self.usersBaseURL = usersURL
    }

    enum APIEndpoint {
        case livestream(String)
        case users(String)

        var baseURL: URL {
            switch self {
            case .livestream:
                return APIClient.shared.livestreamBaseURL
            case .users:
                return APIClient.shared.usersBaseURL
            }
        }

        var path: String {
            switch self {
            case .livestream(let path), .users(let path):
                return path
            }
        }
    }

    /// Make an authenticated API request
    func request<T: Decodable>(
        endpoint: APIEndpoint,
        method: String = "GET",
        body: Encodable? = nil
    ) async throws -> T {
        // Get authentication token from Clerk
        let token = try await Clerk.shared.getSessionToken()

        // Build URL
        let url = endpoint.baseURL.appendingPathComponent(endpoint.path)

        // Create request
        var request = URLRequest(url: url)
        request.httpMethod = method
        request.setValue("Bearer \(token)", forHTTPHeaderField: "Authorization")
        request.setValue("application/json", forHTTPHeaderField: "Content-Type")

        // Add body if present
        if let body = body {
            request.httpBody = try JSONEncoder().encode(body)
        }

        // Make request
        let (data, response) = try await URLSession.shared.data(for: request)

        // Check response
        guard let httpResponse = response as? HTTPURLResponse else {
            throw APIError.invalidResponse
        }

        guard (200...299).contains(httpResponse.statusCode) else {
            throw APIError.httpError(statusCode: httpResponse.statusCode)
        }

        // Decode response
        do {
            let decoder = JSONDecoder()
            decoder.keyDecodingStrategy = .convertFromSnakeCase
            return try decoder.decode(T.self, from: data)
        } catch {
            throw APIError.decodingError(error)
        }
    }
}

enum APIError: LocalizedError {
    case authenticationRequired
    case invalidResponse
    case httpError(statusCode: Int)
    case decodingError(Error)

    var errorDescription: String? {
        switch self {
        case .authenticationRequired:
            return "Authentication is required for this request"
        case .invalidResponse:
            return "Invalid response from server"
        case .httpError(let statusCode):
            return "HTTP error: \(statusCode)"
        case .decodingError(let error):
            return "Failed to decode response: \(error.localizedDescription)"
        }
    }
}

# Testing Livestream with Clerk Authentication

## Prerequisites

1. âœ… Clerk iOS SDK installed in Xcode
2. âœ… Environment variables set in Xcode scheme
3. âœ… Backend `.env` file configured

## Setup

### 1. Backend Environment Variables

Create `.env` in project root:

```bash
# Clerk
CLERK_PUBLISHABLE_KEY=pk_test_...
CLERK_SECRET_KEY=sk_test_...

# LiveKit
LIVEKIT_API_KEY=your_livekit_key
LIVEKIT_API_SECRET=your_livekit_secret
LIVEKIT_URL=wss://your-livekit-server.livekit.cloud

# Database
DATABASE_OWNER_URL=your_database_url
DATABASE_USER_URL=your_database_url

# PostHog (optional for testing)
POSTHOG_API_KEY=
POSTHOG_HOST=https://us.i.posthog.com
POSTHOG_ENABLED=false

# Security
ALLOWED_ORIGINS=http://localhost:3000,capacitor://localhost
```

### 2. Start Backend

```bash
cd apps/livestream-api
bun install  # if not already done
bun run dev
```

You should see:
```
Server listening at http://localhost:3001
```

### 3. iOS Environment Variables

In Xcode: **Product â†’ Scheme â†’ Edit Scheme â†’ Run â†’ Arguments â†’ Environment Variables**

```
CLERK_PUBLISHABLE_KEY = pk_test_...
LIVESTREAM_API_URL = http://localhost:3001
USERS_API_URL = http://localhost:3002
LIVEKIT_URL = wss://your-livekit-server.livekit.cloud
```

## Testing Flow

### Test 1: Authentication

```swift
// In any view
@EnvironmentObject var clerkService: ClerkService

var body: some View {
    if clerkService.isSignedIn {
        Text("âœ… Authenticated as \(clerkService.user?.firstName ?? "User")")
    } else {
        Text("âŒ Not signed in")
        // Show sign-in UI
    }
}
```

**Expected Result:**
- User can sign in via Clerk
- `clerkService.isSignedIn` becomes `true`
- User data is available

### Test 2: Create Session

```swift
// Example in a view model or view
@MainActor
func testCreateSession() async {
    do {
        let session = try await LivestreamService.shared.createSession(
            title: "Test Stream",
            description: "Testing authentication"
        )

        print("âœ… Session created:")
        print("  - ID: \(session.id)")
        print("  - Room: \(session.roomName)")
        print("  - Slug: \(session.slug)")
        print("  - Status: \(session.status)")
    } catch {
        print("âŒ Error: \(error.localizedDescription)")
    }
}
```

**Expected Result:**
- API call succeeds with 201 status
- Session object returned with ID and room name
- Backend creates session in database

**What This Tests:**
- âœ… Clerk authentication token is sent
- âœ… Backend validates the token
- âœ… Backend creates session for authenticated user
- âœ… Response is properly decoded

### Test 3: Join Session

```swift
@MainActor
func testJoinSession(sessionId: String) async {
    do {
        let joinResponse = try await LivestreamService.shared.joinSession(
            sessionId: sessionId,
            participantName: clerkService.user?.firstName ?? "User"
        )

        print("âœ… Joined session:")
        print("  - Token: \(joinResponse.token)")
        print("  - Room: \(joinResponse.roomName)")
        print("  - Server: \(joinResponse.serverUrl)")

        // Use this token with LiveKit
        // connectToLiveKit(token: joinResponse.token, url: joinResponse.serverUrl)
    } catch {
        print("âŒ Error: \(error.localizedDescription)")
    }
}
```

**Expected Result:**
- Returns LiveKit token for joining the room
- Token includes participant identity
- Server URL is the LiveKit WebSocket URL

**What This Tests:**
- âœ… Backend generates LiveKit token
- âœ… Token includes proper permissions
- âœ… Returns connection details for LiveKit

### Test 4: Full Livestream Flow

```swift
@MainActor
class LiveStreamViewModel: ObservableObject {
    @Published var sessionId: String?
    @Published var livekitToken: String?
    @Published var isLive = false
    @Published var error: String?

    func startBroadcast(title: String) async {
        do {
            // 1. Create session
            let session = try await LivestreamService.shared.createSession(
                title: title,
                description: "Live from iOS"
            )
            self.sessionId = session.id
            print("âœ… Created session: \(session.slug)")

            // 2. Join session (get LiveKit token)
            let joinResponse = try await LivestreamService.shared.joinSession(
                sessionId: session.id,
                participantName: "Broadcaster"
            )
            self.livekitToken = joinResponse.token
            print("âœ… Got LiveKit token")

            // 3. Start session (mark as live)
            let liveSession = try await LivestreamService.shared.startSession(
                sessionId: session.id
            )
            self.isLive = true
            print("âœ… Session is now LIVE: \(liveSession.status)")

            // 4. Connect to LiveKit with token
            // await connectToLiveKit(token: joinResponse.token, url: joinResponse.serverUrl)

        } catch {
            self.error = error.localizedDescription
            print("âŒ Error: \(error)")
        }
    }

    func endBroadcast() async {
        guard let sessionId = sessionId else { return }

        do {
            let session = try await LivestreamService.shared.endSession(
                sessionId: sessionId
            )
            self.isLive = false
            print("âœ… Session ended: \(session.status)")
        } catch {
            self.error = error.localizedDescription
        }
    }
}
```

## Debugging

### Check Authentication Token

Add to `APIClient.swift`:

```swift
// In request() method, after getting token
print("ğŸ”‘ Auth Token: \(token.prefix(20))...")
```

### Check Backend Logs

The livestream-api will log all requests:

```
POST /api/livestream/sessions 201 - 45ms
```

### Common Errors

**401 Unauthorized**
- Check `CLERK_SECRET_KEY` in backend `.env`
- Ensure Clerk keys match (same app in Clerk dashboard)
- Verify user is signed in on iOS

**404 Not Found**
- Check API endpoint URL matches backend routes
- Verify `LIVESTREAM_API_URL` is correct

**Network Error / Connection Refused**
- Ensure backend is running (`bun run dev`)
- Check firewall isn't blocking localhost:3001

## Success Criteria

âœ… User can sign in via Clerk
âœ… Can create a session (POST /sessions)
âœ… Can join a session (POST /sessions/:id/join)
âœ… Receives valid LiveKit token
âœ… Can start session (PUT /sessions/:id/start)
âœ… Can end session (PUT /sessions/:id/end)
âœ… All requests include valid Clerk JWT
âœ… Backend validates tokens and associates sessions with user

## Next Steps

Once testing passes:

1. **Integrate LiveKit**: Use the token from `joinSession()` to connect to LiveKit
2. **Add UI**: Create views for session list, go live, viewer experience
3. **Error Handling**: Add user-friendly error messages
4. **Production Keys**: Switch to production Clerk/LiveKit keys for deployment

## Example Complete Test View

```swift
struct LivestreamTestView: View {
    @EnvironmentObject var clerkService: ClerkService
    @State private var testResults: [String] = []
    @State private var sessionId: String?

    var body: some View {
        VStack(spacing: 20) {
            Text("Livestream API Test")
                .font(.title)

            if clerkService.isSignedIn {
                Button("Test Create Session") {
                    Task { await testCreate() }
                }

                if let sessionId = sessionId {
                    Button("Test Join Session") {
                        Task { await testJoin(sessionId: sessionId) }
                    }

                    Button("Test Start Session") {
                        Task { await testStart(sessionId: sessionId) }
                    }
                }

                List(testResults, id: \.self) { result in
                    Text(result)
                        .font(.caption)
                }
            } else {
                Text("Please sign in first")
                SignInView()
            }
        }
        .padding()
    }

    func testCreate() async {
        do {
            let session = try await LivestreamService.shared.createSession(
                title: "Test Stream \(Date())"
            )
            sessionId = session.id
            testResults.append("âœ… Created: \(session.roomName)")
        } catch {
            testResults.append("âŒ Create failed: \(error)")
        }
    }

    func testJoin(sessionId: String) async {
        do {
            let response = try await LivestreamService.shared.joinSession(
                sessionId: sessionId,
                participantName: "Test User"
            )
            testResults.append("âœ… Joined: Got token")
        } catch {
            testResults.append("âŒ Join failed: \(error)")
        }
    }

    func testStart(sessionId: String) async {
        do {
            let session = try await LivestreamService.shared.startSession(
                sessionId: sessionId
            )
            testResults.append("âœ… Started: Status = \(session.status)")
        } catch {
            testResults.append("âŒ Start failed: \(error)")
        }
    }
}
