//
//  APIClient.swift
//  AthleadAI
//
//  API client with Clerk authentication
//

import Foundation
import Clerk

class APIClient {
    static let shared = APIClient()

    private let livestreamBaseURL: URL
    private let usersBaseURL: URL

    private init() {
        guard let livestreamURL = URL(string: Config.livestreamAPIURL),
              let usersURL = URL(string: Config.usersAPIURL) else {
            fatalError("Invalid API URLs in configuration")
        }
        self.livestreamBaseURL = livestreamURL
        self.usersBaseURL = usersURL
    }

    enum APIEndpoint {
        case livestream(String)
        case users(String)

        var baseURL: URL {
            switch self {
            case .livestream:
                return APIClient.shared.livestreamBaseURL
            case .users:
                return APIClient.shared.usersBaseURL
            }
        }

        var path: String {
            switch self {
            case .livestream(let path), .users(let path):
                return path
            }
        }
    }

    /// Make an authenticated API request
    func request<T: Decodable>(
        endpoint: APIEndpoint,
        method: String = "GET",
        body: Encodable? = nil
    ) async throws -> T {
        // Get authentication token from Clerk
        let token = try await Clerk.shared.getSessionToken()

        // Build URL
        let url = endpoint.baseURL.appendingPathComponent(endpoint.path)

        // Create request
        var request = URLRequest(url: url)
        request.httpMethod = method
        request.setValue("Bearer \(token)", forHTTPHeaderField: "Authorization")
        request.setValue("application/json", forHTTPHeaderField: "Content-Type")

        // Add body if present
        var bodyString = "None"
        if let body = body {
            let encoder = JSONEncoder()
            request.httpBody = try encoder.encode(body)
            if let jsonData = request.httpBody,
               let jsonString = String(data: jsonData, encoding: .utf8) {
                bodyString = jsonString
            }
        }

        // Log request details
        print("ğŸ“¡ [API] Making request")
        print("   Method: \(method)")
        print("   URL: \(url.absoluteString)")
        print("   Auth: Bearer \(String(token.prefix(20)))...")
        print("   Body: \(bodyString)")

        // Make request
        print("ğŸ“¡ [API] Sending request...")
        let (data, response) = try await URLSession.shared.data(for: request)

        // Check response
        guard let httpResponse = response as? HTTPURLResponse else {
            print("âŒ [API] Invalid response type")
            throw APIError.invalidResponse
        }

        print("ğŸ“¡ [API] Response received")
        print("   Status Code: \(httpResponse.statusCode)")

        guard (200...299).contains(httpResponse.statusCode) else {
            // Log error response body
            if let responseString = String(data: data, encoding: .utf8) {
                print("âŒ [API] HTTP error: \(httpResponse.statusCode)")
                print("   Response body: \(responseString)")
            }
            throw APIError.httpError(statusCode: httpResponse.statusCode)
        }

        // Decode response
        do {
            let decoder = JSONDecoder()
            let result = try decoder.decode(T.self, from: data)
            print("âœ… [API] Request successful")
            return result
        } catch {
            print("âŒ [API] Decoding error: \(error.localizedDescription)")
            if let responseString = String(data: data, encoding: .utf8) {
                print("   Response data: \(responseString)")
            }
            throw APIError.decodingError(error)
        }
    }
}

enum APIError: LocalizedError {
    case authenticationRequired
    case invalidResponse
    case httpError(statusCode: Int)
    case decodingError(Error)

    var errorDescription: String? {
        switch self {
        case .authenticationRequired:
            return "Authentication is required for this request"
        case .invalidResponse:
            return "Invalid response from server"
        case .httpError(let statusCode):
            return "HTTP error: \(statusCode)"
        case .decodingError(let error):
            return "Failed to decode response: \(error.localizedDescription)"
        }
    }
}

