   431â†’/**
   432â†’ * Allows a user to join an existing streaming session as a viewer.
   433â†’ * Validates session existence and generates appropriate access token.
   434â†’ *
   435â†’ * @param args - Join session configuration
   436â†’ * @returns Promise resolving to join response with token and session details
   437â†’ * @throws Error if session not found, validation fails, or token generation fails
   438â†’ */
   439â†’export async function joinSession(args: JoinSessionArgs): Promise<JoinSessionData> {
   440â†’  try {
   441â†’    // Validate arguments using Zod schema
   442â†’    const validatedArgs = validateServiceArgs(JoinSessionArgsSchema, args, {
   443â†’      service: "livestream",
   444â†’      operation: "joinSession",
   445â†’      errorMessage: LIVESTREAM_ERRORS.CONTRACT_VIOLATION,
   446â†’      errorCode: LIVESTREAM_ERROR_CODES.CONTRACT_VIOLATION,
   447â†’    });
   448â†’    const { sessionId, authId, participantName, participantId } = validatedArgs;
   449â†’
   450â†’    // Look up the user by authId to get the internal userId for foreign key
   451â†’    const user = await dbOwner.query.users.findFirst({
   452â†’      where: eq(users.authId, authId),
   453â†’    });
   454â†’
   455â†’    if (!user) {
   456â†’      throw ServiceError.notFound({
   457â†’        code: LIVESTREAM_ERROR_CODES.USER_NOT_FOUND,
   458â†’        clientMessage: "User not found",
   459â†’        logMessage: "User not found for userId",
   460â†’        logDetails: { authId },
   461â†’      });
   462â†’    }
   463â†’
   464â†’    // Get session from database to validate it exists and get roomName
   465â†’    const session = await dbOwner.query.streamingSessions.findFirst({
   466â†’      where: eq(streamingSessions.id, sessionId),
   467â†’    });
   468â†’
   469â†’    if (!session) {
   470â†’      throw ServiceError.notFound({
   471â†’        code: LIVESTREAM_ERROR_CODES.SESSION_NOT_FOUND,
   472â†’        clientMessage: LIVESTREAM_ERRORS.SESSION_NOT_FOUND,
   473â†’        logMessage: "Session not found for joinSession",
   474â†’        logDetails: {
   475â†’          sessionId,
   476â†’        },
   477â†’      });
   478â†’    }
   479â†’
   480â†’    if (session.status === "ended" || session.status === "cancelled") {
   481â†’      throw ServiceError.conflict({
   482â†’        code: LIVESTREAM_ERROR_CODES.SESSION_NOT_ACTIVE,
   483â†’        clientMessage: LIVESTREAM_ERRORS.SESSION_NOT_ACTIVE,
   484â†’        logMessage: "Session is not active for joining",
   485â†’        logDetails: {
   486â†’          sessionId,
   487â†’          status: session.status,
   488â†’        },
   489â†’      });
   490â†’    }
   491â†’
   492â†’    // Check if room has reached max participants
   493â†’    if (session.maxParticipants) {
   494â†’      try {
   495â†’        const roomClient = createRoomServiceClient();
   496â†’        const participants = await roomClient.listParticipants(session.roomName);
   497â†’
   498â†’        if (participants.length >= session.maxParticipants) {
   499â†’          throw ServiceError.conflict({
   500â†’            code: LIVESTREAM_ERROR_CODES.ROOM_AT_CAPACITY,
   501â†’            clientMessage: LIVESTREAM_ERRORS.ROOM_AT_CAPACITY,
   502â†’            logMessage: "Room has reached maximum participant capacity",
   503â†’            logDetails: {
   504â†’              sessionId,
   505â†’              currentParticipants: participants.length,
   506â†’              maxParticipants: session.maxParticipants,
   507â†’            },
   508â†’          });
   509â†’        }
   510â†’      } catch (liveKitError) {
   511â†’        // Failed to check participant count - error logged at API layer
   512â†’        // Continue anyway - let LiveKit handle the limit
   513â†’      }
   514â†’    }
   515â†’
   516â†’    const roomName = session.roomName;
   517â†’
   518â†’    // Generate access token first
   519â†’    const tokenRequest: TokenGrantRequest = {
   520â†’      requesterRole: "viewer",
   521â†’      requesterName: participantName,
   522â†’      requesterIdentity: authId, // Use authId for LiveKit identity
   523â†’      roomName,
   524â†’    };
   525â†’
   526â†’    const token = await generateAccessToken(tokenRequest);
   527â†’
   528â†’    // Track user joining session in database
   529â†’    await dbOwner.insert(sessionParticipants).values({
   530â†’      sessionId,
   531â†’      userId: user.id, // Use internal userId for foreign key
   532â†’      participantIdentity: participantId,
   533â†’      participantName,
   534â†’      role: "viewer",
   535â†’      tokenIssuedAt: new Date(),
   536â†’      tokenExpiresAt: new Date(Date.now() + 2 * 60 * 60 * 1000), // 2 hours from now
   537â†’    });
   538â†’
   539â†’    const result = {
   540â†’      token,
   541â†’      participantId,
   542â†’      roomName,
   543â†’      sessionId,
   544â†’    };
   545â†’
   546â†’    return validateServiceData(JoinSessionDataSchema, result, {
   547â†’      service: "livestream",
   548â†’      operation: "joinSession",
   549â†’      errorMessage: LIVESTREAM_ERRORS.CONTRACT_VIOLATION,
   550â†’      errorCode: LIVESTREAM_ERROR_CODES.CONTRACT_VIOLATION,
   551â†’    });
   552â†’  } catch (error) {
   553â†’    // Error logged at API layer through error handling
   554â†’    throw ServiceError.system({
   555â†’      code: LIVESTREAM_ERROR_CODES.TOKEN_GENERATION_FAILED,
   556â†’      clientMessage: LIVESTREAM_ERRORS.TOKEN_GENERATION_FAILED,
   557â†’      logMessage: "Token generation failed in joinSession",
   558â†’      logDetails: {
   559â†’        originalError: error instanceof Error ? error.message : "Unknown error",
   560â†’        sessionId: args.sessionId,
   561â†’      },
   562â†’    });
   563â†’  }
   564â†’}
