   566â†’/**
   567â†’ * Marks a participant as having left the session and removes them from the LiveKit room.
   568â†’ *
   569â†’ * @param args - Arguments containing participantId
   570â†’ * @returns Promise resolving when the participant is marked as left
   571â†’ * @throws Error if participant not found, validation fails, or removal fails
   572â†’ */
   573â†’export async function leaveSession(args: LeaveSessionArgs): Promise<void> {
   574â†’  try {
   575â†’    // Validate arguments using Zod schema
   576â†’    const validatedArgs = validateServiceArgs(LeaveSessionArgsSchema, args, {
   577â†’      service: "livestream",
   578â†’      operation: "leaveSession",
   579â†’      errorMessage: LIVESTREAM_ERRORS.CONTRACT_VIOLATION,
   580â†’      errorCode: LIVESTREAM_ERROR_CODES.CONTRACT_VIOLATION,
   581â†’    });
   582â†’
   583â†’    // First get the participant and session info
   584â†’    const participant = await dbOwner.query.sessionParticipants.findFirst({
