   606â†’      .where(eq(sessionParticipants.participantIdentity, validatedArgs.participantId));
   607â†’
   608â†’    // Remove participant from LiveKit room
   609â†’    try {
   610â†’      const roomClient = createRoomServiceClient();
   611â†’      await roomClient.removeParticipant(participant.session.roomName, validatedArgs.participantId);
   612â†’    } catch (liveKitError) {
   613â†’      // Failed to remove participant from LiveKit room - error logged at API layer
   614â†’      // Don't throw here - participant is already marked as left in database
   615â†’    }
   616â†’  } catch (error) {
   617â†’    // Error logged at API layer through error handling
   618â†’    throw ServiceError.system({
   619â†’      code: LIVESTREAM_ERROR_CODES.LIVEKIT_API_ERROR,
   620â†’      clientMessage: LIVESTREAM_ERRORS.LIVEKIT_API_ERROR,
   621â†’      logMessage: "LiveKit API error in leaveSession",
   622â†’      logDetails: {
   623â†’        originalError: error instanceof Error ? error.message : "Unknown error",
   624â†’        participantId: args.participantId,
   625â†’      },
   626â†’    });
   627â†’  }
   628â†’}
