import { dbOwner, streamingSessions, sessionParticipants, users } from "@athlead/database";
import { eq, inArray } from "@athlead/database/drizzle";
import { ServiceError, isServiceError } from "@athlead/shared-services/errors";
import { validateServiceArgs, validateServiceData } from "@athlead/shared-services/validation";
import { generateAccessToken, createRoomServiceClient } from "./tokens";
import type { TokenGrantRequest } from "./types";
import type { Room } from "livekit-server-sdk";
import { LIVESTREAM_ERRORS, LIVESTREAM_ERROR_CODES } from "./errors";
import { generateUniqueSlug } from "./utils";

import type {
  CreateSessionArgs,
  CreateSessionData,
  StartSessionArgs,
  EndSessionArgs,
  JoinSessionArgs,
  JoinSessionData,
  LeaveSessionArgs,
  GetSessionArgs,
  GetSessionBySlugArgs,
  GetSessionParticipantsArgs,
  GetSessionParticipantsData,
  GetSessionRoomStatusArgs,
  GetSessionRoomStatusData,
  ListActiveSessionsData,
  GetViewerCountsArgs,
  GetViewerCountsData,
  Session,
  SessionWithStreamer,
} from "./contracts";
import {
  CreateSessionArgsSchema,
  CreateSessionDataSchema,
  StartSessionArgsSchema,
  EndSessionArgsSchema,
  JoinSessionArgsSchema,
  JoinSessionDataSchema,
  LeaveSessionArgsSchema,
  GetSessionArgsSchema,
  GetSessionBySlugArgsSchema,
  GetSessionParticipantsArgsSchema,
  GetSessionParticipantsDataSchema,
  GetSessionRoomStatusArgsSchema,
  GetSessionRoomStatusDataSchema,
  ListActiveSessionsDataSchema,
  GetViewerCountsArgsSchema,
  GetViewerCountsDataSchema,
} from "./contracts";

// =============================================================================
// STREAMER OPERATIONS
// =============================================================================

/**
 * Creates a new streaming session with associated LiveKit room and token.
 * Coordinates database persistence with LiveKit room creation.
 *
 * @param args - The session creation request parameters
 * @returns Promise resolving to session details including token and playback URL
 * @throws Error if validation fails, room creation fails, or database insertion fails
 */
export async function createSession(args: CreateSessionArgs): Promise<CreateSessionData> {
  try {
    const validatedArgs = validateServiceArgs(CreateSessionArgsSchema, args, {
      service: "livestream",
      operation: "createSession",
      errorMessage: LIVESTREAM_ERRORS.CONTRACT_VIOLATION,
      errorCode: LIVESTREAM_ERROR_CODES.CONTRACT_VIOLATION,
    });
    const { authId, title, description, roomName } = validatedArgs;

    const user = await dbOwner.query.users.findFirst({
      where: eq(users.authId, authId),
    });

    if (!user) {
      throw ServiceError.notFound({
        code: LIVESTREAM_ERROR_CODES.USER_NOT_FOUND,
        clientMessage: "User not found",
        logMessage: "User not found for streamerId",
        logDetails: { authId },
      });
    }

    const slug = await generateUniqueSlug();

    const tokenRequest: TokenGrantRequest = {
      requesterRole: "streamer",
      requesterName: title,
      requesterIdentity: authId,
      roomName,
    };

    let liveKitRoom: Room | null = null;

    const roomClient = createRoomServiceClient();
    liveKitRoom = await roomClient.createRoom({
      name: roomName,
      emptyTimeout: 10 * 60, // 10 minutes
      maxParticipants: 50,
      metadata: JSON.stringify({
        title,
        description,
        streamerAuthId: authId,
      }),
    });

    console.log(roomClient, liveKitRoom);

    const token = await generateAccessToken(tokenRequest);

    const [session] = await dbOwner
      .insert(streamingSessions)
      .values({
        slug: slug,
        streamerId: user.id,
        title: title,
        description: description,
        roomName: roomName,
        liveKitRoomSid: liveKitRoom?.sid || null,
        status: "scheduled",
      })
      .returning();

    if (!session) {
      throw ServiceError.system({
        code: LIVESTREAM_ERROR_CODES.SESSION_CREATE_FAILED,
        clientMessage: LIVESTREAM_ERRORS.SESSION_CREATE_FAILED,
        logMessage: "Failed to create session in database",
        logDetails: {
          roomName,
        },
      });
    }

    const createdAt = session.createdAt ? new Date(session.createdAt) : new Date();

    const result = {
      sessionId: session.id,
      slug,
      token,
      roomName,
      playbackUrl: process.env.LIVEKIT_URL || "",
      createdAt: createdAt.toISOString(),
    };

    return validateServiceData(CreateSessionDataSchema, result, {
      service: "livestream",
      operation: "createSession",
      errorMessage: LIVESTREAM_ERRORS.CONTRACT_VIOLATION,
      errorCode: LIVESTREAM_ERROR_CODES.CONTRACT_VIOLATION,
    });
  } catch (error) {
    console.error("Create Session error:", error);
    // Rollback: If database insert failed but LiveKit room was created, clean it up
    try {
      const roomClient = createRoomServiceClient();
      await roomClient.deleteRoom(args.roomName);
    } catch (cleanupError) {
      // Cleanup errors are logged at API layer through error handling
    }

    // Re-throw ServiceErrors as-is, only wrap unexpected errors
    if (isServiceError(error)) {
      throw error;
    }

    throw ServiceError.system({
      code: LIVESTREAM_ERROR_CODES.SESSION_CREATE_FAILED,
      clientMessage: LIVESTREAM_ERRORS.SESSION_CREATE_FAILED,
      logMessage: "Session creation failed",
      logDetails: {
        originalError: error instanceof Error ? error.message : "Unknown error",
        roomName: args.roomName,
      },
    });
  }
}

/**
 * Starts a streaming session by updating its status to active.
 * Validates that the corresponding LiveKit room exists.
 *
 * @param args - Arguments containing sessionId
 * @returns Promise resolving to the updated session
 * @throws Error if session not found or validation fails
 */
export async function startSession(args: StartSessionArgs): Promise<Session> {
  try {
    // Validate arguments using Zod schema
    const validatedArgs = validateServiceArgs(StartSessionArgsSchema, args, {
      service: "livestream",
      operation: "startSession",
      errorMessage: LIVESTREAM_ERRORS.CONTRACT_VIOLATION,
      errorCode: LIVESTREAM_ERROR_CODES.CONTRACT_VIOLATION,
    });

    // Get session to validate it exists
    const existingSession = await dbOwner.query.streamingSessions.findFirst({
      where: eq(streamingSessions.id, validatedArgs.sessionId),
    });

    if (!existingSession) {
      throw ServiceError.notFound({
        code: LIVESTREAM_ERROR_CODES.SESSION_NOT_FOUND,
        clientMessage: LIVESTREAM_ERRORS.SESSION_NOT_FOUND,
        logMessage: "Session not found for startSession",
        logDetails: {
          sessionId: validatedArgs.sessionId,
        },
      });
    }

    // Validate ownership - only the session owner can start their session
    const user = await dbOwner.query.users.findFirst({
      where: eq(users.authId, validatedArgs.authId),
    });

    if (!user) {
      throw ServiceError.notFound({
        code: LIVESTREAM_ERROR_CODES.USER_NOT_FOUND,
        clientMessage: "User not found",
        logMessage: "User not found for ownership validation",
        logDetails: { authId: validatedArgs.authId },
      });
    }

    if (existingSession.streamerId !== user.id) {
      throw ServiceError.businessRule({
        code: LIVESTREAM_ERROR_CODES.SESSION_OWNERSHIP_REQUIRED,
        clientMessage: "Only the session owner can start the session",
        logMessage: "Attempted to start session without ownership",
        logDetails: {
          sessionId: validatedArgs.sessionId,
          authId: validatedArgs.authId,
          actualStreamerId: existingSession.streamerId,
          requestingUserId: user.id,
        },
      });
    }

    // Verify LiveKit room exists
    try {
      const roomClient = createRoomServiceClient();
      const rooms = await roomClient.listRooms([existingSession.roomName]);

      if (rooms.length === 0) {
        throw ServiceError.notFound({
          code: LIVESTREAM_ERROR_CODES.LIVEKIT_ROOM_NOT_FOUND,
          clientMessage: LIVESTREAM_ERRORS.LIVEKIT_ROOM_NOT_FOUND,
          logMessage: "LiveKit room not found for session",
          logDetails: { roomName: existingSession.roomName },
        });
      }
    } catch (liveKitError) {
      // LiveKit room verification failed - error logged at API layer
      throw ServiceError.businessRule({
        code: LIVESTREAM_ERROR_CODES.ROOM_UNAVAILABLE,
        clientMessage: LIVESTREAM_ERRORS.ROOM_UNAVAILABLE,
        logMessage: "LiveKit room verification failed",
        logDetails: {
          roomName: existingSession.roomName,
          liveKitError: liveKitError instanceof Error ? liveKitError.message : "Unknown error",
        },
      });
    }

    const [session] = await dbOwner
      .update(streamingSessions)
      .set({
        status: "active",
        startedAt: new Date(),
      })
      .where(eq(streamingSessions.id, validatedArgs.sessionId))
      .returning();

    if (!session) {
      throw ServiceError.system({
        code: LIVESTREAM_ERROR_CODES.SESSION_UPDATE_FAILED,
        clientMessage: LIVESTREAM_ERRORS.SESSION_UPDATE_FAILED,
        logMessage: "Failed to update session status to active",
        logDetails: {
          sessionId: validatedArgs.sessionId,
        },
      });
    }

    return session;
  } catch (error) {
    // Re-throw ServiceErrors as-is, only wrap unexpected errors
    if (isServiceError(error)) {
      throw error;
    }

    throw ServiceError.system({
      code: LIVESTREAM_ERROR_CODES.SESSION_CREATE_FAILED,
      clientMessage: LIVESTREAM_ERRORS.SESSION_CREATE_FAILED,
      logMessage: "LiveKit API error during session creation",
      logDetails: {
        originalError: error instanceof Error ? error.message : "Unknown error",
        sessionId: args.sessionId,
      },
    });
  }
}

/**
 * Ends a streaming session by updating its status to ended and deleting the LiveKit room.
 *
 * @param args - Arguments containing sessionId
 * @returns Promise resolving to the updated session
 * @throws Error if session not found or validation fails
 */
export async function endSession(args: EndSessionArgs): Promise<Session> {
  try {
    // Validate arguments using Zod schema
    const validatedArgs = validateServiceArgs(EndSessionArgsSchema, args, {
      service: "livestream",
      operation: "endSession",
      errorMessage: LIVESTREAM_ERRORS.CONTRACT_VIOLATION,
      errorCode: LIVESTREAM_ERROR_CODES.CONTRACT_VIOLATION,
    });

    // Get session to validate it exists and get roomName
    const existingSession = await dbOwner.query.streamingSessions.findFirst({
      where: eq(streamingSessions.id, validatedArgs.sessionId),
    });

    if (!existingSession) {
      throw ServiceError.notFound({
        code: LIVESTREAM_ERROR_CODES.SESSION_NOT_FOUND,
        clientMessage: LIVESTREAM_ERRORS.SESSION_NOT_FOUND,
        logMessage: "Session not found for endSession",
        logDetails: {
          sessionId: validatedArgs.sessionId,
        },
      });
    }

    // Validate ownership - only the session owner can end their session
    const user = await dbOwner.query.users.findFirst({
      where: eq(users.authId, validatedArgs.authId),
    });

    if (!user) {
      throw ServiceError.notFound({
        code: LIVESTREAM_ERROR_CODES.USER_NOT_FOUND,
        clientMessage: "User not found",
        logMessage: "User not found for ownership validation",
        logDetails: { authId: validatedArgs.authId },
      });
    }

    if (existingSession.streamerId !== user.id) {
      throw ServiceError.businessRule({
        code: LIVESTREAM_ERROR_CODES.SESSION_OWNERSHIP_REQUIRED,
        clientMessage: "Only the session owner can end the session",
        logMessage: "Attempted to end session without ownership",
        logDetails: {
          sessionId: validatedArgs.sessionId,
          authId: validatedArgs.authId,
          actualStreamerId: existingSession.streamerId,
          requestingUserId: user.id,
        },
      });
    }

    // Update session status in database first
    const [session] = await dbOwner
      .update(streamingSessions)
      .set({
        status: "ended",
        endedAt: new Date(),
      })
      .where(eq(streamingSessions.id, validatedArgs.sessionId))
      .returning();

    if (!session) {
      throw ServiceError.system({
        code: LIVESTREAM_ERROR_CODES.SESSION_UPDATE_FAILED,
        clientMessage: LIVESTREAM_ERRORS.SESSION_UPDATE_FAILED,
        logMessage: "Failed to update session status to ended",
        logDetails: {
          sessionId: validatedArgs.sessionId,
        },
      });
    }

    // Then delete the LiveKit room
    try {
      const roomClient = createRoomServiceClient();
      await roomClient.deleteRoom(existingSession.roomName);
    } catch (liveKitError) {
      // Failed to delete LiveKit room - error logged at API layer
      // Don't throw here - session is already marked as ended in database
    }

    // Finalize session (calculate analytics and cleanup participants)
    try {
      const { processSessionAnalyticsAndCleanup } = await import("./analytics.js");
      await processSessionAnalyticsAndCleanup({ sessionId: session.id, skipIfProcessed: false });
      console.log(`âœ… Session ${session.id} finalized with analytics`);
    } catch (analyticsError) {
      // Log but don't fail - analytics are non-critical for the main flow
      console.error("âŒ Failed to finalize session analytics:", analyticsError);
    }

    return session;
  } catch (error) {
    // Re-throw ServiceErrors as-is, only wrap unexpected errors
    if (isServiceError(error)) {
      throw error;
    }

    throw ServiceError.system({
      code: LIVESTREAM_ERROR_CODES.LIVEKIT_API_ERROR,
      clientMessage: LIVESTREAM_ERRORS.LIVEKIT_API_ERROR,
      logMessage: "LiveKit API error",
      logDetails: {
        originalError: error instanceof Error ? error.message : "Unknown error",
        sessionId: args.sessionId,
      },
    });
  }
}

// =============================================================================
// PARTICIPANT OPERATIONS
// =============================================================================

/**
 * Allows a user to join an existing streaming session as a viewer.
 * Validates session existence and generates appropriate access token.
 *
 * @param args - Join session configuration
 * @returns Promise resolving to join response with token and session details
 * @throws Error if session not found, validation fails, or token generation fails
 */
export async function joinSession(args: JoinSessionArgs): Promise<JoinSessionData> {
  try {
    // Validate arguments using Zod schema
    const validatedArgs = validateServiceArgs(JoinSessionArgsSchema, args, {
      service: "livestream",
      operation: "joinSession",
      errorMessage: LIVESTREAM_ERRORS.CONTRACT_VIOLATION,
      errorCode: LIVESTREAM_ERROR_CODES.CONTRACT_VIOLATION,
    });
    const { sessionId, authId, participantName, participantId } = validatedArgs;

    // Look up the user by authId to get the internal userId for foreign key
    const user = await dbOwner.query.users.findFirst({
      where: eq(users.authId, authId),
    });

    if (!user) {
      throw ServiceError.notFound({
        code: LIVESTREAM_ERROR_CODES.USER_NOT_FOUND,
        clientMessage: "User not found",
        logMessage: "User not found for userId",
        logDetails: { authId },
      });
    }

    // Get session from database to validate it exists and get roomName
    const session = await dbOwner.query.streamingSessions.findFirst({
      where: eq(streamingSessions.id, sessionId),
    });

    if (!session) {
      throw ServiceError.notFound({
        code: LIVESTREAM_ERROR_CODES.SESSION_NOT_FOUND,
        clientMessage: LIVESTREAM_ERRORS.SESSION_NOT_FOUND,
        logMessage: "Session not found for joinSession",
        logDetails: {
          sessionId,
        },
      });
    }

    if (session.status === "ended" || session.status === "cancelled") {
      throw ServiceError.conflict({
        code: LIVESTREAM_ERROR_CODES.SESSION_NOT_ACTIVE,
        clientMessage: LIVESTREAM_ERRORS.SESSION_NOT_ACTIVE,
        logMessage: "Session is not active for joining",
        logDetails: {
          sessionId,
          status: session.status,
        },
      });
    }

    // Check if room has reached max participants
    if (session.maxParticipants) {
      try {
        const roomClient = createRoomServiceClient();
        const participants = await roomClient.listParticipants(session.roomName);

        if (participants.length >= session.maxParticipants) {
          throw ServiceError.conflict({
            code: LIVESTREAM_ERROR_CODES.ROOM_AT_CAPACITY,
            clientMessage: LIVESTREAM_ERRORS.ROOM_AT_CAPACITY,
            logMessage: "Room has reached maximum participant capacity",
            logDetails: {
              sessionId,
              currentParticipants: participants.length,
              maxParticipants: session.maxParticipants,
            },
          });
        }
      } catch (liveKitError) {
        // Failed to check participant count - error logged at API layer
        // Continue anyway - let LiveKit handle the limit
      }
    }

    const roomName = session.roomName;

    // Generate access token first
    const tokenRequest: TokenGrantRequest = {
      requesterRole: "viewer",
      requesterName: participantName,
      requesterIdentity: authId, // Use authId for LiveKit identity
      roomName,
    };

    const token = await generateAccessToken(tokenRequest);

    // Track user joining session in database
    await dbOwner.insert(sessionParticipants).values({
      sessionId,
      userId: user.id, // Use internal userId for foreign key
      participantIdentity: participantId,
      participantName,
      role: "viewer",
      tokenIssuedAt: new Date(),
      tokenExpiresAt: new Date(Date.now() + 2 * 60 * 60 * 1000), // 2 hours from now
    });

    const result = {
      token,
      participantId,
      roomName,
      sessionId,
    };

    return validateServiceData(JoinSessionDataSchema, result, {
      service: "livestream",
      operation: "joinSession",
      errorMessage: LIVESTREAM_ERRORS.CONTRACT_VIOLATION,
      errorCode: LIVESTREAM_ERROR_CODES.CONTRACT_VIOLATION,
    });
  } catch (error) {
    // Error logged at API layer through error handling
    throw ServiceError.system({
      code: LIVESTREAM_ERROR_CODES.TOKEN_GENERATION_FAILED,
      clientMessage: LIVESTREAM_ERRORS.TOKEN_GENERATION_FAILED,
      logMessage: "Token generation failed in joinSession",
      logDetails: {
        originalError: error instanceof Error ? error.message : "Unknown error",
        sessionId: args.sessionId,
      },
    });
  }
}

/**
 * Marks a participant as having left the session and removes them from the LiveKit room.
 *
 * @param args - Arguments containing participantId
 * @returns Promise resolving when the participant is marked as left
 * @throws Error if participant not found, validation fails, or removal fails
 */
export async function leaveSession(args: LeaveSessionArgs): Promise<void> {
  try {
    // Validate arguments using Zod schema
    const validatedArgs = validateServiceArgs(LeaveSessionArgsSchema, args, {
      service: "livestream",
      operation: "leaveSession",
      errorMessage: LIVESTREAM_ERRORS.CONTRACT_VIOLATION,
      errorCode: LIVESTREAM_ERROR_CODES.CONTRACT_VIOLATION,
    });

    // First get the participant and session info
    const participant = await dbOwner.query.sessionParticipants.findFirst({
      where: eq(sessionParticipants.participantIdentity, validatedArgs.participantId),
      with: {
        session: true,
      },
    });

    if (!participant) {
      throw ServiceError.notFound({
        code: LIVESTREAM_ERROR_CODES.PARTICIPANT_NOT_FOUND,
        clientMessage: LIVESTREAM_ERRORS.PARTICIPANT_NOT_FOUND,
        logMessage: "Participant not found for leaveSession",
        logDetails: { participantId: validatedArgs.participantId },
      });
    }

    // Mark participant as left in database
    await dbOwner
      .update(sessionParticipants)
      .set({
        leftAt: new Date(),
      })
      .where(eq(sessionParticipants.participantIdentity, validatedArgs.participantId));

    // Remove participant from LiveKit room
    try {
      const roomClient = createRoomServiceClient();
      await roomClient.removeParticipant(participant.session.roomName, validatedArgs.participantId);
    } catch (liveKitError) {
      // Failed to remove participant from LiveKit room - error logged at API layer
      // Don't throw here - participant is already marked as left in database
    }
  } catch (error) {
    // Error logged at API layer through error handling
    throw ServiceError.system({
      code: LIVESTREAM_ERROR_CODES.LIVEKIT_API_ERROR,
      clientMessage: LIVESTREAM_ERRORS.LIVEKIT_API_ERROR,
      logMessage: "LiveKit API error in leaveSession",
      logDetails: {
        originalError: error instanceof Error ? error.message : "Unknown error",
        participantId: args.participantId,
      },
    });
  }
}

