/**
 * Gets the current status of a LiveKit room and updates session accordingly.
 *
 * @param args - Arguments containing sessionId
 * @returns Promise resolving to room status information
 * @throws Error if session not found or validation fails
 */
export async function getSessionRoomStatus(
  args: GetSessionRoomStatusArgs,
): Promise<GetSessionRoomStatusData> {
  try {
    // Validate arguments using Zod schema
    const validatedArgs = validateServiceArgs(GetSessionRoomStatusArgsSchema, args, {
      service: "livestream",
      operation: "getSessionRoomStatus",
      errorMessage: LIVESTREAM_ERRORS.CONTRACT_VIOLATION,
      errorCode: LIVESTREAM_ERROR_CODES.CONTRACT_VIOLATION,
    });

    const session = await dbOwner.query.streamingSessions.findFirst({
      where: eq(streamingSessions.id, validatedArgs.sessionId),
    });

    if (!session) {
      throw ServiceError.notFound({
        code: LIVESTREAM_ERROR_CODES.SESSION_NOT_FOUND,
        clientMessage: LIVESTREAM_ERRORS.SESSION_NOT_FOUND,
        logMessage: "Session not found",
        logDetails: {
          sessionId: validatedArgs.sessionId,
        },
      });
    }

    const roomClient = createRoomServiceClient();

    // Get room info
    const rooms = await roomClient.listRooms([session.roomName]);
    const room = rooms.length > 0 ? rooms[0] : null;

    let participants: any[] = [];
    if (room) {
      try {
        participants = await roomClient.listParticipants(session.roomName);
      } catch (error) {
        // Failed to get participants - error logged at API layer
      }
    }

    const result = {
      sessionId: validatedArgs.sessionId,
      roomExists: !!room,
      participantCount: room ? room.numParticipants : 0,
      status: session.status,
      liveKitRoom: room
        ? {
            sid: room.sid,
            name: room.name,
            numParticipants: room.numParticipants,
            metadata: (() => {
              try {
                const parsed = JSON.parse(room.metadata);
                const { streamerAuthId, ...safeMetadata } = parsed;
                return JSON.stringify(safeMetadata);
              } catch {
                return room.metadata;
              }
            })(),
            creationTime: Number(room.creationTime),
            activeRecording: room.activeRecording,
          }
        : undefined,
      participants: participants.map(p => ({
        identity: "***",
        name: p.name || "",
        joinedAt: p.joinedAt,
        state: p.state,
        isPublisher: p.permission?.canPublish || false,
      })),
    };

    return validateServiceData(GetSessionRoomStatusDataSchema, result, {
      service: "livestream",
      operation: "getSessionRoomStatus",
      errorMessage: LIVESTREAM_ERRORS.CONTRACT_VIOLATION,
      errorCode: LIVESTREAM_ERROR_CODES.CONTRACT_VIOLATION,
    });
  } catch (error) {
    if (isServiceError(error)) {
      throw error;
    }

    throw ServiceError.system({
      code: LIVESTREAM_ERROR_CODES.LIVEKIT_API_ERROR,
      clientMessage: LIVESTREAM_ERRORS.LIVEKIT_API_ERROR,
      logMessage: "LiveKit API error",
      logDetails: {
        originalError: error instanceof Error ? error.message : "Unknown error",
        sessionId: args.sessionId,
      },
    });
  }
}

/**
 * Lists all currently active streaming sessions with streamer information.
 *
 * @returns Promise resolving to array of active sessions with room status and streamer details
 */
export async function listActiveSessions(): Promise<ListActiveSessionsData> {
  try {
    // Join with users table to get streamer information
    const activeSessions = await dbOwner.query.streamingSessions.findMany({
      where: eq(streamingSessions.status, "active"),
      with: {
        streamer: {
          columns: {
            firstName: true,
            lastName: true,
            userName: true,
            profilePictureUrl: true,
          },
        },
      },
    });

    const roomClient = createRoomServiceClient();
    const allRooms = await roomClient.listRooms();

    const result = activeSessions.map(session => {
      const correspondingRoom = allRooms.find(room => room.name === session.roomName);

      // Format streamer name, fallback to userName if no first/last name
      const streamerName =
        session.streamer.firstName && session.streamer.lastName
          ? `${session.streamer.firstName} ${session.streamer.lastName}`.trim()
          : session.streamer.userName;

      return {
        sessionId: session.id,
        slug: session.slug,
        title: session.title,
        streamerId: session.streamerId,
        roomName: session.roomName,
        status: session.status,
        startedAt: session.startedAt?.toISOString() || null,
        participantCount: correspondingRoom ? correspondingRoom.numParticipants : 0,
        liveKitRoomExists: !!correspondingRoom,
        liveKitRoomSid: correspondingRoom?.sid,
        // Streamer information
        streamerName,
        streamerUserName: session.streamer.userName,
        streamerProfileUrl: session.streamer.profilePictureUrl,
      };
    });

    return validateServiceData(ListActiveSessionsDataSchema, result, {
      service: "livestream",
      operation: "listActiveSessions",
      errorMessage: LIVESTREAM_ERRORS.CONTRACT_VIOLATION,
      errorCode: LIVESTREAM_ERROR_CODES.CONTRACT_VIOLATION,
    });
  } catch (error) {
    // Error logged at API layer through error handling
    throw ServiceError.system({
      code: LIVESTREAM_ERROR_CODES.LIVEKIT_API_ERROR,
      clientMessage: LIVESTREAM_ERRORS.LIVEKIT_API_ERROR,
      logMessage: "LiveKit API error in listActiveSessions",
      logDetails: {
        originalError: error instanceof Error ? error.message : "Unknown error",
      },
    });
  }
}

/**
 * Gets viewer counts for multiple sessions.
 * Used for batch polling to update viewer counts on the client.
 *
 * @param args - Object containing array of session IDs
 * @returns Promise resolving to a record of sessionId to viewer count
 * @throws ServiceError if validation fails or API call fails
 */
export async function getViewerCounts(args: GetViewerCountsArgs): Promise<GetViewerCountsData> {
  try {
    // Validate arguments using Zod schema
    const validatedArgs = validateServiceArgs(GetViewerCountsArgsSchema, args, {
      service: "livestream",
      operation: "getViewerCounts",
      errorMessage: LIVESTREAM_ERRORS.CONTRACT_VIOLATION,
      errorCode: LIVESTREAM_ERROR_CODES.CONTRACT_VIOLATION,
    });

    const { sessionIds } = validatedArgs;

    // Find sessions by IDs and their room names
    const sessions = await dbOwner.query.streamingSessions.findMany({
      where: inArray(streamingSessions.id, sessionIds),
      columns: {
        id: true,
        roomName: true,
        status: true,
      },
    });

    if (sessions.length === 0) {
      console.warn("No sessions found for getViewerCounts", { sessionIds });
      return {};
    }

    const roomClient = createRoomServiceClient();
    const viewerCounts: Record<string, number> = {};

    for (const session of sessions) {
      try {
        if (session.status !== "active") {
          viewerCounts[session.id] = 0;
          continue;
        }

        const rooms = await roomClient.listRooms([session.roomName]);
        const room = rooms.find(r => r.name === session.roomName);

        if (room) {
          viewerCounts[session.id] = room.numParticipants;
        } else {
          viewerCounts[session.id] = 0;
        }
      } catch (roomError) {
        viewerCounts[session.id] = 0;
      }
    }

    return validateServiceData(GetViewerCountsDataSchema, viewerCounts, {
      service: "livestream",
      operation: "getViewerCounts",
      errorMessage: LIVESTREAM_ERRORS.CONTRACT_VIOLATION,
      errorCode: LIVESTREAM_ERROR_CODES.CONTRACT_VIOLATION,
    });
  } catch (error) {
    console.error("âŒ Error getting viewer counts:", error);

    if (error instanceof ServiceError) {
      throw error;
    }

    throw ServiceError.system({
      code: LIVESTREAM_ERROR_CODES.LIVEKIT_API_ERROR,
      clientMessage: LIVESTREAM_ERRORS.LIVEKIT_API_ERROR,
      logMessage: "LiveKit API error in getViewerCounts",
      logDetails: {
        sessionIds: args.sessionIds,
        originalError: error instanceof Error ? error.message : "Unknown error",
      },
    });
  }
}

