import Foundation

class LivestreamService {
    static let shared = LivestreamService()

    private init() {}

    // MARK: - Request/Response Models

    struct CreateSessionRequest: Encodable {
        let roomName: String
        let title: String
        let description: String?
    }

    struct CreateSessionResponse: Decodable {
        let sessionId: String
        let roomName: String
        let slug: String
        let token: String
        let playbackUrl: String
        let createdAt: String

        // Computed property for compatibility
        var id: String { sessionId }
        var serverUrl: String { playbackUrl }
    }

    struct JoinSessionRequest: Encodable {
        let participantName: String
        let participantId: String?
    }

    struct JoinSessionResponse: Decodable {
        let token: String
        let participantId: String
        let roomName: String
        let serverUrl: String
    }

    struct SessionData: Decodable {
        let id: String
        let roomName: String
        let slug: String
        let title: String
        let description: String?
        let authId: String
        let status: String
        let viewCount: Int
        let peakViewCount: Int
        let scheduledStart: String?
        let startedAt: String?
        let endedAt: String?
    }

    struct ActiveSessionsResponse: Decodable {
        let sessions: [SessionData]
    }

    // MARK: - Session Management

    /// Create a new livestream session
    func createSession(title: String, roomName: String, description: String? = nil) async throws -> CreateSessionResponse {
        let request = CreateSessionRequest(
            roomName: roomName,
            title: title,
            description: description
        )

        let response: CreateSessionResponse = try await APIClient.shared.request(
            endpoint: .livestream("/api/livestream/sessions"),
            method: "POST",
            body: request
        )

        return response
    }

    /// Join a livestream session (as streamer or viewer)
    func joinSession(sessionId: String, participantName: String, participantId: String? = nil) async throws -> JoinSessionResponse {
        let request = JoinSessionRequest(
            participantName: participantName,
            participantId: participantId
        )

        let response: JoinSessionResponse = try await APIClient.shared.request(
            endpoint: .livestream("/api/livestream/sessions/\(sessionId)/join"),
            method: "POST",
            body: request
        )

        return response
    }

    /// Start a session (broadcaster only)
    func startSession(sessionId: String) async throws -> SessionData {
        let response: SessionData = try await APIClient.shared.request(
            endpoint: .livestream("/api/livestream/sessions/\(sessionId)/start"),
            method: "PUT"
        )

        return response
    }

    /// End a session (broadcaster only)
    func endSession(sessionId: String) async throws -> SessionData {
        let response: SessionData = try await APIClient.shared.request(
            endpoint: .livestream("/api/livestream/sessions/\(sessionId)/end"),
            method: "PUT"
        )

        return response
    }

    /// Get session by ID
    func getSession(sessionId: String) async throws -> SessionData {
        let response: SessionData = try await APIClient.shared.request(
            endpoint: .livestream("/api/livestream/sessions/\(sessionId)"),
            method: "GET"
        )

        return response
    }

    /// Get session by slug
    func getSessionBySlug(slug: String) async throws -> SessionData {
        let response: SessionData = try await APIClient.shared.request(
            endpoint: .livestream("/api/livestream/sessions/by-slug/\(slug)"),
            method: "GET"
        )

        return response
    }

    /// Get list of active sessions
    func getActiveSessions() async throws -> ActiveSessionsResponse {
        let response: ActiveSessionsResponse = try await APIClient.shared.request(
            endpoint: .livestream("/api/livestream/sessions/active"),
            method: "GET"
        )

        return response
    }
}

// Empty response for endpoints that don't return data
struct EmptyResponse: Decodable {}

// MARK: - Usage Example

/*
 Example usage in a view model:

 @MainActor
 class LiveStreamViewModel: ObservableObject {
     @Published var isLoading = false
     @Published var error: String?
     @Published var currentSession: SessionResponse?

     func startLivestream(roomName: String, title: String) async {
         isLoading = true
         defer { isLoading = false }

         do {
             let session = try await LivestreamService.shared.createSession(
                 roomName: roomName,
                 title: title,
                 isPublic: true
             )
             currentSession = session
         } catch {
             self.error = error.localizedDescription
         }
     }

     func endLivestream() async {
         guard let roomName = currentSession?.roomName else { return }

         do {
             try await LivestreamService.shared.endSession(roomName: roomName)
             currentSession = nil
         } catch {
             self.error = error.localizedDescription
         }
     }
 }
*/

