//
//  LiveKitTestView.swift
//  AthleadAI
//
//  Test view to verify LiveKit integration without full UI flow
//

import SwiftUI

struct LiveKitTestView: View {
    @Environment(\.designTheme) private var theme

    @State private var status = "Ready to test LiveKit integration"
    @State private var isLoading = false
    @State private var testResults: [TestResult] = []
    @State private var sessionId: String?
    @State private var showingLiveStream = false

    struct TestResult: Identifiable {
        let id = UUID()
        let test: String
        let success: Bool
        let message: String
        let timestamp: Date
    }

    var body: some View {
        ZStack {
            // Background
            LinearGradient(
                gradient: Gradient(colors: [
                    theme.color(.background),
                    theme.color(.muted).opacity(0.3)
                ]),
                startPoint: .topLeading,
                endPoint: .bottomTrailing
            )
            .ignoresSafeArea()

            ScrollView {
                VStack(spacing: .spacing6) {
                    // Status Card
                    VStack(alignment: .leading, spacing: .spacing3) {
                        HStack {
                            Image(systemName: isLoading ? "circle.circle" : "checkmark.circle")
                                .font(theme.font(.title2))
                                .foregroundStyle(isLoading ? theme.color(.primary) : theme.color(.success))
                                .symbolEffect(.pulse, isActive: isLoading)

                            Text("Status")
                                .font(theme.font(.title3))
                                .fontWeight(.bold)
                                .foregroundStyle(theme.color(.foreground))
                        }

                        Text(status)
                            .font(theme.font(.body))
                            .foregroundStyle(theme.color(.mutedforeground))
                            .frame(maxWidth: .infinity, alignment: .leading)
                    }
                    .padding(.spacing5)
                    .background(
                        RoundedRectangle(cornerRadius: .radiusLarge)
                            .fill(theme.color(.card).opacity(0.4))
                            .overlay(
                                RoundedRectangle(cornerRadius: .radiusLarge)
                                    .stroke(theme.color(.border), lineWidth: 1)
                            )
                    )
                    .padding(.horizontal, .spacing4)

                    // Test Buttons
                    VStack(spacing: .spacing3) {
                        TestButton(
                            title: "Test Configuration",
                            icon: "gearshape",
                            color: theme.color(.primary),
                            isLoading: isLoading
                        ) {
                            testConfiguration()
                        }

                        TestButton(
                            title: "Test Create Session",
                            icon: "plus.circle",
                            color: theme.color(.success),
                            isLoading: isLoading
                        ) {
                            testCreateSession()
                        }

                        TestButton(
                            title: "Test Join Session",
                            icon: "person.badge.plus",
                            color: theme.color(.primary),
                            isLoading: isLoading,
                            disabled: sessionId == nil
                        ) {
                            testJoinSession()
                        }

                        TestButton(
                            title: "Test Full Flow",
                            icon: "play.circle.fill",
                            color: theme.color(.success),
                            isLoading: isLoading
                        ) {
                            testFullFlow()
                        }

                        TestButton(
                            title: "Go Live (Real Test)",
                            icon: "video.fill",
                            color: theme.color(.destructive),
                            isLoading: isLoading
                        ) {
                            showingLiveStream = true
                        }
                    }
                    .padding(.horizontal, .spacing4)

                    // Test Results
                    if !testResults.isEmpty {
                        VStack(alignment: .leading, spacing: .spacing3) {
                            HStack {
                                Image(systemName: "list.bullet.clipboard")
                                    .font(theme.font(.title3))
                                    .foregroundStyle(theme.color(.foreground))

                                Text("Test Results")
                                    .font(theme.font(.title3))
                                    .fontWeight(.bold)
                                    .foregroundStyle(theme.color(.foreground))

                                Spacer()

                                Button(action: {
                                    testResults.removeAll()
                                }) {
                                    Text("Clear")
                                        .font(theme.font(.caption1))
                                        .foregroundStyle(theme.color(.primary))
                                }
                            }

                            ForEach(testResults.reversed()) { result in
                                TestResultRow(result: result)
                            }
                        }
                        .padding(.spacing5)
                        .background(
                            RoundedRectangle(cornerRadius: .radiusLarge)
                                .fill(theme.color(.card).opacity(0.4))
                                .overlay(
                                    RoundedRectangle(cornerRadius: .radiusLarge)
                                        .stroke(theme.color(.border), lineWidth: 1)
                                )
                        )
                        .padding(.horizontal, .spacing4)
                    }
                }
                .padding(.vertical, .spacing5)
            }
        }
        .navigationTitle("LiveKit Test")
        .navigationBarTitleDisplayMode(.inline)
        .fullScreenCover(isPresented: $showingLiveStream) {
            LiveStreamView(
                streamTitle: "Test Stream",
                streamDescription: "Testing LiveKit Integration",
                isCameraEnabled: true,
                isMicrophoneEnabled: true
            )
        }
    }

    // MARK: - Test Functions

    func testConfiguration() {
        isLoading = true
        status = "Testing configuration..."

        Task {
            await Task.sleep(500_000_000) // 0.5 second delay

            var errors: [String] = []

            if Config.clerkPublishableKey.isEmpty {
                errors.append("Clerk publishable key not set")
            }

            if Config.livestreamAPIURL.isEmpty {
                errors.append("Livestream API URL not set")
            }

            if Config.usersAPIURL.isEmpty {
                errors.append("Users API URL not set")
            }

            if Config.liveKitURL.isEmpty {
                errors.append("LiveKit URL not set")
            }

            if errors.isEmpty {
                status = "âœ… Configuration is valid"
                addResult(test: "Configuration", success: true, message: "All required environment variables are set")
            } else {
                status = "âŒ Configuration has issues"
                addResult(test: "Configuration", success: false, message: errors.joined(separator: ", "))
            }

            isLoading = false
        }
    }

    func testCreateSession() {
        isLoading = true
        status = "Creating test session..."

        Task {
            do {
                let session = try await LivestreamService.shared.createSession(
                    title: "Test Stream \(Date().timeIntervalSince1970)",
                    description: "Automated test session"
                )

                sessionId = session.id
                status = "âœ… Session created successfully"
                addResult(
                    test: "Create Session",
                    success: true,
                    message: "Session ID: \(session.id)\nRoom: \(session.roomName)"
                )
            } catch {
                status = "âŒ Failed to create session"
                addResult(test: "Create Session", success: false, message: error.localizedDescription)
            }

            isLoading = false
        }
    }

    func testJoinSession() {
        guard let sessionId = sessionId else {
            status = "âŒ No session to join. Create a session first."
            return
        }

        isLoading = true
        status = "Joining session..."

        Task {
            do {
                let joinResponse = try await LivestreamService.shared.joinSession(
                    sessionId: sessionId,
                    participantName: "Test Participant"
                )

                status = "âœ… Successfully joined session"
                addResult(
                    test: "Join Session",
                    success: true,
                    message: "Room: \(joinResponse.roomName)\nServer: \(joinResponse.serverUrl)"
                )
            } catch {
                status = "âŒ Failed to join session"
                addResult(test: "Join Session", success: false, message: error.localizedDescription)
            }

            isLoading = false
        }
    }

    func testFullFlow() {
        isLoading = true
        status = "Running full flow test..."

        Task {
            do {
                // Step 1: Create session
                status = "1/3 Creating session..."
                let session = try await LivestreamService.shared.createSession(
                    title: "Full Flow Test \(Date().timeIntervalSince1970)",
                    description: "Testing complete flow"
                )

                await Task.sleep(500_000_000) // 0.5 second

                // Step 2: Join session
                status = "2/3 Joining session..."
                let joinResponse = try await LivestreamService.shared.joinSession(
                    sessionId: session.id,
                    participantName: "Test Broadcaster"
                )

                await Task.sleep(500_000_000) // 0.5 second

                // Step 3: Get session details
                status = "3/3 Verifying session..."
                let sessionData = try await LivestreamService.shared.getSession(sessionId: session.id)

                status = "âœ… Full flow test completed successfully"
                addResult(
                    test: "Full Flow",
                    success: true,
                    message: "Created session: \(session.id)\nJoined room: \(joinResponse.roomName)\nServer: \(joinResponse.serverUrl)\nStatus: \(sessionData.status)"
                )

                sessionId = session.id
            } catch {
                status = "âŒ Full flow test failed"
                addResult(test: "Full Flow", success: false, message: error.localizedDescription)
            }

            isLoading = false
        }
    }

    func addResult(test: String, success: Bool, message: String) {
        testResults.append(TestResult(
            test: test,
            success: success,
            message: message,
            timestamp: Date()
        ))
    }
}

// MARK: - Supporting Views

struct TestButton: View {
    @Environment(\.designTheme) private var theme

    let title: String
    let icon: String
    let color: Color
    let isLoading: Bool
    var disabled: Bool = false
    let action: () -> Void

    var body: some View {
        Button(action: action) {
            HStack {
                Image(systemName: icon)
                    .font(theme.font(.headline))

                Text(title)
                    .font(theme.font(.headline))
                    .fontWeight(.semibold)

                Spacer()

                if isLoading {
                    ProgressView()
                        .tint(theme.color(.foreground))
                }
            }
            .foregroundStyle(theme.color(.foreground))
            .padding(.spacing4)
            .background(
                RoundedRectangle(cornerRadius: .radiusMedium)
                    .fill(color.opacity(disabled ? 0.3 : 0.8))
            )
        }
        .disabled(isLoading || disabled)
        .opacity((isLoading || disabled) ? 0.5 : 1.0)
    }
}

struct TestResultRow: View {
    @Environment(\.designTheme) private var theme

    let result: LiveKitTestView.TestResult

    var formattedTime: String {
        let formatter = DateFormatter()
        formatter.timeStyle = .medium
        return formatter.string(from: result.timestamp)
    }

    var body: some View {
        VStack(alignment: .leading, spacing: .spacing2) {
            HStack {
                Image(systemName: result.success ? "checkmark.circle.fill" : "xmark.circle.fill")
                    .font(theme.font(.body))
                    .foregroundStyle(result.success ? theme.color(.success) : theme.color(.destructive))

                Text(result.test)
                    .font(theme.font(.headline))
                    .foregroundStyle(theme.color(.foreground))

                Spacer()

                Text(formattedTime)
                    .font(theme.font(.caption2))
                    .foregroundStyle(theme.color(.mutedforeground))
            }

            Text(result.message)
                .font(theme.font(.caption1))
                .foregroundStyle(theme.color(.mutedforeground))
                .fixedSize(horizontal: false, vertical: true)
        }
        .padding(.spacing3)
        .background(
            RoundedRectangle(cornerRadius: .radiusSmall)
                .fill(result.success ? theme.color(.success).opacity(0.1) : theme.color(.destructive).opacity(0.1))
        )
    }
}

// Helper extension
extension Task where Success == Never, Failure == Never {
    static func sleep(_ nanoseconds: UInt64) async {
        try? await Task.sleep(nanoseconds: nanoseconds)
    }
}

