//
//  ClerkService.swift
//  AthleadAI
//
//  Helper extension for Clerk authentication
//

import Foundation
import Clerk

extension Clerk {
    /// Get the current session token for authenticated API calls
    func getSessionToken() async throws -> String {
        guard let session = self.session else {
            throw ClerkError.notAuthenticated
        }

        guard let token = try await session.getToken() else {
            throw ClerkError.tokenFetchFailed
        }

        return token.jwt
    }
}

enum ClerkError: LocalizedError {
    case notAuthenticated
    case tokenFetchFailed

    var errorDescription: String? {
        switch self {
        case .notAuthenticated:
            return "User is not authenticated"
        case .tokenFetchFailed:
            return "Failed to fetch session token"
        }
    }
}

