import SwiftUI

struct WelcomeView: View {
    @Binding var showSignIn: Bool
    @State private var currentPage = 0

    var body: some View {
        ZStack {
            // Background
            Color(.systemBackground)
                .ignoresSafeArea()

            VStack(spacing: 40) {
                Spacer()

                // App icon and name
                VStack(spacing: 16) {
                    Image(systemName: "sportscourt.fill")
                        .font(.system(size: 80))
                        .foregroundStyle(
                            LinearGradient(
                                colors: [.blue, .purple],
                                startPoint: .topLeading,
                                endPoint: .bottomTrailing
                            )
                        )

                    Text("AthleadAI")
                        .font(.system(size: 42, weight: .bold, design: .rounded))
                }
                .padding(.top, 20)

                // Features
                TabView(selection: $currentPage) {
                    FeatureCard(
                        icon: "person.3.fill",
                        title: "Connect with Coaches",
                        description: "Build relationships with college coaches and recruiters across the country"
                    )
                    .tag(0)

                    FeatureCard(
                        icon: "sparkles",
                        title: "AI-Powered Insights",
                        description: "Get personalized recommendations and insights to improve your recruiting journey"
                    )
                    .tag(1)

                    FeatureCard(
                        icon: "video.fill",
                        title: "Showcase Your Talent",
                        description: "Share highlights, stats, and achievements to stand out to recruiters"
                    )
                    .tag(2)
                }
                .tabViewStyle(.page(indexDisplayMode: .always))
                .indexViewStyle(.page(backgroundDisplayMode: .always))
                .frame(height: 280)

                Spacer()

                // Get Started button
                VStack(spacing: 16) {
                    Button {
                        withAnimation(.easeInOut(duration: 0.3)) {
                            showSignIn = true
                        }
                    } label: {
                        HStack {
                            Text("Get Started")
                                .font(.headline)
                            Image(systemName: "arrow.right")
                        }
                        .frame(maxWidth: .infinity)
                        .padding()
                        .background(
                            LinearGradient(
                                colors: [.blue, .purple],
                                startPoint: .leading,
                                endPoint: .trailing
                            )
                        )
                        .foregroundStyle(.white)
                        .cornerRadius(14)
                    }

                    Text("By continuing, you agree to our Terms & Privacy Policy")
                        .font(.caption)
                        .foregroundColor(.secondary)
                        .multilineTextAlignment(.center)
                }
                .padding(.horizontal, 32)
                .padding(.bottom, 40)
            }
        }
    }
}

// MARK: - Feature Card Component
struct FeatureCard: View {
    let icon: String
    let title: String
    let description: String

    var body: some View {
        VStack(spacing: 20) {
            Image(systemName: icon)
                .font(.system(size: 60))
                .foregroundStyle(
                    LinearGradient(
                        colors: [.blue, .purple],
                        startPoint: .topLeading,
                        endPoint: .bottomTrailing
                    )
                )

            VStack(spacing: 12) {
                Text(title)
                    .font(.title2)
                    .fontWeight(.bold)

                Text(description)
                    .font(.body)
                    .foregroundColor(.secondary)
                    .multilineTextAlignment(.center)
                    .padding(.horizontal, 40)
            }
        }
        .padding()
    }
}

