//
//  RootView.swift
//  AthleadAI
//
//  Root view that handles authentication routing and app flow
//

import SwiftUI
import Clerk

struct RootView: View {
    @Environment(\.clerk) private var clerk
    @State private var showSplash = true
    @State private var showWelcome = false
    @State private var contentOpacity: Double = 0

    var body: some View {
        ZStack {
            if showSplash {
                // Splash screen shown initially
                SplashScreenView()
                    .transition(.opacity)
            } else {
                // Main content after splash
                Group {
                    if clerk.loadingState == .notLoaded {
                        // Clerk is still loading
                        LoadingView()
                    } else if clerk.user != nil {
                        // User is authenticated - show main app
                        ContentView()
                            .transition(.opacity)
                    } else {
                        // User is not authenticated - show welcome/sign in
                        if showWelcome {
                            SignInView()
                                .transition(.asymmetric(
                                    insertion: .move(edge: .trailing),
                                    removal: .move(edge: .leading)
                                ))
                        } else {
                            WelcomeView(showSignIn: $showWelcome)
                                .transition(.opacity)
                        }
                    }
                }
                .opacity(contentOpacity)
            }
        }
        .animation(.easeInOut(duration: 0.5), value: showSplash)
        .animation(.easeInOut(duration: 0.3), value: showWelcome)
        .animation(.easeInOut(duration: 0.3), value: clerk.user)
        .task {
            // Show splash screen for 1.5 seconds
            try? await Task.sleep(nanoseconds: 1_500_000_000)

            withAnimation {
                showSplash = false
            }

            // Fade in content
            withAnimation(.easeIn(duration: 0.3)) {
                contentOpacity = 1
            }
        }
    }
}

// MARK: - Loading View Component
struct LoadingView: View {
    @State private var isAnimating = false

    var body: some View {
        ZStack {
            Color(.systemBackground)
                .ignoresSafeArea()

            VStack(spacing: 20) {
                Image(systemName: "sportscourt.fill")
                    .font(.system(size: 60))
                    .foregroundStyle(
                        LinearGradient(
                            colors: [.blue, .purple],
                            startPoint: .topLeading,
                            endPoint: .bottomTrailing
                        )
                    )
                    .rotationEffect(.degrees(isAnimating ? 360 : 0))
                    .animation(
                        .linear(duration: 2)
                            .repeatForever(autoreverses: false),
                        value: isAnimating
                    )

                ProgressView()
                    .scaleEffect(1.2)
                    .tint(.blue)

                Text("Loading...")
                    .font(.subheadline)
                    .foregroundColor(.secondary)
            }
        }
        .onAppear {
            isAnimating = true
        }
    }
}

#Preview("Root - Authenticated") {
    RootView()
        .environment(\.clerk, Clerk.shared)
}

#Preview("Loading") {
    LoadingView()
}

