import Foundation

/// Configuration for LiveKit streaming
struct LiveKitConfig {
    private static let sandboxID = "recruitai-2kc5jh"
    private static let tokenServerURL = "https://cloud-api.livekit.io/api/sandbox/connection-details"

    /// Function to generate access tokens via LiveKit Cloud API
    static func generateToken(roomName: String, participantName: String) async throws -> (token: String, serverURL: String) {
        var request = URLRequest(url: URL(string: tokenServerURL)!)
        request.httpMethod = "POST"
        request.setValue(sandboxID, forHTTPHeaderField: "X-Sandbox-ID")
        request.setValue("application/json", forHTTPHeaderField: "Content-Type")

        let body: [String: Any] = [
            "room_name": roomName,
            "participant_name": participantName
        ]
        request.httpBody = try JSONSerialization.data(withJSONObject: body)

        let (data, response) = try await URLSession.shared.data(for: request)

        guard let httpResponse = response as? HTTPURLResponse else {
            throw NSError(domain: "LiveKitConfig", code: -1, userInfo: [NSLocalizedDescriptionKey: "Invalid response"])
        }

        if httpResponse.statusCode != 200 {
            let errorBody = String(data: data, encoding: .utf8) ?? "Unknown error"
            print("LiveKit API Error (\(httpResponse.statusCode)): \(errorBody)")
            throw NSError(domain: "LiveKitConfig", code: httpResponse.statusCode, userInfo: [NSLocalizedDescriptionKey: "API returned status \(httpResponse.statusCode): \(errorBody)"])
        }

        guard let json = try JSONSerialization.jsonObject(with: data) as? [String: Any] else {
            let responseBody = String(data: data, encoding: .utf8) ?? "Unable to decode"
            print("LiveKit API Response (not JSON): \(responseBody)")
            throw NSError(domain: "LiveKitConfig", code: -1, userInfo: [NSLocalizedDescriptionKey: "Invalid JSON response"])
        }

        print("LiveKit API Response: \(json)")

        guard let token = json["participantToken"] as? String,
              let serverURL = json["serverUrl"] as? String else {
            print("LiveKit API Response missing fields. Keys: \(json.keys)")
            throw NSError(domain: "LiveKitConfig", code: -1, userInfo: [NSLocalizedDescriptionKey: "Missing participantToken or serverUrl in response"])
        }

        return (token, serverURL)
    }
}

