    static let usersAPIURL = ProcessInfo.processInfo.environment["USERS_API_URL"] ?? "http://localhost:3002"

    // MARK: - LiveKit (for streaming)
    // Note: iOS app typically doesn't need API key/secret
    // The backend generates tokens using these credentials
    static let liveKitURL = ProcessInfo.processInfo.environment["LIVEKIT_URL"] ?? ""

    // MARK: - Database (if needed client-side)
    static let neonDatabaseURL = ProcessInfo.processInfo.environment["DATABASE_USER_URL"] ?? ""

    // MARK: - Email / Gmail API
    static let gmailAPIKey = ProcessInfo.processInfo.environment["GMAIL_API_KEY"] ?? ""
    static let gmailClientID = ProcessInfo.processInfo.environment["GMAIL_CLIENT_ID"] ?? ""

    // MARK: - Search API (Optional - using free DuckDuckGo by default)
    // If you want to use Google Custom Search instead:
    // static let googleSearchAPIKey = "YOUR_GOOGLE_API_KEY"
