  "sidebar-accent-foreground": "#07362B",
  "sidebar-border": "#D7DEE7",
  "sidebar-ring": "#5AAAF6"
]
  private static let darkHex: [String:String]  = [
  "background": "#0E1116",
  "foreground": "#E6EAF0",
  "card": "#151A21",
  "card-foreground": "#E6EAF0",
  "popover": "#151A21",
  "popover-foreground": "#E6EAF0",
  "primary": "#61AEF8",
  "primary-foreground": "#0A0E14",
  "secondary": "#2D3748",
  "secondary-foreground": "#D1D5DB",
  "muted": "#1F2730",
