  "sidebar-border": "#2A3440",
  "sidebar-ring": "#5AAAF6"
]
}

// MARK: - Example components

public struct DSButton: View {
  public enum Intent { case primary, secondary, destructive }
  public enum Size { case s, m, l }
  public var title: String
  public var intent: Intent = .primary
  public var size: Size = .m
  public var action: () -> Void

  @Environment(\.colorScheme) private var scheme

  public init(_ title: String, intent: Intent = .primary, size: Size = .m, action: @escaping () -> Void) {
    self.title = title
    self.intent = intent
    self.size = size
    self.action = action
  }

  private func theme(for scheme: ColorScheme) -> DesignTheme {
    scheme == .dark ? .defaultDark : .defaultLight
  }

  public var body: some View {
    let theme = theme(for: scheme)
    Button(action: action) {
      Text(title)
        .font(theme.font(.body))
        .foregroundStyle(theme.color(.primaryforeground))
        .padding(.horizontal, padX)
        .padding(.vertical, padY)
        .frame(maxWidth: .infinity)
        .background(background(theme))
        .clipShape(RoundedRectangle(cornerRadius: 16, style: .continuous))
        .shadow(color: theme.color(.ring).opacity(0.12), radius: 8, y: 4)
    }
    .accessibilityLabel(Text(title))
  }

  private var padY: CGFloat { switch size { case .s: 10; case .m: 14; case .l: 18 } }
  private var padX: CGFloat { switch size { case .s: 12; case .m: 16; case .l: 20 } }

  private func background(_ theme: DesignTheme) -> some View {
    switch intent {
      case .primary:    return theme.color(.primary)
      case .secondary:  return theme.color(.secondary)
      case .destructive:return theme.color(.destructive)
    }
  }
}

// MARK: - SwiftUI Preview

#if DEBUG
struct DesignSystem_Previews: PreviewProvider {
  static var previews: some View {
    Group {
      VStack(spacing: 16) {
        Text("Title").font(DesignTheme.defaultLight.font(.title1)).foregroundStyle(DesignTheme.defaultLight.color(.foreground))
        DSButton("Primary"){}
        DSButton("Secondary", intent: .secondary) {}
        DSButton("Delete", intent: .destructive) {}
      }
      .padding()
      .background(DesignTheme.defaultLight.color(.background))

      VStack(spacing: 16) {
        Text("Title").font(DesignTheme.defaultDark.font(.title1)).foregroundStyle(DesignTheme.defaultDark.color(.foreground))
        DSButton("Primary"){}
        DSButton("Secondary", intent: .secondary) {}
        DSButton("Delete", intent: .destructive) {}
      }
      .padding()
      .background(DesignTheme.defaultDark.color(.background))
      .preferredColorScheme(.dark)
    }
  }
}
#endif

//
//  GlassCard.swift
//  A glass-morphism card component with blur effect and border
//
//  Usage:
//  GlassCard {
//      Text("Hello")
//  }
//
//  Or as modifier:
//  VStack { Text("Hello") }
//      .glassCard()
//
//  When to use:
//  - Use .glassCard() for cards on solid/gradient backgrounds
//  - Use .photoOverlayCard() for cards over photo/image backgrounds
//    (PhotoOverlayCard uses Material effects for better contrast on any image)
//

import SwiftUI

/// A glass-morphism card with blur effect and border
/// Uses DesignTheme colors for consistent styling
struct GlassCard<Content: View>: View {
    let content: Content
    var cornerRadius: CGFloat = .radiusLarge
    var padding: CGFloat = .spacing4

    @Environment(\.designTheme) private var theme
    @Environment(\.colorScheme) private var colorScheme

    init(
        cornerRadius: CGFloat = .radiusLarge,
        padding: CGFloat = .spacing4,
        @ViewBuilder content: () -> Content
    ) {
        self.cornerRadius = cornerRadius
        self.padding = padding
        self.content = content()
    }

    var body: some View {
        content
            .padding(padding)
            .background(
                RoundedRectangle(cornerRadius: cornerRadius, style: .continuous)
                    .fill(theme.color(.card))
                    .overlay(
                        RoundedRectangle(cornerRadius: cornerRadius, style: .continuous)
                            .stroke(theme.color(.border).opacity(0.3), lineWidth: 1)
                    )
            )
    }
}

// MARK: - ViewModifier Version

struct GlassCardModifier: ViewModifier {
    var cornerRadius: CGFloat = .radiusLarge
    var padding: CGFloat = .spacing4

    @Environment(\.designTheme) private var theme

    func body(content: Content) -> some View {
        content
            .padding(padding)
            .background(
                RoundedRectangle(cornerRadius: cornerRadius, style: .continuous)
                    .fill(theme.color(.card))
                    .overlay(
                        RoundedRectangle(cornerRadius: cornerRadius, style: .continuous)
                            .stroke(theme.color(.border).opacity(0.3), lineWidth: 1)
                    )
            )
    }
}

extension View {
    /// Applies glass card styling to the view
    /// - Parameters:
    ///   - cornerRadius: Corner radius of the card (default: .radiusLarge)
    ///   - padding: Internal padding (default: .spacing4)
    func glassCard(cornerRadius: CGFloat = .radiusLarge, padding: CGFloat = .spacing4) -> some View {
        modifier(GlassCardModifier(cornerRadius: cornerRadius, padding: padding))
    }
}

// MARK: - Preview

#if DEBUG
struct GlassCard_Previews: PreviewProvider {
    static var previews: some View {
        Group {
            // Light mode
            VStack(spacing: .spacing4) {
                // Container version
                GlassCard {
                    VStack(alignment: .leading, spacing: .spacing3) {
                        Text("Container Version")
                            .font(.headline)
                        Text("This is a glass card using the container style.")
                            .font(.subheadline)
                            .foregroundColor(.secondary)
                    }
                }

                // Modifier version
                VStack(alignment: .leading, spacing: .spacing3) {
                    Text("Modifier Version")
                        .font(.headline)
                    Text("This is a glass card using the modifier style.")
                        .font(.subheadline)
                        .foregroundColor(.secondary)
                }
                .glassCard()

                // Custom radius
                VStack(alignment: .leading, spacing: .spacing3) {
                    Text("Custom Radius")
                        .font(.headline)
                    Text("This card has a small corner radius.")
                        .font(.subheadline)
                        .foregroundColor(.secondary)
                }
                .glassCard(cornerRadius: .radiusSmall)
            }
            .padding()
            .withDesignTheme()
            .background(DesignTheme.defaultLight.color(.background))
            .previewDisplayName("Light Mode")

            // Dark mode
            VStack(spacing: .spacing4) {
                GlassCard {
                    VStack(alignment: .leading, spacing: .spacing3) {
                        Text("Glass Card")
                            .font(.headline)
                        Text("Dark mode glass card example.")
                            .font(.subheadline)
                            .foregroundColor(.secondary)
                    }
                }

                VStack(alignment: .leading, spacing: .spacing3) {
                    Text("Another Card")
                        .font(.headline)
                    Text("With modifier style.")
                        .font(.subheadline)
                        .foregroundColor(.secondary)
                }
                .glassCard()
            }
            .padding()
            .withDesignTheme()
            .background(DesignTheme.defaultDark.color(.background))
            .preferredColorScheme(.dark)
            .previewDisplayName("Dark Mode")
        }
    }
}
#endif

