//
//  BrandBackground.swift
//  Unified background component for consistent app styling
//
//  Usage:
//  VStack {
//      // content
//  }
//  .brandBackground(.auth)
//

import SwiftUI

/// Background style options
enum BackgroundStyle {
    case auth       // Gradient background for auth flows
    case feed       // Dark gradient for feed views
    case adaptive   // Respects system appearance (solid color)
}

/// Unified background component for the app
struct BrandBackground: View {
    let style: BackgroundStyle

    @Environment(\.designTheme) private var theme

    var body: some View {
        Group {
            switch style {
            case .auth:
                LinearGradient.brandGradient
            case .feed:
                LinearGradient.feedBackground
            case .adaptive:
                theme.color(.background)
            }
        }
        .ignoresSafeArea()
    }
}

// MARK: - ViewModifier Version

struct BrandBackgroundModifier: ViewModifier {
    let style: BackgroundStyle

    func body(content: Content) -> some View {
        ZStack {
            BrandBackground(style: style)
            content
        }
    }
}

extension View {
    /// Applies brand background to the view
    /// - Parameter style: Background style to use (default: .adaptive)
    /// - Returns: View with background applied
    func brandBackground(_ style: BackgroundStyle = .adaptive) -> some View {
        modifier(BrandBackgroundModifier(style: style))
    }
}

// MARK: - Preview

#if DEBUG
struct BrandBackground_Previews: PreviewProvider {
    static var previews: some View {
        Group {
            // Auth background
            VStack {
                Text("Auth Background")
                    .font(.largeTitle)
                    .fontWeight(.bold)
                    .foregroundColor(.white)
            }
            .brandBackground(.auth)
            .previewDisplayName("Auth Background")

            // Feed background
            VStack {
                Text("Feed Background")
                    .font(.largeTitle)
                    .fontWeight(.bold)
                    .foregroundColor(.white)
            }
            .brandBackground(.feed)
            .previewDisplayName("Feed Background")

            // Adaptive background (light mode)
            VStack {
                Text("Adaptive Background")
                    .font(.largeTitle)
                    .fontWeight(.bold)
            }
            .withDesignTheme()
            .brandBackground(.adaptive)
            .previewDisplayName("Adaptive - Light")

            // Adaptive background (dark mode)
            VStack {
                Text("Adaptive Background")
                    .font(.largeTitle)
                    .fontWeight(.bold)
            }
            .withDesignTheme()
            .brandBackground(.adaptive)
            .preferredColorScheme(.dark)
            .previewDisplayName("Adaptive - Dark")
        }
    }
}
#endif

