#### **File: DesignSystem/Components/Cards/LiquidGlassCard.swift** (NEW)

Primary Liquid Glass card implementation.

```swift
import SwiftUI

/// Modern Liquid Glass card component for iOS 26+
/// Uses Apple's Liquid Glass effect for depth, vibrancy, and fluid interactions
@available(iOS 26.0, *)
struct LiquidGlassCard<Content: View>: View {
    let content: Content
    var glass: Glass = .regular
    var cornerRadius: CGFloat = .radiusLarge
    var padding: CGFloat = .spacing4
    var isInteractive: Bool = false
    var tintColor: Color? = nil

    init(
        glass: Glass = .regular,
        cornerRadius: CGFloat = .radiusLarge,
        padding: CGFloat = .spacing4,
        isInteractive: Bool = false,
        tintColor: Color? = nil,
        @ViewBuilder content: () -> Content
    ) {
        self.glass = glass
        self.cornerRadius = cornerRadius
        self.padding = padding
        self.isInteractive = isInteractive
        self.tintColor = tintColor
        self.content = content()
    }

    var body: some View {
        content
            .padding(padding)
            .glassEffect(
                configuredGlass,
                in: RoundedRectangle(cornerRadius: cornerRadius, style: .continuous)
            )
    }

    private var configuredGlass: Glass {
        var glass = self.glass

        if isInteractive {
            glass = glass.interactive()
        }

        if let tintColor = tintColor {
            glass = glass.tint(tintColor.opacity(0.8))
        }

        return glass
    }
}

// MARK: - ViewModifier Version

@available(iOS 26.0, *)
struct LiquidGlassCardModifier: ViewModifier {
    var glass: Glass = .regular
    var cornerRadius: CGFloat = .radiusLarge
    var padding: CGFloat = .spacing4
    var isInteractive: Bool = false
    var tintColor: Color? = nil

    func body(content: Content) -> some View {
        content
            .padding(padding)
            .glassEffect(
                configuredGlass,
                in: RoundedRectangle(cornerRadius: cornerRadius, style: .continuous)
            )
    }

    private var configuredGlass: Glass {
        var glass = self.glass

        if isInteractive {
            glass = glass.interactive()
        }

        if let tintColor = tintColor {
            glass = glass.tint(tintColor.opacity(0.8))
        }

        return glass
    }
}

extension View {
    /// Applies Liquid Glass card styling (iOS 26+)
    @available(iOS 26.0, *)
    func liquidGlassCard(
        glass: Glass = .regular,
        cornerRadius: CGFloat = .radiusLarge,
        padding: CGFloat = .spacing4,
        isInteractive: Bool = false,
        tintColor: Color? = nil
    ) -> some View {
        modifier(LiquidGlassCardModifier(
            glass: glass,
            cornerRadius: cornerRadius,
            padding: padding,
            isInteractive: isInteractive,
            tintColor: tintColor
        ))
    }
}
```

