                // Interactive cards
                interactiveExamples

                // Morphing animation
                morphingExample

                // Button styles
                buttonExamples
            }
            .padding()
        }
        .background(Color(.systemGroupedBackground))
    }

    private var basicExamples: some View {
        VStack(spacing: 16) {
            Text("Basic Glass Cards")
                .font(.headline)

            // Regular variant
            VStack(alignment: .leading) {
                Text("Regular Glass")
                    .font(.subheadline)
                Text("Most versatile, adaptive")
                    .font(.caption)
                    .foregroundColor(.secondary)
            }
            .liquidGlassCard(glass: .regular)

            // Clear variant
            VStack(alignment: .leading) {
                Text("Clear Glass")
                    .font(.subheadline)
                Text("Transparent, shows content below")
                    .font(.caption)
                    .foregroundColor(.secondary)
            }
            .liquidGlassCard(glass: .clear)
        }
    }

    private var interactiveExamples: some View {
        VStack(spacing: 16) {
            Text("Interactive Glass")
                .font(.headline)

            VStack(alignment: .leading) {
                Text("Tap me!")
                    .font(.subheadline)
                Text("Interactive with tint")
                    .font(.caption)
                    .foregroundColor(.secondary)
            }
            .liquidGlassCard(isInteractive: true, tintColor: .blue)
            .onTapGesture {
                print("Tapped!")
            }
        }
    }

    private var morphingExample: some View {
        VStack(spacing: 16) {
            Text("Morphing Animation")
                .font(.headline)

            AnimatedGlassContainer(spacing: 40.0) {
                VStack(spacing: 12) {
                    VStack {
                        Text("Header")
                            .font(.headline)
                    }
                    .liquidGlassCard()
                    .glassID("header", in: namespace)
                    .frame(height: showDetails ? 80 : 60)

                    if showDetails {
                        VStack {
                            Text("Details revealed!")
                            Text("Watch the smooth morphing")
                                .font(.caption)
                        }
                        .liquidGlassCard()
                        .glassID("details", in: namespace)
                        .transition(.scale.combined(with: .opacity))
                    }
                }
            }
            .onTapGesture {
                withAnimation(.spring(response: 0.4, dampingFraction: 0.8)) {
                    showDetails.toggle()
                }
            }
        }
    }

    private var buttonExamples: some View {
        VStack(spacing: 16) {
            Text("Glass Buttons")
                .font(.headline)

            BrandButton(title: "Glass Style", style: .glass) {}
            BrandButton(title: "Glass Prominent", style: .glassProminent) {}
        }
    }
}

