import Foundation

struct FeatureFlags {
    /// Enable Liquid Glass effects (iOS 26+)
    ///
    /// In DEBUG builds: Controlled by UserDefaults key "dev.liquidGlass.enabled"
    /// In RELEASE builds: Checks iOS version and remote config/rollout percentage
    static var useLiquidGlass: Bool {
        #if DEBUG
        // Debug: Toggle via UserDefaults for testing
        // Enable with: defaults write com.yourapp.bundle dev.liquidGlass.enabled -bool true
        return UserDefaults.standard.bool(forKey: "dev.liquidGlass.enabled")
        #else
        // Production: Check iOS version and rollout logic
        return isLiquidGlassEnabled()
        #endif
    }

    /// Performance monitoring enabled in debug builds
    static var glassEffectMonitoring: Bool {
        #if DEBUG
        return true
        #else
        return false
        #endif
    }

    // MARK: - Private Helpers

    private static func isLiquidGlassEnabled() -> Bool {
        // Check iOS version first
        guard #available(iOS 26.0, *) else { return false }

        // Add remote config / rollout logic here
        // Examples:
        // - Firebase Remote Config
        // - LaunchDarkly
        // - Custom rollout percentage logic
        // - User bucket assignment

        // For now, enable for all iOS 26+ users
        return true
    }
}
