extension CGFloat {
    /// 4pt - Extra Small spacing
    static let spacing1: CGFloat = 4
    /// 8pt - Small spacing
    static let spacing2: CGFloat = 8
    /// 12pt - Medium spacing
    static let spacing3: CGFloat = 12
    /// 16pt - Large spacing (default)
    static let spacing4: CGFloat = 16
    /// 20pt - Extra Large spacing
    static let spacing5: CGFloat = 20
    /// 24pt - 2X Large spacing
    static let spacing6: CGFloat = 24
    /// 32pt - 3X Large spacing
    static let spacing8: CGFloat = 32
    /// 40pt - 4X Large spacing
    static let spacing10: CGFloat = 40
}

// MARK: - Corner Radius
extension CGFloat {
    /// 8pt - Small corner radius
    static let radiusSmall: CGFloat = 8
    /// 12pt - Medium corner radius
    static let radiusMedium: CGFloat = 12
    /// 16pt - Large corner radius (default)
    static let radiusLarge: CGFloat = 16
    /// 20pt - Extra Large corner radius
    static let radiusXLarge: CGFloat = 20
}

// MARK: - Shadow & Elevation
extension View {
    /// Applies a subtle shadow (elevation 1)
    /// Use for slightly raised elements like buttons or small cards
    func shadowSubtle() -> some View {
        self.shadow(color: Color.black.opacity(0.05), radius: 2, x: 0, y: 1)
    }

    /// Applies a medium shadow (elevation 2)
    /// Use for cards, modals, and raised content
    func shadowMedium() -> some View {
        self.shadow(color: Color.black.opacity(0.08), radius: 8, x: 0, y: 4)
    }

    /// Applies a large shadow (elevation 3)
    /// Use for floating action buttons, popovers, and prominent elements
    func shadowLarge() -> some View {
        self.shadow(color: Color.black.opacity(0.12), radius: 16, x: 0, y: 8)
    }

    /// Applies an extra large shadow (elevation 4)
    /// Use for modals, sheets, and elements that should appear above all content
    func shadowXLarge() -> some View {
        self.shadow(color: Color.black.opacity(0.16), radius: 24, x: 0, y: 12)
    }
}

// MARK: - Gradients for Sports App
extension LinearGradient {
