// MARK: - Environment Key for Theme
struct DesignThemeKey: EnvironmentKey {
    static let defaultValue: DesignTheme = .defaultLight
}

extension EnvironmentValues {
    var designTheme: DesignTheme {
        get { self[DesignThemeKey.self] }
        set { self[DesignThemeKey.self] = newValue }
    }
}

// MARK: - View Extension for Easy Theme Access
extension View {
    /// Injects the appropriate design theme based on current color scheme
    func withDesignTheme() -> some View {
        self.modifier(DesignThemeModifier())
    }
}

private struct DesignThemeModifier: ViewModifier {
    @Environment(\.colorScheme) private var colorScheme

    func body(content: Content) -> some View {
        content
            .environment(\.designTheme, colorScheme == .dark ? .defaultDark : .defaultLight)
    }
}

// MARK: - Semantic Font Extensions
extension Font {
    /// Section title font (title2 with semibold weight)
    static let sectionTitle: Font = .system(.title2, design: .default, weight: .semibold)

    /// Body text font (standard body text)
    static let bodyText: Font = .system(.body, design: .default)
}

// MARK: - iOS 26+ Liquid Glass Helpers

/// Helper extension for applying glass effects with app-specific defaults (iOS 26+)
@available(iOS 26.0, *)
extension View {
    /// Applies Liquid Glass effect with app-specific configuration
    ///
    /// This is a convenience method that wraps SwiftUI's .glassEffect() with
    /// performance monitoring and app-specific defaults.
    ///
    /// - Parameters:
    ///   - glass: Glass variant (default: .regular)
    ///   - cornerRadius: Corner radius for the glass shape (default: .radiusLarge)
    /// - Returns: View with glass effect applied
    ///
    /// ## Example:
    /// ```swift
    /// VStack {
    ///     Text("Hello")
    /// }
    /// .appGlassEffect()
    ///
    /// // With custom corner radius
    /// VStack {
    ///     Text("Hello")
    /// }
    /// .appGlassEffect(glass: .clear, cornerRadius: .radiusSmall)
    /// ```
    func appGlassEffect(
        _ glass: Glass = .regular,
        cornerRadius: CGFloat = .radiusLarge
    ) -> some View {
        self
            .glassEffect(
                glass,
                in: RoundedRectangle(cornerRadius: cornerRadius, style: .continuous)
            )
            .onAppear {
                #if DEBUG
                if FeatureFlags.glassEffectMonitoring {
                    GlassEffectCounter.increment()
                }
                #endif
            }
            .onDisappear {
                #if DEBUG
                if FeatureFlags.glassEffectMonitoring {
                    GlassEffectCounter.decrement()
                }
                #endif
            }
    }
}

// MARK: - Performance Monitoring (Debug Only)

#if DEBUG
/// Performance counter for active glass effects (iOS 26+)
///
/// Tracks the number of active glass effects on screen and warns
/// when exceeding recommended limits (5 effects).
///
/// This counter is automatically updated by LiquidGlassCard and appGlassEffect().
/// Manual increment/decrement is only needed for custom glass effect implementations.
@available(iOS 26.0, *)
struct GlassEffectCounter {
    static var activeCount: Int = 0
    static let maxRecommended: Int = 5

    /// Increments the active glass effect count and logs a warning if limit exceeded
    static func increment() {
        activeCount += 1
        if activeCount > maxRecommended {
            print("⚠️ GLASS EFFECT WARNING: \(activeCount) glass effects active (max recommended: \(maxRecommended))")
            print("   Consider grouping effects in AnimatedGlassContainer or reducing effect count.")
        }
    }

    /// Decrements the active glass effect count
    static func decrement() {
        activeCount = max(0, activeCount - 1)
    }

    /// Resets the counter to zero
    static func reset() {
        activeCount = 0
    }
}
#endif

