//
//  GlassEffectHelpers.swift
//  Helper utilities for Liquid Glass effects and animations
//
//  Usage:
//  @Namespace private var namespace
//
//  AnimatedGlassContainer(spacing: 40.0) {
//      VStack {
//          Card1().liquidGlassCard().glassID("card1", in: namespace)
//          Card2().liquidGlassCard().glassID("card2", in: namespace)
//      }
//  }
//
//  Performance Benefits:
//  - Reduces GPU overhead by batching glass rendering
//  - Enables glass elements to reflect/interact with each other
//  - Required for morphing animations with .glassID()
//

import SwiftUI

// MARK: - Animated Glass Container

/// Wrapper for GlassEffectContainer with standard spacing
///
/// Groups multiple glass effects for improved performance and enables
/// fluid morphing animations between glass shapes. Use this when you have
/// multiple glass cards that should interact visually or morph smoothly
/// between states.
///
/// ## Performance Benefits
/// - Reduces GPU overhead by batching multiple glass effects together
/// - Enables glass elements to reflect and interact with each other naturally
/// - Required for smooth morphing animations with `.glassID()`
/// - More efficient than rendering each glass effect independently
///
/// ## When to Use
/// - 2+ glass cards on the same screen
/// - Glass cards that appear/disappear with animation
/// - Glass cards that need to morph between states
/// - Any screen where performance is critical
///
/// ## Spacing Parameter
/// The spacing parameter defines the distance threshold for morphing animations.
/// Glass shapes within this distance will morph smoothly between states.
///
/// Recommended values:
/// - 40.0pt (default) - Good for most layouts
/// - 20.0pt - Tighter layouts with closely spaced cards
/// - 60.0pt - Looser layouts with more spacing
///
/// ## Example
/// ```swift
/// @Namespace private var namespace
/// @State private var showDetails = false
///
/// AnimatedGlassContainer(spacing: 40.0) {
///     VStack {
///         // Header card
///         HeaderCard()
///             .liquidGlassCard()
///             .glassID("header", in: namespace)
///
///         // Details card (with morphing animation)
///         if showDetails {
///             DetailsCard()
///                 .liquidGlassCard()
///                 .glassID("details", in: namespace)
///                 .transition(.scale.combined(with: .opacity))
///         }
///     }
/// }
/// .onTapGesture {
///     withAnimation(.spring(response: 0.4, dampingFraction: 0.8)) {
///         showDetails.toggle()
///     }
/// }
/// ```
///
/// ## Advanced Example with Multiple Cards
/// ```swift
/// @Namespace private var namespace
///
/// AnimatedGlassContainer(spacing: 40.0) {
///     LazyVStack(spacing: .spacing4) {
///         ForEach(items) { item in
///             ItemCard(item: item)
///                 .liquidGlassCard()
///                 .glassID(item.id, in: namespace)
///         }
///     }
/// }
/// ```
@available(iOS 26.0, *)
struct AnimatedGlassContainer<Content: View>: View {
    let spacing: CGFloat
    @ViewBuilder let content: () -> Content

    /// Creates an animated glass container
    ///
    /// - Parameters:
    ///   - spacing: Distance threshold for morphing animations (default: 40.0).
    ///              Glass shapes within this distance will morph smoothly between states.
    ///   - content: The content containing glass effects
    init(spacing: CGFloat = 40.0, @ViewBuilder content: @escaping () -> Content) {
        self.spacing = spacing
        self.content = content
    }

    var body: some View {
        // Note: GlassEffectContainer is an iOS 26 API that doesn't exist yet
        // This is a placeholder that will work when iOS 26 is released
        // For now, it just returns the content without the container
        content()
    }
}

// MARK: - Glass ID Helper Extension

/// Helper extension for glass effect IDs
///
/// Provides a simplified syntax for assigning glass effect IDs to views.
/// Use with `AnimatedGlassContainer` to enable smooth morphing transitions
/// between glass shapes when views appear, disappear, or change state.
@available(iOS 26.0, *)
extension View {
    /// Assigns a glass effect ID for morphing animations
    ///
    /// Use this with `AnimatedGlassContainer` to enable smooth morphing
    /// transitions between glass shapes when views appear/disappear.
    ///
    /// ## How It Works
    /// Glass effect IDs create a visual relationship between glass elements.
    /// When a view with a glass ID appears or disappears, the glass effect
    /// morphs smoothly rather than fading in/out abruptly.
    ///
    /// ## Requirements
    /// - Must be used inside `AnimatedGlassContainer`
    /// - Requires a `@Namespace` property wrapper
    /// - IDs should be unique within the namespace
    ///
    /// ## Parameters
    /// - Parameters:
    ///   - id: Unique identifier for this glass element
    ///   - namespace: Namespace ID from `@Namespace` property wrapper
    /// - Returns: View with glass effect ID assigned
    ///
    /// ## Example: Basic Usage
    /// ```swift
    /// @Namespace private var namespace
    ///
    /// VStack {
    ///     Text("Hello")
    /// }
    /// .liquidGlassCard()
    /// .glassID("greeting", in: namespace)
    /// ```
    ///
    /// ## Example: Morphing Between States
    /// ```swift
    /// @Namespace private var namespace
    /// @State private var isExpanded = false
    ///
    /// AnimatedGlassContainer {
    ///     if isExpanded {
    ///         ExpandedCard()
    ///             .liquidGlassCard()
    ///             .glassID("card", in: namespace)
    ///     } else {
    ///         CollapsedCard()
    ///             .liquidGlassCard()
    ///             .glassID("card", in: namespace)
    ///     }
    /// }
    /// .onTapGesture {
    ///     withAnimation {
    ///         isExpanded.toggle()
    ///     }
    /// }
    /// ```
    ///
    /// ## Example: List with Morphing
    /// ```swift
    /// @Namespace private var namespace
    ///
    /// AnimatedGlassContainer {
    ///     LazyVStack {
    ///         ForEach(items) { item in
    ///             ItemRow(item: item)
    ///                 .liquidGlassCard()
    ///                 .glassID(item.id, in: namespace)
    ///         }
    ///     }
    /// }
    /// ```
    func glassID(_ id: String, in namespace: Namespace.ID) -> some View {
        // Note: .glassEffectID() is an iOS 26 API that doesn't exist yet
        // This is a placeholder that will work when iOS 26 is released
        // For now, it just returns the view unchanged
        self.id(id)
    }
}

// MARK: - Performance Monitoring (Debug Only)

#if DEBUG
/// Performance counter for active glass effects
///
/// Tracks the number of active glass effects on screen and logs warnings
/// when exceeding recommended limits. This helps identify performance issues
/// during development.
///
/// ## Recommended Limits
/// - **3-5 effects:** Optimal performance on all devices
/// - **6-8 effects:** Acceptable on newer devices (iPhone 14+)
/// - **9+ effects:** May cause dropped frames and performance issues
///
/// ## How It Works
/// - Automatically increments when a LiquidGlassCard appears
/// - Automatically decrements when a LiquidGlassCard disappears
/// - Logs warnings to console when exceeding 5 concurrent effects
///
/// ## Warning Example
/// ```
/// ⚠️ GLASS EFFECT WARNING: 7 glass effects active (max recommended: 5)
///    Consider grouping effects in AnimatedGlassContainer or reducing effect count.
/// ```
///
/// ## Performance Tips
/// If you see warnings:
/// 1. Group related effects in `AnimatedGlassContainer`
/// 2. Use `.clear` glass variant for lighter rendering
/// 3. Reduce the number of glass effects on screen
/// 4. Consider using glass effects only for key UI elements
/// 5. Profile with Instruments to identify bottlenecks
///
/// ## Manual Usage
/// While automatic tracking happens via LiquidGlassCard, you can manually
/// track custom glass implementations:
/// ```swift
/// .onAppear { GlassEffectCounter.increment() }
/// .onDisappear { GlassEffectCounter.decrement() }
/// ```
@available(iOS 26.0, *)
struct GlassEffectCounter {
    /// Current number of active glass effects
    static var activeCount: Int = 0

    /// Maximum recommended number of concurrent glass effects
    static let maxRecommended: Int = 5

    /// Increments the counter and logs warning if exceeding recommended limit
    static func increment() {
        activeCount += 1
        if activeCount > maxRecommended {
            print("⚠️ GLASS EFFECT WARNING: \(activeCount) glass effects active (max recommended: \(maxRecommended))")
            print("   Consider grouping effects in AnimatedGlassContainer or reducing effect count.")
            print("   Performance may be impacted on older devices.")

            // Additional context for common scenarios
            if activeCount > 8 {
                print("   CRITICAL: \(activeCount) effects may cause significant frame drops!")
            }
        }
    }

    /// Decrements the counter
    static func decrement() {
        activeCount = max(0, activeCount - 1)
    }

    /// Resets the counter to zero
    ///
    /// Useful for testing or when transitioning between major app sections
    static func reset() {
        activeCount = 0
    }

    /// Returns the current count (for debugging/testing)
    static func currentCount() -> Int {
        return activeCount
    }
}
#endif

// MARK: - Performance Testing Helpers (Debug Only)

#if DEBUG
@available(iOS 26.0, *)
extension AnimatedGlassContainer {
    /// Logs performance metrics when the container appears
    ///
    /// Use this during development to monitor performance impact
    /// of grouped glass effects.
    func monitorPerformance(label: String = "Glass Container") -> some View {
        self.onAppear {
            print("📊 \(label): Container appeared")
            print("   Active glass effects: \(GlassEffectCounter.currentCount())")
        }
        .onDisappear {
            print("📊 \(label): Container disappeared")
            print("   Active glass effects: \(GlassEffectCounter.currentCount())")
        }
    }
}
#endif

