//
//  DesignTokens+Extensions.swift
//  Extensions for DesignSystem providing spacing, radius, gradients, and environment access
//
//  Usage:
//  .padding(.spacing4)
//  .cornerRadius(.radiusLarge)
//  LinearGradient.athleteGradient
//  @Environment(\.designTheme) var theme
//

import SwiftUI

// MARK: - Spacing Extensions (4pt grid)
extension CGFloat {
    /// 4pt - Extra Small spacing
    static let spacing1: CGFloat = 4
    /// 8pt - Small spacing
    static let spacing2: CGFloat = 8
    /// 12pt - Medium spacing
    static let spacing3: CGFloat = 12
    /// 16pt - Large spacing (default)
    static let spacing4: CGFloat = 16
    /// 20pt - Extra Large spacing
    static let spacing5: CGFloat = 20
    /// 24pt - 2X Large spacing
    static let spacing6: CGFloat = 24
    /// 32pt - 3X Large spacing
    static let spacing8: CGFloat = 32
    /// 40pt - 4X Large spacing
    static let spacing10: CGFloat = 40
}

// MARK: - Corner Radius
extension CGFloat {
    /// 8pt - Small corner radius
    static let radiusSmall: CGFloat = 8
    /// 12pt - Medium corner radius
    static let radiusMedium: CGFloat = 12
    /// 16pt - Large corner radius (default)
    static let radiusLarge: CGFloat = 16
    /// 20pt - Extra Large corner radius
    static let radiusXLarge: CGFloat = 20
}

// MARK: - Shadow & Elevation
extension View {
    /// Applies a subtle shadow (elevation 1)
    /// Use for slightly raised elements like buttons or small cards
    func shadowSubtle() -> some View {
        self.shadow(color: Color.black.opacity(0.05), radius: 2, x: 0, y: 1)
    }

    /// Applies a medium shadow (elevation 2)
    /// Use for cards, modals, and raised content
    func shadowMedium() -> some View {
        self.shadow(color: Color.black.opacity(0.08), radius: 8, x: 0, y: 4)
    }

    /// Applies a large shadow (elevation 3)
    /// Use for floating action buttons, popovers, and prominent elements
    func shadowLarge() -> some View {
        self.shadow(color: Color.black.opacity(0.12), radius: 16, x: 0, y: 8)
    }

    /// Applies an extra large shadow (elevation 4)
    /// Use for modals, sheets, and elements that should appear above all content
    func shadowXLarge() -> some View {
        self.shadow(color: Color.black.opacity(0.16), radius: 24, x: 0, y: 12)
    }
}

// MARK: - Gradients for Sports App
extension LinearGradient {
    /// Athlete gradient (indigo/purple)
    static let athleteGradient = LinearGradient(
        colors: [Color(hex: "#6366F1"), Color(hex: "#818CF8")],
        startPoint: .topLeading,
        endPoint: .bottomTrailing
    )

    /// Coach gradient (red/pink)
    static let coachGradient = LinearGradient(
        colors: [Color(hex: "#EF4444"), Color(hex: "#F87171")],
        startPoint: .topLeading,
        endPoint: .bottomTrailing
    )

    /// Default brand gradient (uses athlete gradient)
    static let brandGradient = athleteGradient

    /// Feed background gradient (dark variant)
    static let feedBackground = LinearGradient(
        colors: [
            Color(hex: "#1E293B"),
            Color(hex: "#0F172A")
        ],
        startPoint: .top,
        endPoint: .bottom
    )
}

// MARK: - Environment Key for Theme
struct DesignThemeKey: EnvironmentKey {
    static let defaultValue: DesignTheme = .defaultLight
}

extension EnvironmentValues {
    var designTheme: DesignTheme {
        get { self[DesignThemeKey.self] }
        set { self[DesignThemeKey.self] = newValue }
    }
}

// MARK: - View Extension for Easy Theme Access
extension View {
    /// Injects the appropriate design theme based on current color scheme
    func withDesignTheme() -> some View {
        self.modifier(DesignThemeModifier())
    }
}

private struct DesignThemeModifier: ViewModifier {
    @Environment(\.colorScheme) private var colorScheme

    func body(content: Content) -> some View {
        content
            .environment(\.designTheme, colorScheme == .dark ? .defaultDark : .defaultLight)
    }
}

// MARK: - Semantic Font Extensions
extension Font {
    /// Section title font (title2 with semibold weight)
    static let sectionTitle: Font = .system(.title2, design: .default, weight: .semibold)

    /// Body text font (standard body text)
    static let bodyText: Font = .system(.body, design: .default)
}

// MARK: - iOS 26+ Liquid Glass Helpers

/// Helper extension for applying glass effects with app-specific defaults (iOS 26+)
@available(iOS 26.0, *)
extension View {
    /// Applies Liquid Glass effect with app-specific configuration
    ///
    /// This is a convenience method that wraps SwiftUI's .glassEffect() with
    /// performance monitoring and app-specific defaults.
    ///
    /// - Parameters:
    ///   - glass: Glass variant (default: .regular)
    ///   - cornerRadius: Corner radius for the glass shape (default: .radiusLarge)
    /// - Returns: View with glass effect applied
    ///
    /// ## Example:
    /// ```swift
    /// VStack {
    ///     Text("Hello")
    /// }
    /// .appGlassEffect()
    ///
    /// // With custom corner radius
    /// VStack {
    ///     Text("Hello")
    /// }
    /// .appGlassEffect(glass: .clear, cornerRadius: .radiusSmall)
    /// ```
    func appGlassEffect(
        cornerRadius: CGFloat = .radiusLarge
    ) -> some View {
        self
            // Note: .glassEffect() is an iOS 26 API that doesn't exist yet
            // This placeholder uses existing Material effects as a fallback
            .background(
                RoundedRectangle(cornerRadius: cornerRadius, style: .continuous)
                    .fill(.ultraThinMaterial)
            )
            .onAppear {
                #if DEBUG
                if FeatureFlags.glassEffectMonitoring {
                    GlassEffectCounter.increment()
                }
                #endif
            }
            .onDisappear {
                #if DEBUG
                if FeatureFlags.glassEffectMonitoring {
                    GlassEffectCounter.decrement()
                }
                #endif
            }
    }
}

// MARK: - Performance Monitoring (Debug Only)

#if DEBUG
/// Performance counter for active glass effects (iOS 26+)
///
/// Tracks the number of active glass effects on screen and warns
/// when exceeding recommended limits (5 effects).
///
/// This counter is automatically updated by LiquidGlassCard and appGlassEffect().
/// Manual increment/decrement is only needed for custom glass effect implementations.
@available(iOS 26.0, *)
struct GlassEffectCounter {
    static var activeCount: Int = 0
    static let maxRecommended: Int = 5

    /// Increments the active glass effect count and logs a warning if limit exceeded
    static func increment() {
        activeCount += 1
        if activeCount > maxRecommended {
            print("⚠️ GLASS EFFECT WARNING: \(activeCount) glass effects active (max recommended: \(maxRecommended))")
            print("   Consider grouping effects in AnimatedGlassContainer or reducing effect count.")
        }
    }

    /// Decrements the active glass effect count
    static func decrement() {
        activeCount = max(0, activeCount - 1)
    }

    /// Resets the counter to zero
    static func reset() {
        activeCount = 0
    }
}
#endif

//
//  FeatureFlags.swift
//  Feature flag system for gradual rollout of new features
//
//  Usage:
//  if FeatureFlags.useLiquidGlass { ... }
//
//  Debug Configuration:
//  Enable Liquid Glass in debug builds with:
//  defaults write com.athleadai.app dev.liquidGlass.enabled -bool true
//

import Foundation

/// Feature flags for managing gradual rollout and A/B testing
///
/// This system provides controlled feature rollout with different behavior
/// in DEBUG and RELEASE builds:
///
/// - DEBUG: Features controlled via UserDefaults for easy testing
/// - RELEASE: Features controlled via iOS version checks and remote config
struct FeatureFlags {

    // MARK: - Liquid Glass Feature

    /// Enable Liquid Glass effects (iOS 26+)
    ///
    /// Controls whether the app uses modern iOS 26 Liquid Glass effects or
    /// falls back to legacy solid-fill glass cards.
    ///
    /// ## Debug Builds
    /// Controlled by UserDefaults key "dev.liquidGlass.enabled"
    ///
    /// Enable with terminal command:
    /// ```bash
    /// defaults write com.athleadai.app dev.liquidGlass.enabled -bool true
    /// ```
    ///
    /// Disable with terminal command:
    /// ```bash
    /// defaults write com.athleadai.app dev.liquidGlass.enabled -bool false
    /// ```
    ///
    /// ## Release Builds
    /// Automatically enabled for iOS 26+ devices. Can be extended with
    /// remote config for gradual rollout (Firebase, LaunchDarkly, etc.)
    static var useLiquidGlass: Bool {
        #if DEBUG
        // Debug: Toggle via UserDefaults for testing
        return UserDefaults.standard.bool(forKey: "dev.liquidGlass.enabled")
        #else
        // Production: Check iOS version and rollout logic
        return isLiquidGlassEnabled()
        #endif
    }

    // MARK: - Performance Monitoring

    /// Performance monitoring enabled in debug builds
    ///
    /// When enabled, tracks the number of active glass effects and logs
    /// warnings when exceeding recommended limits (5 concurrent effects).
    ///
    /// Always enabled in DEBUG builds for development visibility.
    /// Always disabled in RELEASE builds for performance.
    static var glassEffectMonitoring: Bool {
        #if DEBUG
        return true
        #else
        return false
        #endif
    }

    // MARK: - Private Helpers

    /// Determines if Liquid Glass should be enabled in production
    ///
    /// Current implementation:
    /// - Requires iOS 26.0+
    /// - Enabled for all iOS 26+ users
    ///
    /// Future enhancements:
    /// - Remote config integration (Firebase Remote Config, LaunchDarkly)
    /// - Rollout percentage logic (e.g., 10% of users, then 50%, then 100%)
    /// - User bucket assignment for A/B testing
    /// - Device capability checks (RAM, GPU)
    private static func isLiquidGlassEnabled() -> Bool {
        // Check iOS version first
        guard #available(iOS 26.0, *) else { return false }

        // Add remote config / rollout logic here
        // Examples:
        // - Firebase Remote Config:
        //   return RemoteConfig.remoteConfig().configValue(forKey: "liquid_glass_enabled").boolValue
        //
        // - LaunchDarkly:
        //   return LDClient.shared.boolVariation(forKey: "liquid-glass-rollout", defaultValue: false)
        //
        // - Rollout percentage:
        //   let userId = AuthManager.shared.currentUser?.id ?? UUID().uuidString
        //   let hash = abs(userId.hashValue)
        //   let bucket = hash % 100
        //   return bucket < 50  // 50% rollout

        // For now, enable for all iOS 26+ users
        return true
    }
}

