                .onTapGesture {
                    print("Green tinted tapped!")
                }

                sectionHeader("Clear Glass Interactive", subtitle: "Transparent with touch feedback")

                VStack(alignment: .leading, spacing: 8) {
                    Text("Clear Interactive Glass")
                        .font(.headline)
                    Text("Combines transparency with interactivity")
                        .font(.caption)
                        .foregroundColor(.secondary)
