//
//  FeatureFlags.swift
//  Feature flag system for gradual rollout of new features
//
//  Usage:
//  if FeatureFlags.useLiquidGlass { ... }
//
//  Debug Configuration:
//  Enable Liquid Glass in debug builds with:
//  defaults write com.athleadai.app dev.liquidGlass.enabled -bool true
//

import Foundation

/// Feature flags for managing gradual rollout and A/B testing
///
/// This system provides controlled feature rollout with different behavior
/// in DEBUG and RELEASE builds:
///
/// - DEBUG: Features controlled via UserDefaults for easy testing
/// - RELEASE: Features controlled via iOS version checks and remote config
struct FeatureFlags {

    // MARK: - Liquid Glass Feature

    /// Enable Liquid Glass effects (iOS 26+)
    ///
    /// Controls whether the app uses modern iOS 26 Liquid Glass effects or
    /// falls back to legacy solid-fill glass cards.
    ///
    /// ## Debug Builds
    /// Controlled by UserDefaults key "dev.liquidGlass.enabled"
    ///
    /// Enable with terminal command:
    /// ```bash
    /// defaults write com.athleadai.app dev.liquidGlass.enabled -bool true
    /// ```
    ///
    /// Disable with terminal command:
    /// ```bash
    /// defaults write com.athleadai.app dev.liquidGlass.enabled -bool false
    /// ```
    ///
    /// ## Release Builds
    /// Automatically enabled for iOS 26+ devices. Can be extended with
    /// remote config for gradual rollout (Firebase, LaunchDarkly, etc.)
    static var useLiquidGlass: Bool {
        #if DEBUG
        // Debug: Toggle via UserDefaults for testing
        return UserDefaults.standard.bool(forKey: "dev.liquidGlass.enabled")
        #else
        // Production: Check iOS version and rollout logic
        return isLiquidGlassEnabled()
        #endif
    }

    // MARK: - Performance Monitoring

    /// Performance monitoring enabled in debug builds
    ///
    /// When enabled, tracks the number of active glass effects and logs
    /// warnings when exceeding recommended limits (5 concurrent effects).
    ///
    /// Always enabled in DEBUG builds for development visibility.
    /// Always disabled in RELEASE builds for performance.
    static var glassEffectMonitoring: Bool {
        #if DEBUG
        return true
        #else
        return false
        #endif
    }

    // MARK: - Private Helpers

    /// Determines if Liquid Glass should be enabled in production
    ///
    /// Current implementation:
    /// - Requires iOS 18.0+
    /// - Enabled for all iOS 18+ users
    ///
    /// Future enhancements:
    /// - Remote config integration (Firebase Remote Config, LaunchDarkly)
    /// - Rollout percentage logic (e.g., 10% of users, then 50%, then 100%)
    /// - User bucket assignment for A/B testing
    /// - Device capability checks (RAM, GPU)
    private static func isLiquidGlassEnabled() -> Bool {
        // Check iOS version first
        guard #available(iOS 18.0, *) else { return false }

        // Add remote config / rollout logic here
        // Examples:
        // - Firebase Remote Config:
        //   return RemoteConfig.remoteConfig().configValue(forKey: "liquid_glass_enabled").boolValue
        //
        // - LaunchDarkly:
        //   return LDClient.shared.boolVariation(forKey: "liquid-glass-rollout", defaultValue: false)
        //
        // - Rollout percentage:
        //   let userId = AuthManager.shared.currentUser?.id ?? UUID().uuidString
        //   let hash = abs(userId.hashValue)
        //   let bucket = hash % 100
        //   return bucket < 50  // 50% rollout

        // For now, enable for all iOS 18+ users
        return true
    }
}

