//
//  GlassCard.swift
//  A glass-morphism card component with blur effect and border
//
//  Usage:
//  GlassCard {
//      Text("Hello")
//  }
//
//  Or as modifier:
//  VStack { Text("Hello") }
//      .glassCard()
//

import SwiftUI

/// A glass-morphism card with blur effect and border
/// Uses DesignTheme colors for consistent styling
struct GlassCard<Content: View>: View {
    let content: Content
    var cornerRadius: CGFloat = .radiusLarge
    var padding: CGFloat = .spacing4

    @Environment(\.designTheme) private var theme
    @Environment(\.colorScheme) private var colorScheme

    init(
        cornerRadius: CGFloat = .radiusLarge,
        padding: CGFloat = .spacing4,
        @ViewBuilder content: () -> Content
    ) {
        self.cornerRadius = cornerRadius
        self.padding = padding
        self.content = content()
    }

    var body: some View {
        content
            .padding(padding)
            .background(
                RoundedRectangle(cornerRadius: cornerRadius, style: .continuous)
                    .fill(theme.color(.card))
                    .overlay(
                        RoundedRectangle(cornerRadius: cornerRadius, style: .continuous)
                            .stroke(theme.color(.border).opacity(0.3), lineWidth: 1)
                    )
            )
    }
}

// MARK: - ViewModifier Version

struct GlassCardModifier: ViewModifier {
    var cornerRadius: CGFloat = .radiusLarge
    var padding: CGFloat = .spacing4

    @Environment(\.designTheme) private var theme

    func body(content: Content) -> some View {
        content
            .padding(padding)
            .background(
                RoundedRectangle(cornerRadius: cornerRadius, style: .continuous)
                    .fill(theme.color(.card))
                    .overlay(
                        RoundedRectangle(cornerRadius: cornerRadius, style: .continuous)
                            .stroke(theme.color(.border).opacity(0.3), lineWidth: 1)
                    )
            )
    }
}

extension View {
    /// Applies glass card styling to the view
    /// - Parameters:
    ///   - cornerRadius: Corner radius of the card (default: .radiusLarge)
    ///   - padding: Internal padding (default: .spacing4)
    func glassCard(cornerRadius: CGFloat = .radiusLarge, padding: CGFloat = .spacing4) -> some View {
        modifier(GlassCardModifier(cornerRadius: cornerRadius, padding: padding))
    }
}

// MARK: - Preview

#if DEBUG
struct GlassCard_Previews: PreviewProvider {
    static var previews: some View {
        Group {
            // Light mode
            VStack(spacing: .spacing4) {
                // Container version
                GlassCard {
                    VStack(alignment: .leading, spacing: .spacing3) {
                        Text("Container Version")
                            .font(.headline)
                        Text("This is a glass card using the container style.")
                            .font(.subheadline)
                            .foregroundColor(.secondary)
                    }
                }

                // Modifier version
                VStack(alignment: .leading, spacing: .spacing3) {
                    Text("Modifier Version")
                        .font(.headline)
                    Text("This is a glass card using the modifier style.")
                        .font(.subheadline)
                        .foregroundColor(.secondary)
                }
                .glassCard()

                // Custom radius
                VStack(alignment: .leading, spacing: .spacing3) {
                    Text("Custom Radius")
                        .font(.headline)
                    Text("This card has a small corner radius.")
                        .font(.subheadline)
                        .foregroundColor(.secondary)
                }
                .glassCard(cornerRadius: .radiusSmall)
            }
            .padding()
            .withDesignTheme()
            .background(DesignTheme.defaultLight.color(.background))
            .previewDisplayName("Light Mode")

            // Dark mode
            VStack(spacing: .spacing4) {
                GlassCard {
                    VStack(alignment: .leading, spacing: .spacing3) {
                        Text("Glass Card")
                            .font(.headline)
                        Text("Dark mode glass card example.")
                            .font(.subheadline)
                            .foregroundColor(.secondary)
                    }
                }

                VStack(alignment: .leading, spacing: .spacing3) {
                    Text("Another Card")
                        .font(.headline)
                    Text("With modifier style.")
                        .font(.subheadline)
                        .foregroundColor(.secondary)
                }
                .glassCard()
            }
            .padding()
            .withDesignTheme()
            .background(DesignTheme.defaultDark.color(.background))
            .preferredColorScheme(.dark)
            .previewDisplayName("Dark Mode")
        }
    }
}
#endif

//
//  FeedPost.swift
//  AthleadAI
//
//  Shared component for displaying social media posts in feeds
//  Used by both athlete and coach feed views
//

import SwiftUI

struct FeedPost: View {
    let authorName: String
    let authorHandle: String
    let timeAgo: String
    let squad: String  // Tag to display (e.g., "Carroll Volleyball", "Class of 2026")
    let content: String
    let likes: Int
    let comments: Int
    var mediaType: MediaType? = nil

    @State private var isLiked = false

    enum MediaType {
        case image
        case video
    }

    var body: some View {
        VStack(alignment: .leading, spacing: .spacing3) {
            // MARK: - Header
            headerView

            // MARK: - Squad Tag
            tagView

            // MARK: - Content
            contentView

            // MARK: - Media (Image or Video)
            if let mediaType = mediaType {
                mediaView(type: mediaType)
            }

            // MARK: - Action Buttons
            actionButtons
        }
        .padding(.spacing4)
        .glassCard(cornerRadius: .radiusLarge)
    }

    // MARK: - Header View
    private var headerView: some View {
        HStack(spacing: .spacing3) {
            // Profile image
            profileImage

            VStack(alignment: .leading, spacing: 2) {
                Text(authorName)
                    .font(.headline)
                    .foregroundColor(.white)

                HStack(spacing: 4) {
                    Text(authorHandle)
                        .font(.caption)
                        .foregroundColor(.gray)
                    Text("")
                        .font(.caption)
                        .foregroundColor(.gray)
                    Text(timeAgo)
                        .font(.caption)
                        .foregroundColor(.gray)
                }
            }

            Spacer()

            Button(action: {}) {
                Image(systemName: "ellipsis")
                    .foregroundColor(.gray)
            }
            .accessibilityLabel("Post options")
        }
    }

    // MARK: - Profile Image
    private var profileImage: some View {
        Group {
            if authorHandle == "@kinsleyyoung" {
                Image("B302C166-4D7D-4BAD-BBBA-39D27A56987D")
                    .resizable()
                    .scaledToFill()
                    .frame(width: 44, height: 44)
                    .clipShape(Circle())
            } else if authorHandle == "@lc_clayton51" {
                Image("lc")
                    .resizable()
                    .scaledToFill()
                    .frame(width: 44, height: 44)
                    .clipShape(Circle())
            } else {
                Image(systemName: "person.crop.circle.fill")
                    .resizable()
                    .frame(width: 44, height: 44)
                    .foregroundColor(.gray)
            }
        }
        .accessibilityLabel("\(authorName)'s profile picture")
    }

    // MARK: - Squad Tag
    private var tagView: some View {
        HStack(spacing: 6) {
            Image(systemName: "person.3.fill")
                .font(.caption2)
            Text(squad)
                .font(.caption)
        }
        .foregroundColor(.blue)
        .padding(.horizontal, 10)
        .padding(.vertical, 4)
        .background(
            Capsule()
                .fill(Color.blue.opacity(0.2))
        )
        .accessibilityLabel("Squad: \(squad)")
    }

    // MARK: - Content View
    private var contentView: some View {
        Text(content)
            .font(.body)
            .foregroundColor(.white)
            .lineSpacing(4)
    }

    // MARK: - Media View
    private func mediaView(type: MediaType) -> some View {
        Group {
            if type == .image {
                imageMediaView
            } else if type == .video {
                videoMediaView
            }
        }
    }

    private var imageMediaView: some View {
        Group {
            // Check if this is Kaylee's post and show kales image
            if authorHandle == "@kales_usc" {
                Image("kales")
                    .resizable()
                    .scaledToFill()
                    .frame(height: 200)
                    .clipped()
                    .cornerRadius(.radiusMedium)
            } else if authorHandle == "@lc_clayton51" {
                Image("lc")
                    .resizable()
                    .scaledToFill()
                    .frame(height: 200)
                    .clipped()
                    .cornerRadius(.radiusMedium)
            } else if authorHandle == "@kinsleyyoung" && squad == "Carroll Volleyball" && content.contains("Team photo") {
                Image("dragons")
                    .resizable()
                    .scaledToFill()
                    .frame(height: 200)
                    .clipped()
                    .cornerRadius(.radiusMedium)
            } else {
                RoundedRectangle(cornerRadius: .radiusMedium)
                    .fill(Color.gray.opacity(0.3))
                    .frame(height: 200)
                    .overlay(
                        Image(systemName: "photo.fill")
                            .font(.system(size: 60))
                            .foregroundColor(.white.opacity(0.5))
                    )
            }
        }
        .accessibilityLabel("\(authorName)'s post image")
    }

    private var videoMediaView: some View {
        ZStack {
            // Check if this is Kaylee's post and show kales image as video thumbnail
            if authorHandle == "@kales_usc" {
                Image("kales")
                    .resizable()
                    .scaledToFill()
                    .frame(height: 200)
                    .clipped()
                    .cornerRadius(.radiusMedium)
            } else if authorHandle == "@kinsleyyoung" && content.contains("block timing") {
                Image("block")
                    .resizable()
                    .scaledToFill()
                    .frame(height: 200)
                    .clipped()
                    .cornerRadius(.radiusMedium)
            } else {
                RoundedRectangle(cornerRadius: .radiusMedium)
                    .fill(Color.gray.opacity(0.3))
                    .frame(height: 200)
                    .overlay(
                        Image(systemName: "video.fill")
                            .font(.system(size: 40))
                            .foregroundColor(.white.opacity(0.3))
                    )
            }

            // Play button overlay
            Circle()
                .fill(Color.white.opacity(0.9))
                .frame(width: 60, height: 60)

            Image(systemName: "play.fill")
                .font(.system(size: 24))
                .foregroundColor(.black)
        }
        .accessibilityLabel("\(authorName)'s post video")
        .accessibilityHint("Double tap to play video")
    }

    // MARK: - Action Buttons
    private var actionButtons: some View {
        HStack(spacing: 24) {
            // Like button
            Button(action: {
                isLiked.toggle()
            }) {
                HStack(spacing: 6) {
                    Image(systemName: isLiked ? "heart.fill" : "heart")
                        .foregroundColor(isLiked ? .red : .gray)
                    Text("\(isLiked ? likes + 1 : likes)")
                        .font(.subheadline)
                        .foregroundColor(.gray)
                }
            }
            .accessibilityLabel(isLiked ? "Unlike post" : "Like post")
            .accessibilityValue("\(isLiked ? likes + 1 : likes) likes")

            // Comment button
            Button(action: {}) {
                HStack(spacing: 6) {
                    Image(systemName: "bubble.right")
                        .foregroundColor(.gray)
                    Text("\(comments)")
                        .font(.subheadline)
                        .foregroundColor(.gray)
                }
            }
            .accessibilityLabel("Comment on post")
            .accessibilityValue("\(comments) comments")

            // Share button
            Button(action: {}) {
                Image(systemName: "arrow.turn.up.right")
                    .foregroundColor(.gray)
            }
            .accessibilityLabel("Share post")

            Spacer()

            // Bookmark button
            Button(action: {}) {
                Image(systemName: "bookmark")
                    .foregroundColor(.gray)
            }
            .accessibilityLabel("Bookmark post")
        }
    }
}

