struct ProfileView: View {
    @State private var currentUser = User.currentUser
    @State private var showingGoLive = false
    @State private var showingPrivacySettings = false

    var body: some View {
        NavigationView {
            ZStack {
                // Background - Athlete photo
                GeometryReader { geometry in
                    Image(currentUser.profileImageName)
                        .resizable()
                        .aspectRatio(contentMode: .fill)
                        .frame(width: geometry.size.width, height: geometry.size.height)
                        .clipped()
                        .ignoresSafeArea()
                        .accessibilityHidden(true)
                        .overlay(
                            LinearGradient(
                                gradient: Gradient(colors: [
                                    Color.black.opacity(0.3),
                                    Color.black.opacity(0.7)
                                ]),
                                startPoint: .top,
                                endPoint: .bottom
                            )
                            .ignoresSafeArea()
                        )
                }

                ScrollView {
                    VStack(spacing: 0) {
                        Spacer()
                            .frame(height: 500)

                        // Slim Profile Info Card
                        VStack(spacing: 4) {
                            Text(currentUser.fullName)
                                .font(.title2)
                                .fontWeight(.bold)
                                .foregroundColor(.white)

                            Text("\(currentUser.sport)  \(currentUser.position)  \(currentUser.classYear)")
                                .font(.subheadline)
                                .foregroundColor(.white.opacity(0.8))
                        }
                        .frame(maxWidth: .infinity)
                        .glassCard(cornerRadius: .radiusLarge, padding: .spacing4)
                        .padding(.horizontal)
                        .padding(.top, 16)

                        // Menu Items
                        VStack(spacing: 12) {
                            MenuItem(icon: "person.fill", title: "About", iconColor: .blue, destination: AboutView())
                            MenuItem(icon: "video.fill", title: "Film", iconColor: .red, destination: FilmView())
                            MenuItem(icon: "pencil", title: "Posts", iconColor: .green, destination: PostsView())
                            MenuItem(icon: "figure.run", title: "Athletics", iconColor: .orange, destination: AthleticsView())
                            MenuItem(icon: "book.fill", title: "Academics", iconColor: .purple, destination: AcademicsView())
                        }
                        .padding(.horizontal)
                        .padding(.top, 16)
                        .padding(.bottom, 100)
                    }
                }
            }
            .navigationBarHidden(true)
            .sheet(isPresented: $showingGoLive) {
                GoLiveView()
            }
            .overlay(
                HStack(spacing: 16) {
