                }
                .padding(20)
                .background(
                    RoundedRectangle(cornerRadius: 16)
                        .fill(.ultraThinMaterial)
                        .overlay(
                            RoundedRectangle(cornerRadius: 16)
                                .stroke(Color.white.opacity(0.2), lineWidth: 1)
                        )
                )
                .padding(.horizontal)

                // Personal Information Card (Privacy-Aware)
                if hasVisiblePersonalInfo {
                    VStack(alignment: .leading, spacing: 20) {
                        Text("Personal Information")
                            .font(.title3)
                            .fontWeight(.bold)
                            .foregroundColor(.white)

                        if user.privacySettings.showFullName {
                            InfoRow(icon: "person.fill", iconColor: .blue, label: "Full Name", value: user.fullName)
