                        if user.privacySettings.showAddress {
                            InfoRow(icon: "mappin.circle.fill", iconColor: .blue, label: "Address", value: user.address)
                        }

                        if user.privacySettings.showPhone {
                            InfoRow(icon: "phone.fill", iconColor: .blue, label: "Phone", value: user.phone)
                        }

                        if user.privacySettings.showEmail {
                            InfoRow(icon: "envelope.fill", iconColor: .blue, label: "Email", value: user.email)
                        }

                        if user.privacySettings.showBirthday {
                            InfoRow(icon: "calendar", iconColor: .blue, label: "Birthday", value: user.birthday)
                        }

                        if !hasVisiblePersonalInfo {
                            Text("This information is private")
                                .font(.subheadline)
                                .foregroundColor(.gray)
                                .italic()
                        }
                    }
                    .padding(20)
                    .background(
                        RoundedRectangle(cornerRadius: 16)
                            .fill(Color.black.opacity(0.4))
                            .overlay(
                                RoundedRectangle(cornerRadius: 16)
                                    .stroke(Color.white.opacity(0.3), lineWidth: 1)
                            )
                    )
                    .padding(.horizontal)
                }

                // Social Media Card (Privacy-Aware)
                if user.privacySettings.showSocialMedia {
                    VStack(alignment: .leading, spacing: 20) {
                        Text("Social Media")
                            .font(.title3)
                            .fontWeight(.bold)
                            .foregroundColor(.white)

                        if !user.instagram.isEmpty {
                            SocialRow(icon: "camera.fill", iconColor: .pink, handle: user.instagram, platform: "Instagram")
                        }
                        if !user.twitter.isEmpty {
                            SocialRow(icon: "play.rectangle.fill", iconColor: .black, handle: user.twitter, platform: "TikTok")
                        }
                        if !user.youtube.isEmpty {
                            SocialRow(icon: "play.fill", iconColor: .red, handle: user.youtube, platform: "YouTube")
                        }
                    }
                    .padding(20)
                    .background(
                        RoundedRectangle(cornerRadius: 16)
                            .fill(Color.black.opacity(0.4))
                            .overlay(
                                RoundedRectangle(cornerRadius: 16)
                                    .stroke(Color.white.opacity(0.3), lineWidth: 1)
                            )
                    )
                    .padding(.horizontal)
                }

                // Guardian Information Card (Privacy-Aware)
                if user.privacySettings.showGuardianInfo {
                    VStack(alignment: .leading, spacing: 20) {
                        Text("Guardian Information")
                            .font(.title3)
                            .fontWeight(.bold)
                            .foregroundColor(.white)

                        if !user.guardian1Name.isEmpty {
                            GuardianCard(
                                number: 1,
                                name: user.guardian1Name,
                                phone: user.guardian1Phone,
                                email: user.guardian1Email,
                                relation: user.guardian1Relation
                            )
                        }

                        if !user.guardian2Name.isEmpty {
                            GuardianCard(
                                number: 2,
                                name: user.guardian2Name,
                                phone: user.guardian2Phone,
                                email: user.guardian2Email,
                                relation: user.guardian2Relation
                            )
                        }
                    }
                    .padding(20)
                    .background(
                        RoundedRectangle(cornerRadius: 16)
                            .fill(Color.black.opacity(0.4))
                            .overlay(
                                RoundedRectangle(cornerRadius: 16)
                                    .stroke(Color.white.opacity(0.3), lineWidth: 1)
                            )
                    )
                    .padding(.horizontal)
                }
            }
            .padding(.vertical, 20)
        }
        .background(
            LinearGradient(
                gradient: Gradient(colors: [
                    Color(red: 0.1, green: 0.1, blue: 0.2),
                    Color(red: 0.05, green: 0.05, blue: 0.15)
                ]),
                startPoint: .topLeading,
                endPoint: .bottomTrailing
            )
            .ignoresSafeArea()
        )
        .navigationTitle("About")
        .navigationBarTitleDisplayMode(.inline)
    }

    private var hasVisiblePersonalInfo: Bool {
        user.privacySettings.showFullName ||
        user.privacySettings.showAddress ||
        user.privacySettings.showPhone ||
        user.privacySettings.showEmail ||
        user.privacySettings.showBirthday
    }
}

