        shadowStrength: ShadowStrength = .medium
    ) -> some View {
        modifier(PhotoOverlayCardModifier(
            material: material,
            cornerRadius: cornerRadius,
            padding: padding,
            showBorder: showBorder,
            shadowStrength: shadowStrength
        ))
    }
}

// MARK: - Container Version (Alternative API)

/// A container view that wraps content in a photo overlay card
/// This is an alternative to the modifier version, useful when you prefer container syntax
struct PhotoOverlayCard<Content: View>: View {
    let content: Content
    var material: Material = .ultraThinMaterial
    var cornerRadius: CGFloat = .radiusLarge
    var padding: CGFloat = .spacing4
    var showBorder: Bool = true
    var shadowStrength: ShadowStrength = .medium

    @Environment(\.colorScheme) private var colorScheme

    init(
        material: Material = .ultraThinMaterial,
        cornerRadius: CGFloat = .radiusLarge,
        padding: CGFloat = .spacing4,
        showBorder: Bool = true,
        shadowStrength: ShadowStrength = .medium,
        @ViewBuilder content: () -> Content
    ) {
        self.material = material
        self.cornerRadius = cornerRadius
        self.padding = padding
        self.showBorder = showBorder
        self.shadowStrength = shadowStrength
        self.content = content()
    }

    var body: some View {
        content
            .padding(padding)
            .background(
                RoundedRectangle(cornerRadius: cornerRadius, style: .continuous)
                    .fill(material)
                    .overlay(
                        RoundedRectangle(cornerRadius: cornerRadius, style: .continuous)
                            .strokeBorder(
                                Color.white.opacity(showBorder ? (colorScheme == .dark ? 0.2 : 0.3) : 0),
                                lineWidth: 1
                            )
                    )
                    .shadow(
                        color: Color.black.opacity(shadowStrength.opacity),
                        radius: shadowStrength.radius,
                        x: 0,
                        y: shadowStrength.yOffset
                    )
            )
    }
}

