//
//  AthleteFeedView.swift
//  AthleadAI
//
//  Feed view customized for athlete users with squad functionality
//

import SwiftUI

struct AthleteFeedView: View {
    @State private var viewModel = FeedViewModel(feedType: .athlete)

    var body: some View {
        NavigationView {
            ScrollView {
                VStack(spacing: 20) {
                    // Squads Section
                    VStack(alignment: .leading, spacing: 12) {
                        HStack {
                            Text("Squads")
                                .font(.title3)
                                .fontWeight(.bold)
                                .foregroundColor(.white)

                            Spacer()

                            Button(action: {}) {
                                    Image(systemName: "plus.circle.fill")
                                        .foregroundColor(.blue)
                                        .font(.title3)
                                }
                                .accessibilityLabel("Add new squad")
                                .accessibilityHint("Create or join a new squad")
                            }
                            .padding(.horizontal)

                            ScrollView(.horizontal, showsIndicators: false) {
                                HStack(spacing: 16) {
                                    // All Posts (default)
                                    CircleFilter(
                                        name: "All",
                                        icon: "house.fill",
                                        isSelected: viewModel.selectedSquad == nil
                                    ) {
                                        viewModel.selectSquad(nil)
                                    }

                                    // Squad circles with custom images
                                    CircleFilter(
                                        name: "Carroll",
                                        imageName: "carroll",
                                        isSelected: viewModel.selectedSquad == "Carroll Volleyball"
                                    ) {
                                        viewModel.selectSquad("Carroll Volleyball")
                                    }

                                    CircleFilter(
                                        name: "2026",
                                        imageName: "26",
                                        isSelected: viewModel.selectedSquad == "Class of 2026"
                                    ) {
                                        viewModel.selectSquad("Class of 2026")
                                    }

                                    CircleFilter(
                                        name: "Texas",
                                        imageName: "tx",
                                        isSelected: viewModel.selectedSquad == "Texas Volleyball"
                                    ) {
                                        viewModel.selectSquad("Texas Volleyball")
                                    }
                                }
                                .padding(.horizontal)
                            }
                        }
                        .padding(.vertical, 12)

                        // Feed Posts
                        VStack(spacing: 16) {
                            ForEach(viewModel.filteredPosts, id: \.id) { post in
                                FeedPost(
                                    authorName: post.authorName,
                                    authorHandle: post.authorHandle,
                                    timeAgo: post.timeAgo,
                                    squad: post.squad,
                                    content: post.content,
                                    likes: post.likes,
                                    comments: post.comments,
                                    mediaType: post.mediaType
                                )
                            }
                        }
                        .padding(.horizontal)
                        .padding(.bottom, 20)
                    .padding(.top, 20)
                }
            }
            .withDesignTheme()
            .brandBackground(.feed)
            .navigationTitle("Feed")
            .navigationBarTitleDisplayMode(.inline)
            .toolbar {
                ToolbarItem(placement: .navigationBarTrailing) {
                    Button(action: { viewModel.toggleCreatePost() }) {
                        Image(systemName: "square.and.pencil")
                            .foregroundColor(.white)
                    }
                    .accessibilityLabel("Create new post")
                    .accessibilityHint("Opens a form to create and share a new post")
                }
            }
            .sheet(isPresented: $viewModel.showCreatePost) {
                CreatePostView()
            }
            .refreshable {
                await viewModel.refresh()
            }
            .task {
                await viewModel.loadFeed()
            }
        }
    }
}

// Create Post View (placeholder)
struct CreatePostView: View {
    @Environment(\.dismiss) var dismiss
    @State private var postContent = ""
    @State private var selectedSquad = "All"

    var body: some View {
        NavigationView {
            VStack(spacing: 20) {
                // Squad picker
                    VStack(alignment: .leading, spacing: 8) {
                        Text("Post to Squad")
                            .font(.subheadline)
                            .foregroundColor(.gray)

                        Menu {
                            Button("All") { selectedSquad = "All" }
                            Button("Carroll Volleyball") { selectedSquad = "Carroll Volleyball" }
                            Button("Class of 2026") { selectedSquad = "Class of 2026" }
                            Button("Texas Volleyball") { selectedSquad = "Texas Volleyball" }
                        } label: {
                            HStack {
                                Text(selectedSquad)
                                    .foregroundColor(.white)
                                Spacer()
                                Image(systemName: "chevron.down")
                                    .foregroundColor(.gray)
                                    .accessibilityHidden(true)
                            }
                            .padding()
                            .background(
                                RoundedRectangle(cornerRadius: 12)
                                    .fill(Color.black.opacity(0.4))
                                    .overlay(
                                        RoundedRectangle(cornerRadius: 12)
                                            .stroke(Color.white.opacity(0.3), lineWidth: 1)
                                    )
                            )
                        }
                    }

                    // Text editor
                    TextEditor(text: $postContent)
                        .frame(height: 200)
                        .scrollContentBackground(.hidden)
                        .padding()
                        .background(
                            RoundedRectangle(cornerRadius: 12)
                                .fill(.ultraThinMaterial)
                        )
                        .foregroundColor(.white)

                Spacer()
            }
            .padding()
            .withDesignTheme()
            .brandBackground(.feed)
            .navigationTitle("Create Post")
            .navigationBarTitleDisplayMode(.inline)
            .toolbar {
                ToolbarItem(placement: .navigationBarLeading) {
                    Button("Cancel") {
                        dismiss()
                    }
                    .foregroundColor(.white)
                }
                ToolbarItem(placement: .navigationBarTrailing) {
                    Button("Post") {
                        // Post logic here
                        dismiss()
                    }
                    .foregroundColor(.blue)
                    .fontWeight(.semibold)
                }
            }
        }
    }
}

