//
//  FeedPost.swift
//  AthleadAI
//
//  Shared component for displaying social media posts in feeds
//  Used by both athlete and coach feed views
//

import SwiftUI

struct FeedPost: View {
    let authorName: String
    let authorHandle: String
    let timeAgo: String
    let squad: String  // Tag to display (e.g., "Carroll Volleyball", "Class of 2026")
    let content: String
    let likes: Int
    let comments: Int
    var mediaType: MediaType? = nil

    @State private var isLiked = false

    enum MediaType {
        case image
        case video
    }

    var body: some View {
        VStack(alignment: .leading, spacing: .spacing3) {
            // MARK: - Header
            headerView

            // MARK: - Squad Tag
            tagView

            // MARK: - Content
            contentView

            // MARK: - Media (Image or Video)
            if let mediaType = mediaType {
                mediaView(type: mediaType)
            }

            // MARK: - Action Buttons
            actionButtons
        }
        .padding(.spacing4)
        .glassCard(cornerRadius: .radiusLarge)
    }

    // MARK: - Header View
    private var headerView: some View {
        HStack(spacing: .spacing3) {
            // Profile image
            profileImage

            VStack(alignment: .leading, spacing: 2) {
                Text(authorName)
                    .font(.headline)
                    .foregroundColor(.white)

                HStack(spacing: 4) {
                    Text(authorHandle)
                        .font(.caption)
                        .foregroundColor(.gray)
                    Text("")
                        .font(.caption)
                        .foregroundColor(.gray)
                    Text(timeAgo)
                        .font(.caption)
                        .foregroundColor(.gray)
                }
            }

            Spacer()

            Button(action: {}) {
                Image(systemName: "ellipsis")
                    .foregroundColor(.gray)
            }
            .accessibilityLabel("Post options")
        }
    }

    // MARK: - Profile Image
    private var profileImage: some View {
        Group {
            if authorHandle == "@kinsleyyoung" {
                Image("B302C166-4D7D-4BAD-BBBA-39D27A56987D")
                    .resizable()
                    .scaledToFill()
                    .frame(width: 44, height: 44)
                    .clipShape(Circle())
            } else if authorHandle == "@lc_clayton51" {
                Image("lc")
                    .resizable()
                    .scaledToFill()
                    .frame(width: 44, height: 44)
                    .clipShape(Circle())
            } else {
                Image(systemName: "person.crop.circle.fill")
                    .resizable()
                    .frame(width: 44, height: 44)
                    .foregroundColor(.gray)
            }
        }
        .accessibilityLabel("\(authorName)'s profile picture")
    }

    // MARK: - Squad Tag
    private var tagView: some View {
        HStack(spacing: 6) {
            Image(systemName: "person.3.fill")
                .font(.caption2)
            Text(squad)
                .font(.caption)
        }
        .foregroundColor(.blue)
        .padding(.horizontal, 10)
        .padding(.vertical, 4)
        .background(
            Capsule()
                .fill(Color.blue.opacity(0.2))
        )
        .accessibilityLabel("Squad: \(squad)")
    }

    // MARK: - Content View
    private var contentView: some View {
        Text(content)
            .font(.body)
            .foregroundColor(.white)
            .lineSpacing(4)
    }

    // MARK: - Media View
    private func mediaView(type: MediaType) -> some View {
        Group {
            if type == .image {
                imageMediaView
            } else if type == .video {
                videoMediaView
            }
        }
    }

    private var imageMediaView: some View {
        Group {
            // Check if this is Kaylee's post and show kales image
            if authorHandle == "@kales_usc" {
                Image("kales")
                    .resizable()
                    .scaledToFill()
                    .frame(height: 200)
                    .clipped()
                    .cornerRadius(.radiusMedium)
            } else if authorHandle == "@lc_clayton51" {
                Image("lc")
                    .resizable()
                    .scaledToFill()
                    .frame(height: 200)
                    .clipped()
                    .cornerRadius(.radiusMedium)
            } else if authorHandle == "@kinsleyyoung" && squad == "Carroll Volleyball" && content.contains("Team photo") {
                Image("dragons")
                    .resizable()
                    .scaledToFill()
                    .frame(height: 200)
                    .clipped()
                    .cornerRadius(.radiusMedium)
            } else {
                RoundedRectangle(cornerRadius: .radiusMedium)
                    .fill(Color.gray.opacity(0.3))
                    .frame(height: 200)
                    .overlay(
                        Image(systemName: "photo.fill")
                            .font(.system(size: 60))
                            .foregroundColor(.white.opacity(0.5))
                    )
            }
        }
        .accessibilityLabel("\(authorName)'s post image")
    }

    private var videoMediaView: some View {
        ZStack {
            // Check if this is Kaylee's post and show kales image as video thumbnail
            if authorHandle == "@kales_usc" {
                Image("kales")
                    .resizable()
                    .scaledToFill()
                    .frame(height: 200)
                    .clipped()
                    .cornerRadius(.radiusMedium)
            } else if authorHandle == "@kinsleyyoung" && content.contains("block timing") {
                Image("block")
                    .resizable()
                    .scaledToFill()
                    .frame(height: 200)
                    .clipped()
                    .cornerRadius(.radiusMedium)
            } else {
                RoundedRectangle(cornerRadius: .radiusMedium)
                    .fill(Color.gray.opacity(0.3))
                    .frame(height: 200)
                    .overlay(
                        Image(systemName: "video.fill")
                            .font(.system(size: 40))
                            .foregroundColor(.white.opacity(0.3))
                    )
            }

            // Play button overlay
            Circle()
                .fill(Color.white.opacity(0.9))
                .frame(width: 60, height: 60)

            Image(systemName: "play.fill")
                .font(.system(size: 24))
                .foregroundColor(.black)
        }
        .accessibilityLabel("\(authorName)'s post video")
        .accessibilityHint("Double tap to play video")
    }

    // MARK: - Action Buttons
    private var actionButtons: some View {
        HStack(spacing: 24) {
            // Like button
            Button(action: {
                isLiked.toggle()
            }) {
                HStack(spacing: 6) {
                    Image(systemName: isLiked ? "heart.fill" : "heart")
                        .foregroundColor(isLiked ? .red : .gray)
                    Text("\(isLiked ? likes + 1 : likes)")
                        .font(.subheadline)
                        .foregroundColor(.gray)
                }
            }
            .accessibilityLabel(isLiked ? "Unlike post" : "Like post")
            .accessibilityValue("\(isLiked ? likes + 1 : likes) likes")

            // Comment button
            Button(action: {}) {
                HStack(spacing: 6) {
                    Image(systemName: "bubble.right")
                        .foregroundColor(.gray)
                    Text("\(comments)")
                        .font(.subheadline)
                        .foregroundColor(.gray)
                }
            }
            .accessibilityLabel("Comment on post")
            .accessibilityValue("\(comments) comments")

            // Share button
            Button(action: {}) {
                Image(systemName: "arrow.turn.up.right")
                    .foregroundColor(.gray)
            }
            .accessibilityLabel("Share post")

            Spacer()

            // Bookmark button
            Button(action: {}) {
                Image(systemName: "bookmark")
                    .foregroundColor(.gray)
            }
            .accessibilityLabel("Bookmark post")
        }
    }
}

