//
//  PrimaryPositionView.swift
//  AthleadAI
//
//  Primary position selection screen during onboarding
//

import SwiftUI

struct PrimaryPositionView: View {
    @Environment(\.dismiss) private var dismiss

    // User data passed from previous screen
    let userName: String
    let sport: String

    @State private var selectedPosition: String = "Middle Blocker"
    @State private var isLoading = false
    @State private var navigateToMeasurements = false

    // Position options based on sport
    private var positions: [String] {
        switch sport.lowercased() {
        case "volleyball":
            return ["Outside Hitter", "Middle Blocker", "Libero", "Opposite", "Setter", "Defensive Specialist"]
        case "basketball":
            return ["Point Guard", "Shooting Guard", "Small Forward", "Power Forward", "Center"]
        case "football":
            return ["Quarterback", "Running Back", "Wide Receiver", "Tight End", "Offensive Line", "Defensive Line", "Linebacker", "Cornerback", "Safety", "Kicker", "Punter"]
        case "soccer":
            return ["Goalkeeper", "Defender", "Midfielder", "Forward", "Striker"]
        case "baseball":
            return ["Pitcher", "Catcher", "First Base", "Second Base", "Third Base", "Shortstop", "Outfield"]
        case "softball":
            return ["Pitcher", "Catcher", "First Base", "Second Base", "Third Base", "Shortstop", "Outfield"]
        default:
            return ["Position 1", "Position 2", "Position 3", "Position 4"]
        }
    }

    var body: some View {
        ZStack {
            // Background
            Color(.systemBackground)
                .ignoresSafeArea()

            VStack(spacing: 0) {
                // Header
                VStack(spacing: 16) {
                    Text("Athlete Sign Up 1")
                        .font(.caption)
                        .foregroundColor(.gray)
                        .frame(maxWidth: .infinity, alignment: .leading)
                        .padding(.horizontal, 24)
                        .padding(.top, 20)

                    Image(systemName: "target")
                        .font(.system(size: 60))
                        .foregroundStyle(
                            LinearGradient(
                                colors: [.blue, .purple],
                                startPoint: .topLeading,
                                endPoint: .bottomTrailing
                            )
                        )

                    Text("ATHLEAD")
                        .font(.system(size: 32, weight: .bold))
                        .foregroundColor(.blue)

                    VStack(spacing: 4) {
                        Text("Hi \(userName)!")
                            .font(.title2)
                            .fontWeight(.bold)

                        Text("Welcome to the")
                            .font(.body)

                        Text("team. Let's get your")
                            .font(.body)

                        Text("profile setup")
                            .font(.body)
                    }
                    .padding(.top, 8)
                }
                .padding(.bottom, 32)

                // Primary Position Dropdown
                VStack(spacing: 12) {
                    Menu {
                        ForEach(positions, id: \.self) { position in
                            Button {
                                selectedPosition = position
                            } label: {
                                HStack {
                                    Text(position)
                                    Spacer()
                                    if selectedPosition == position {
                                        Image(systemName: "checkmark")
                                    }
                                }
                            }
                        }
                    } label: {
                        HStack {
                            Text("Primary Position")
                                .foregroundColor(.white)
                                .fontWeight(.medium)
                            Spacer()
                            Image(systemName: "chevron.down")
                                .foregroundColor(.white)
                                .font(.caption)
                        }
                        .padding()
                        .frame(maxWidth: .infinity)
                        .background(Color.blue)
                        .cornerRadius(8)
                    }
                    .padding(.horizontal, 24)

                    // Position options list
                    VStack(spacing: 12) {
                        ForEach(positions, id: \.self) { position in
                            Button {
                                selectedPosition = position
                            } label: {
                                HStack(spacing: 12) {
                                    Image(systemName: selectedPosition == position ? "record.circle.fill" : "circle")
                                        .foregroundColor(selectedPosition == position ? .blue : .gray)
                                        .font(.system(size: 20))

                                    Text(position)
                                        .foregroundColor(.primary)
                                        .font(.body)

                                    Spacer()
                                }
                                .padding(.horizontal, 24)
                                .contentShape(Rectangle())
                            }
                        }
                    }
                    .padding(.top, 8)
                }

                Spacer()

                // Progress indicator
                VStack(spacing: 12) {
                    HStack {
                        Spacer()
                        Text("25%")
                            .font(.caption)
                            .foregroundColor(.gray)
                            .padding(.trailing, 24)
                    }

                    GeometryReader { geometry in
                        ZStack(alignment: .leading) {
                            Rectangle()
                                .fill(Color.gray.opacity(0.2))
                                .frame(height: 4)

                            Rectangle()
                                .fill(Color.blue)
                                .frame(width: geometry.size.width * 0.25, height: 4)
                        }
                    }
                    .frame(height: 4)
                    .padding(.horizontal, 24)
                }
                .padding(.bottom, 40)
            }

            // Navigation buttons
            VStack {
                Spacer()
                HStack {
                    // Back button
                    Button {
                        dismiss()
                    } label: {
                        Image(systemName: "arrow.left")
                            .foregroundColor(.gray)
                            .font(.system(size: 20))
                            .padding()
                    }
                    .padding(.leading, 24)

                    Spacer()

                    // Continue button (arrow right)
                    Button {
                        navigateToMeasurements = true
                    } label: {
                        Image(systemName: "arrow.right")
                            .foregroundColor(.gray)
                            .font(.system(size: 20))
                            .padding()
                    }
                    .padding(.trailing, 24)
                }
                .padding(.bottom, 20)
            }
        }
        .navigationBarBackButtonHidden(true)
        .navigationDestination(isPresented: $navigateToMeasurements) {
            AthleteMeasurementsView(userName: userName, sport: sport)
        }
    }
}

