                            )
                        )

                    Text("ATHLEAD")
                        .font(.system(size: 32, weight: .bold))
                        .foregroundColor(.blue)
                }
                .padding(.bottom, 32)

                // Form
                ScrollView {
                    VStack(spacing: 20) {
                        // Height
                        DropdownField(
                            label: "Height",
                            selectedValue: $height,
                            options: heightOptions
                        )

                        // Handedness
                        VStack(alignment: .leading, spacing: 12) {
                            HStack(spacing: 16) {
                                Button {
                                    isRightHanded = true
                                } label: {
                                    HStack(spacing: 8) {
                                        Image(systemName: isRightHanded ? "checkmark.square.fill" : "square")
                                            .foregroundColor(isRightHanded ? .blue : .gray)
                                        Text("Right Handed")
                                            .foregroundColor(.primary)
                                    }
                                }

                                Button {
                                    isRightHanded = false
                                } label: {
                                    HStack(spacing: 8) {
                                        Image(systemName: !isRightHanded ? "checkmark.square.fill" : "square")
                                            .foregroundColor(!isRightHanded ? .blue : .gray)
                                        Text("Left Handed")
                                            .foregroundColor(.primary)
                                    }
                                }
                            }
                            .padding(.horizontal, 24)
                        }

                        // Approach Touch
                        DropdownField(
                            label: "Approach Touch",
                            selectedValue: $approachTouch,
                            options: touchHeightOptions
                        )

                        // Block Jump
                        DropdownField(
                            label: "Block Jump",
                            selectedValue: $blockJump,
                            options: touchHeightOptions
                        )

                        // Vertical Jump
                        DropdownField(
                            label: "Vertical Jump",
                            selectedValue: $verticalJump,
                            options: verticalJumpOptions
                        )
                    }
                    .padding(.bottom, 40)
                }

                Spacer()

                // Progress indicator
                VStack(spacing: 12) {
                    HStack {
                        Spacer()
                        Text("50%")
                            .font(.caption)
                            .foregroundColor(.gray)
                            .padding(.trailing, 24)
                    }

                    GeometryReader { geometry in
                        ZStack(alignment: .leading) {
                            Rectangle()
                                .fill(Color.gray.opacity(0.2))
                                .frame(height: 4)

                            Rectangle()
                                .fill(Color.blue)
                                .frame(width: geometry.size.width * 0.5, height: 4)
                        }
                    }
                    .frame(height: 4)
                    .padding(.horizontal, 24)
                }
                .padding(.bottom, 40)
            }

            // Navigation buttons
            VStack {
                Spacer()
                HStack {
                    // Back button
                    Button {
                        dismiss()
                    } label: {
                        Image(systemName: "arrow.left")
                            .foregroundColor(.gray)
                            .font(.system(size: 20))
                            .padding()
                    }
                    .padding(.leading, 24)

                    Spacer()

                    // Continue button
                    Button {
                        navigateToContactInfo = true
                    } label: {
                        Image(systemName: "arrow.right")
                            .foregroundColor(.gray)
                            .font(.system(size: 20))
                            .padding()
                    }
                    .padding(.trailing, 24)
                }
                .padding(.bottom, 20)
            }
        }
        .navigationBarBackButtonHidden(true)
        .navigationDestination(isPresented: $navigateToContactInfo) {
            ContactInformationView(userName: userName, sport: sport)
        }
    }
}

