//
//  ContactInformationView.swift
//  AthleadAI
//
//  Contact information screen during onboarding
//

import SwiftUI

struct ContactInformationView: View {
    @Environment(\.dismiss) private var dismiss

    // User data
    let userName: String
    let sport: String

    // Form fields
    @State private var phoneNumber: String = ""
    @State private var streetAddress: String = ""
    @State private var city: String = ""
    @State private var state: String = "Texas"
    @State private var guardianEmail: String = ""

    @State private var isLoading = false

    // US States
    private let states = [
        "Alabama", "Alaska", "Arizona", "Arkansas", "California", "Colorado",
        "Connecticut", "Delaware", "Florida", "Georgia", "Hawaii", "Idaho",
        "Illinois", "Indiana", "Iowa", "Kansas", "Kentucky", "Louisiana",
        "Maine", "Maryland", "Massachusetts", "Michigan", "Minnesota",
        "Mississippi", "Missouri", "Montana", "Nebraska", "Nevada",
        "New Hampshire", "New Jersey", "New Mexico", "New York",
        "North Carolina", "North Dakota", "Ohio", "Oklahoma", "Oregon",
        "Pennsylvania", "Rhode Island", "South Carolina", "South Dakota",
        "Tennessee", "Texas", "Utah", "Vermont", "Virginia", "Washington",
        "West Virginia", "Wisconsin", "Wyoming"
    ]

    var body: some View {
        ZStack {
            // Background
            Color(.systemBackground)
                .ignoresSafeArea()

            VStack(spacing: 0) {
                // Header
                VStack(spacing: 16) {
                    Text("Athlete Sign Up")
                        .font(.caption)
                        .foregroundColor(.gray)
                        .frame(maxWidth: .infinity, alignment: .leading)
                        .padding(.horizontal, 24)
                        .padding(.top, 20)

                    Image(systemName: "target")
                        .font(.system(size: 60))
                        .foregroundStyle(
                            LinearGradient(
                                colors: [.blue, .purple],
                                startPoint: .topLeading,
                                endPoint: .bottomTrailing
                            )
                        )

                    Text("ATHLEAD")
                        .font(.system(size: 32, weight: .bold))
                        .foregroundColor(.blue)
                }
                .padding(.bottom, 32)

                // Form
                ScrollView {
                    VStack(spacing: 20) {
                        // Phone Number
                        VStack(alignment: .leading, spacing: 8) {
                            Text("Phone Number")
                                .font(.subheadline)
                                .fontWeight(.medium)
                                .foregroundColor(.primary)

                            HStack {
                                Image(systemName: "phone")
                                    .foregroundColor(.gray)
                                    .frame(width: 20)

                                TextField("123-456-7890", text: $phoneNumber)
                                    .keyboardType(.phonePad)

                                if !phoneNumber.isEmpty {
                                    Button {
                                        phoneNumber = ""
                                    } label: {
                                        Image(systemName: "xmark.circle.fill")
                                            .foregroundColor(.gray)
                                    }
                                }
                            }
                            .padding()
                            .background(Color(.systemGray6))
                            .cornerRadius(8)

                            Text("Where can coaches contact you")
                                .font(.caption)
                                .foregroundColor(.gray)
                        }
                        .padding(.horizontal, 24)

                        // Street Address
                        VStack(alignment: .leading, spacing: 8) {
                            Text("Street Address")
                                .font(.subheadline)
                                .fontWeight(.medium)
                                .foregroundColor(.primary)

                            TextField("Placeholder", text: $streetAddress)
                                .padding()
                                .background(Color(.systemGray6))
                                .cornerRadius(8)
                        }
                        .padding(.horizontal, 24)

                        // City and State
                        HStack(spacing: 12) {
                            // City
                            VStack(alignment: .leading, spacing: 8) {
                                Text("City")
                                    .font(.subheadline)
                                    .fontWeight(.medium)
                                    .foregroundColor(.primary)

                                TextField("Placeholder", text: $city)
                                    .padding()
                                    .background(Color(.systemGray6))
                                    .cornerRadius(8)
                            }

                            // State
                            VStack(alignment: .leading, spacing: 8) {
                                Text("State")
                                    .font(.subheadline)
                                    .fontWeight(.medium)
                                    .foregroundColor(.primary)

                                Menu {
                                    ForEach(states, id: \.self) { stateName in
                                        Button {
                                            state = stateName
                                        } label: {
                                            HStack {
                                                Text(stateName)
                                                Spacer()
                                                if state == stateName {
                                                    Image(systemName: "checkmark")
                                                }
                                            }
                                        }
                                    }
                                } label: {
                                    HStack {
                                        Text(state)
                                            .foregroundColor(.primary)
                                        Spacer()
                                        Image(systemName: "chevron.down")
                                            .foregroundColor(.gray)
                                            .font(.caption)
                                    }
                                    .padding()
                                    .background(Color(.systemGray6))
                                    .cornerRadius(8)
                                }
                            }
                        }
                        .padding(.horizontal, 24)

                        // Guardian Email
                        VStack(alignment: .leading, spacing: 8) {
                            Text("Guardian Email")
                                .font(.subheadline)
                                .fontWeight(.medium)
                                .foregroundColor(.primary)

                            HStack {
                                TextField("", text: $guardianEmail)
                                    .keyboardType(.emailAddress)
                                    .autocapitalization(.none)

                                Image(systemName: "envelope")
                                    .foregroundColor(.gray)
                            }
                            .padding()
                            .background(Color(.systemGray6))
                            .cornerRadius(8)
                        }
                        .padding(.horizontal, 24)
                    }
                    .padding(.bottom, 40)
                }

                Spacer()

                // Progress indicator and Done button
                VStack(spacing: 12) {
                    HStack {
                        Spacer()
                        Text("100%")
                            .font(.caption)
                            .foregroundColor(.gray)
                            .padding(.trailing, 24)
                    }

                    GeometryReader { geometry in
                        ZStack(alignment: .leading) {
                            Rectangle()
                                .fill(Color.gray.opacity(0.2))
                                .frame(height: 4)

                            Rectangle()
                                .fill(Color.blue)
                                .frame(width: geometry.size.width, height: 4)
                        }
                    }
                    .frame(height: 4)
                    .padding(.horizontal, 24)
                }
                .padding(.bottom, 16)
            }

            // Navigation buttons and Done button
            VStack {
                Spacer()
                HStack {
                    // Back button
                    Button {
                        dismiss()
                    } label: {
                        Image(systemName: "arrow.left")
                            .foregroundColor(.gray)
                            .font(.system(size: 20))
                            .padding()
                    }
                    .padding(.leading, 24)

                    Spacer()

                    // Done button
                    Button {
                        completeOnboarding()
                    } label: {
                        HStack {
                            if isLoading {
                                ProgressView()
                                    .progressViewStyle(CircularProgressViewStyle(tint: .white))
                                    .scaleEffect(0.9)
                            } else {
                                Text("Done")
                                    .font(.headline)
                                    .fontWeight(.semibold)
                            }
                        }
                        .frame(width: 100)
                        .padding(.vertical, 12)
                        .background(Color.blue)
                        .foregroundStyle(.white)
                        .cornerRadius(8)
                    }
                    .disabled(isLoading)
                    .padding(.trailing, 24)
                }
                .padding(.bottom, 20)
            }
        }
        .navigationBarBackButtonHidden(true)
    }

    private func completeOnboarding() {
        isLoading = true

        // TODO: Submit all onboarding data to backend
        // For now, just simulate a delay
        DispatchQueue.main.asyncAfter(deadline: .now() + 1.5) {
            isLoading = false
            // TODO: Navigate to main app
        }
    }
}

